INSERT INTO `courses` VALUES ('541', '249111', 'ESLG', '071', '2006', 'BEGINNING READING AND WRITING', 'A beginning English reading and writing course for non-native speakers. Students will receive a grade of A, B, C, or NC.', '5', '5', '0', '0', '', '0');
