INSERT INTO `courses` VALUES ('14161', '469911', 'ENTR', '352', '2006', 'Entrepreneurial Finance', 'Financing of new and growing business enterprises; structuring, valuing, and pricing new venture deals, and sources and uses of funds; financial analysis and forecasting, capital budgeting, and working capital management in new, growing enterprises; later stage financing, going public. Prerequisite: Junior standing. (Same as Finance 330)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14155', '688211', 'ENTR', '353', '2006', 'Entrepreneurial Marketing', 'Marketing during the start up and growth stage of new ventures in a dynamic economy at the local, national, and international level. Emphasis on the application of marketing fundamentals in new growing ventures. Prerequisite: Junior standing. (Same as Marketing 330)', '3', '3', '3', '0', '', '0');
