INSERT INTO `courses` VALUES ('606', '193617', 'FCSC', '430', '2006', 'CURRENT TRENDS IN THE FASHION INDUSTRY', 'Study of current trends of the industry. Review of the journals and other publications used in the industry. Prerequisite: 329.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('607', '172614', 'FCSC', '437', '2006', 'INTERMEDIATE STUDIO', 'Individual design projects in a residential and/or commercial setting. Six hours laboratory or equivalent. Prerequisites: 206, 337.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('608', '640749', 'FCSC', '412', '2006', 'ADVANCED NUTRITION', 'Problems in human nutrition. Investigation of recent developments in the field of nutrition.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('609', '980387', 'FCSC', '422', '2006', 'HISTORY OF COSTUME', 'Styles of costumes in western civilization from ancient times to the present day; cultural and economic factors associated with the development, adoption, and decadence of styles.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('610', '873390', 'FCSC', '431', '2006', 'PARENTING: FATHERS, MOTHERS, AND CHILDREN', 'Practical knowledge of parenting roles. Interrelationships of fathers, mothers, and children. Current issues in parenting, including bonding, discipline, child care, family crises, support systems and non- traditional parent situations. Parents and children in historical and contemporary perspectives.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('611', '368837', 'FCSC', '432', '2006', 'FASHION MERCHANDISING PRE-PRACTICUM', 'A survey of the job market with emphasis on researching a company, writing a resume, and interviewing skills. Class research project includes developing a questionnaire, collecting and analyzing data. Prerequisites: Senior standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('612', '548296', 'FCSC', '433', '2006', 'FASHION MERCHANDISING PRACTICUM', 'An in-depth experience planned and supervised to meet individual needs of students. Includes work experience and/or field study. Prerequisites: Senior standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('613', '273830', 'FCSC', '434', '2006', 'FAMILIES IN A CHANGING WORLD', 'Study of current issues affecting families and the professionals who work with them.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14065', '330615', 'FCSC', '435', '2006', 'Housing The Elderly', 'Examination and application of the guidelines, issues and choices associated with housing disruption and re-location of the elderly. (Same as GERO 435.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('616', '158522', 'FINA', '315', '2006', 'BUSINESS FINANCE', 'The organization and financing of a business enterprise; types of securities and their utilization in proportioning income, risk and control; financial analysis and forecasting, working capital management; selection of long term investments and determination of costs of capital; use of financial institutions for obtaining funds. Prerequisite: ACCT 110 or 213, and ECON 201. F, Sp, Su', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('617', '705256', 'FINA', '316', '2006', 'INTERMEDIATE CORPORATE FINANCE', 'Advanced topics in risk and return, bond and stock valuation, options and derivatives. Cost of capital, capital budgeting, and cash flow analysis. Corporate capital structure, financial planning, and credit policies. Mergers and acquisitions, corporate risk management, and multinational finance. Prerequisite: 315.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14158', '993223', 'FINA', '330', '2006', 'Entrepreneurial Finance', 'Financing of new and growing business enterprises; structuring, valuing, and pricing new venture deals, and sources and uses of funds; financial analysis and forecasting, capital budgeting, and working capital management in new, growing enterprises; later stage financing, going public. Prerequisite: Junior standing.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('619', '146502', 'FINA', '410', '2006', 'PRINCIPLES OF BANK OPERATIONS', 'Operations of commercial banks --analyzing deposits, payments, loans and money functions of commercial banks. Bank asset and liability management; internal controls and audits. Prerequisite: 315 and ECON 301.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('620', '987267', 'FINA', '401', '2006', 'INVESTMENTS', 'The principles of investments in stocks and bonds and their application to specific classes of investments. Sources of financial information; interpreting financial news; protection in purchasing of securities, taxation of securities. Prerequisite: 315. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('621', '8815', 'FINA', '404', '2006', 'INTERNATIONAL FINANCE', 'Theory and analysis of international money flows, sources of capital, exchange rates, money markets and international banking institutions with emphasis on financing the firm operating in international markets. Prerequisite: 315. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('622', '435712', 'FRNH', '102', '2006', 'ELEMENTARY', 'A continuation of the study of French on the elementary level. Prerequisite: A &quot;C&quot; or better in French 101 or permission of the department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('623', '781172', 'FRNH', '210', '2006', 'COMMERCIAL FRENCH', 'Study of the French language as used in business to include readings, discussions, and translations of business materials in French. Prerequisite: 202 or 205 or permission of the department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('624', '435600', 'FRNH', '231', '2006', 'GRAMMAR AND COMPOSITION', 'Elements of French grammar and syntax with drill in style, idioms, and verb forms. Prerequisite: 202 or 205.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('625', '933047', 'FRNH', '312', '2006', 'PHONETICS', 'French phonetic system. Corrective and fluency drills in the language laboratory. Prerequisite: 202, 205 or equivalent or permission of the department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('13960', '270642', 'FRNH', '474', '2006', 'Second Language Acquisition', 'The application of second language acquisition theory in the classroom. Prerequisite: Spanish/French 473.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('627', '139990', 'GEOG', '103', '2006', 'SYSTEMATIC GEOGRAPHY', 'The goal of this course is an integrated approach to geography. The course is standards-based and is structured around the essential elements and geography standards. In terms of content, the approach is thematic with examples drawn from both the developed and developing worlds. The method of instruction will consist of a combination of direct instruction and problem-solving computer- and classroom-based exercises and activities. For Elementary Education Majors and Geography majors only.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('628', '559674', 'GEOG', '203', '2006', 'LOUISIANA', 'A survey of the natural landscape; land forms, climate, soils, drainage, and the geographic factors; agriculture, transportation, and population.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('629', '157216', 'GEOL', '102', '2006', 'HISTORICAL GEOLOGY', 'The development, changes, and destruction of the land features and sea areas of the earth and the changing panorama of plant and animal life from the earth’s origin to the present day.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('630', '400147', 'GEOL', '103', '2006', 'PHYSICAL LABORATORY', 'Two hours laboratory to accompany 101. Credit may not be earned in both 103 and 208.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('631', '35060', 'GEOL', '104', '2006', 'HISTORICAL LABORATORY', 'Two hours laboratory to accompany 102.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('632', '107281', 'GEOL', '110', '2006', 'THE AGE OF DINOSAURS', 'A nontechnical survey, especially designed for nonscience majors, of what is currently known about dinosaurs and their world. Their origin, evolution, ecology, physiology, and social behavior are reconstructed from bones, tracks, nests, and applied biology, and reasons for their extinction are considered. Emphasis is placed on viewing dinosaurs as superbly successful members of their ecosystem.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('633', '989664', 'GEOL', '280', '2006', 'OCEANOGRAPHY', 'The origin and development of the ocean basins and seawater. Ocean processes, marine sediments and ocean resources also will be considered. Prerequisite: GEOL 101.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('634', '608315', 'GEOS', '450', '2006', 'REMOTE SENSING OF THE ENVIRONMENT', 'This course will cover the basics and the interpretation of remote sensing data that will include aerial photography, U. S. and French satellite imagery, thermal infrared, Sideways Looking Radar, Synthetic Aperture Radar, and other airborne platforms such as the Daedalus Multispectral Scanner used by NASA.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('635', '727052', 'GRMN', '103', '2006', 'ELEMENTARY CONVERSATION', 'Especially designed to meet the needs of travelers and students interested in a broad initiation to the language. (Does not substitute for 101, 102 or 105.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('636', '238405', 'GRMN', '209', '2006', 'ADVANCED GERMAN', 'Introduction to finer points of German language, development of writing skills in German, grammatical and stylistic analysis of literary texts. Prerequisite: 202 or 205.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('637', '801931', 'GERO', '101', '2006', 'BASIC ACTIVITY', 'An introduction to the profession of Activity Directors, Therapeutic activity practice, and their integration into health care. An examination of practice standards, practitioner behavior, activity planning, and service delivery.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14044', '123064', 'GERO', '410', '2006', 'Economics Of Aging', 'An examination of issues related to the economic status of persons as they grow older. Attention will be given to problems and policies which relate to income maintenance, planned and unplanned expenditures, inflation, reduced income, post-retirement employment, public and private pensions, health insurance, pre-retirement employment, public and private pensions, health insurance, pre-retirement counseling and legislative reforms. (Formerly ECON 510, same as ECON 410.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14329', '879153', 'GERO', '411', '2006', 'Social Work Practice With The Elderly', 'An introduction to the social, economic, and psychological dimensions of aging using a person-in-environment approach. (Same as SOCW 411.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14050', '782319', 'GERO', '424', '2006', 'Women And Aging', 'Examines the life experiences of women as they age. Emphasis is on sociological influences on perceptions of aging among women and on aging women’s roles in society. (Same as SOCL 424)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14056', '773056', 'GERO', '425', '2006', 'Seminar In Death And Dying', 'Exploration of the social organization of dying, including a view on cross-cultural perspectives on death, treatment of the dying patient, and the grief process. (Same as SOCL 425)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('642', '247991', 'GERO', '431', '2006', 'ELDER LAW', 'An examination of legal challenges older Americans are facing as they plan their later years and seek to maximize their quality of life. These challenges include age discrimination in employment, housing alternatives and options, financial management and planning, medical needs and attention, and consent, and long-term care.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('645', '59452', 'GERO', '456', '2006', 'INTERNATIONAL STUDIES', 'Extended gerontology field study outside the United States with guest lectures, visits to gerontological related facilities and programs, and assigned readings related to aging in the designated study region. A. Europe, B. England, C. Latin America, D. Far East, E. Middle East. Individual topics may not be repeated for credit.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('646', '116152', 'GERO', '457', '2006', 'REGIONAL UNITED STATES FIELD STUDIES', 'Extended gerontology field study within the United States with guest lectures, visits to gerontological facilities and programs, and assigned readings related to aging in the designated study region. A. North, B. South, C. East, D. West. Individual topics may not be repeated for credit.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('13061', '205408', 'GOVM', '101', '2006', 'Introduction To Political Science', 'Development of the state; nature and problems of government in relation to the individual; emphasis upon the development of various forms of government.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13067', '764708', 'GOVM', '200', '2006', 'Directed Study', 'Selected studies in government. Content varies. A. American Government (Same as GOVM 201.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13073', '109376', 'GOVM', '201', '2006', 'American National', 'Consideration of the nature of democracy, civil rights, federalism, the political process, the legislative, executive, and judicial branches, and current problems.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13079', '351909', 'GOVM', '202', '2006', 'State And Local', 'An intensive study of state and local government, structural components, political process, and basic problems. Special emphasis on Louisiana government.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13103', '118190', 'GOVM', '406', '2006', 'Political Parties, Campaigns And Elections', 'Political parties, Campaigns and Elections focuses on three closely related topics and phenomena: parties, campaigns and elections in the American context. We will be seeking to understand their parallel, and in some sense, symbiotic development and relationships. We will study the history of party politics in the United States, focusing on change and development in relationship to the dynamic American Republic. Prerequisite: GOVM 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13109', '787621', 'GOVM', '407', '2006', 'Western Political Theory', 'A study of political theory beginning with Plato and the ancient Greeks and continuing through the Middle Ages, Reformation, and Enlightenment to Marx, Mills and the modern age. The central questions of political thought are explored e.g., what is the good?; what is justice?; how is political authority acquired?; where does sovereignty reside?; and how do we incur obligations? Prerequisite: GOVM 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13115', '533147', 'GOVM', '413', '2006', 'Constitutional Law: Governmental Powers', 'A study of the U.S. Constitution through analysis of decisions of the Supreme Court. The cases demonstrate the nature of judicial function, the nature of the federal system, and the scope and character of the process of the national government. Prerequisite: GOVM 201 or approval of department head and permission of the instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13121', '553789', 'GOVM', '414', '2006', 'Constitutional Law: Civil Rights', 'A study of the U.S. Constitution through analysis of decisions of the Supreme Court. The cases demonstrate the scope and character of the limitations on governmental powers concerning civil rights such as speech, press, religions, and rights against discrimination. Prerequisite: GOVM 201 or approval of department head and permission of the instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13127', '720669', 'GOVM', '417', '2006', 'Public Administration And Administrative Law', 'Structure and processes of public administration, practices and processes of public administration, practices in personnel, organization and development of administrative law. Prerequisite:  201. (Formerly 309.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13139', '693779', 'GOVM', '420', '2006', 'Louisiana Government And Politics', 'Development and organization of political processes in Louisiana; contemporary problems of Louisiana state and local governments. (Formerly 302.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13145', '280343', 'GOVM', '421', '2006', 'American Political Theory', 'This course focuses on the origin and development of American political thought. Topics include Locke and liberalism, Puritanism and republicanism, Paine and the revolutionary impulse, Founders and Anti-Federalists, Jefferson’s agricultural republic, Tocqueville’s social America, Jacksonian democracy, Lincoln and Calhoun, populists, progressives and pragmatists, New Dealers and social engineers. Prerequisite: GOVM 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13157', '961005', 'GOVM', '429', '2006', 'Women And Politics', 'An examination of the role that women have played in the development of the U.S. political system, including historical analysis of the barriers to women’s participation and the impact of politically active women on contemporary issues.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13163', '93926', 'GOVM', '433', '2006', 'The Judicial Process', 'Structure, functions, and operations of the judiciary. Prerequisite: 201. (Formerly 312.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13193', '315402', 'GOVM', '454', '2006', 'Congress And The Presidency', 'A course focused on two of the three major institutional components of American politics. Main topics include the history of the Congress and Presidency (i.e., the two institution’s structural, behavioral and institutional adaptation to a changing America), the internal and external dynamics of public policy production (i.e., how demands arising in the electorate and elsewhere are conveyed to and/or produced within the Congress and the translation of these demands into specific laws and policies), and, the complex interaction between the Congress and the Presidency as different and separate institutions sharing power. Prerequisite: GOVM 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13169', '68286', 'GOVM', '440', '2006', 'Comparative', 'The structure, theory, organization, and methods of control in the governments of the major European powers; recent political trends. Prerequisite: 201. (Formerly 305.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13175', '83591', 'GOVM', '441', '2006', 'Comparative Government And Politics In Latin Ameri', 'A comparative survey of governmental institutions and political processes which characterized the Latin American republics from the colonial period to the present. (Formerly 306.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13181', '923717', 'GOVM', '450', '2006', 'American Foreign Policy', 'Major foreign policies, objectives, and commitments of the United States; their political, economic, and military implications. Policy decisions currently confronting the United States in various areas of the world. (Formerly 317.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13187', '795337', 'GOVM', '453', '2006', 'International Politics', 'The nation-state system, power politics, processes affecting international relations; sources of tension in international politics and possibilities of their peaceful resolution. (Formerly 315.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13199', '321995', 'GOVM', '457', '2006', 'Contemporary Political Thought', 'Political thought of the twentieth century; liberalism, modern totalitarianism, conservatism, Freudianism, existentialism, and democracy.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13205', '725649', 'GOVM', '461', '2006', 'International Political Economy', 'A course focused on the intersection of politics and economies in relations among actors in the international environment. Main topics include: international trade strategies; the political strategies of transnational firms; assessment of international organization; the role of the Uited States in the contemporary global economy. Prerequisite: 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13211', '695003', 'GOVM', '463', '2006', 'Comparative Politics Of Race And Ethnicity', 'International comparative analysis of how political processes both shape and are shaped by racial and ethnic differences, both within and among nation-states. Prerequisite: 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13217', '201149', 'GOVM', '465', '2006', 'Global Development', 'Cross-cultural exploration of the processes of economic and political development in different regions and countries of the world, including discussion of globalization and its character and effects in geographical and political settings. Prerequisite: 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('671', '507968', 'HIST', '110H', '2006', 'HONORS HISTORY', 'An introduction to the world’s major ideas and historical developments from ancient times to the 1550s. This course will emphasize the contributions of the diverse ancient civilizations to the formation of the modern world. Prerequisite: Admission to the Honors Program or permission of the Honors Council. Substitutes for History 111.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('672', '468059', 'HIST', '111', '2006', 'WORLD CIVILIZATION', 'Survey of world history from early humanity to the eighteenth century.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('673', '449139', 'HIST', '112', '2006', 'WORLD CIVILIZATION II', 'Survey of world history since the seventeenth century.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('674', '838583', 'HIST', '209H', '2006', 'HONORS HISTORY', 'An introduction to the world’s major ideas and historical developments from the 1500s through the 1700s. This course will emphasize the contributions of the diverse civilizations to the formation of the modern world. Prerequisite: Admission to the Honors Program or permission of the Honors Council. Substitutes for History 112.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('675', '514944', 'HIST', '219H', '2006', 'HONORS HISTORY', 'U.S. history from 1800 to present with emphasis on the U.S. in the context of European and global affairs. Prerequisite: Admission to the Honors Program or permission of the Honors Council. Substitutes for History 201 or  202.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('676', '508591', 'HIST', '302', '2006', 'The West', 'The historical development of the American West as seen in the videotape series, &quot;THE WEST&quot; by Ken Burns, lectures and readings. The role the West played in molding the American experience will be evaluated.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('677', '954734', 'HIST', '305', '2006', 'LOUISIANA HISTORY', 'An overview of Louisiana History from the first Native Americans to the present. Major areas of emphasis include Native American cultures; French and Spanish colonial period; Louisiana Purchase and ante-bellum Louisiana; Civil War and Reconstruction; the Huey P. Long era; and modern Louisiana. Special attention is given to race relations, political and socio-economic developments, and the major personalities who shaped Louisiana.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14104', '720351', 'HIST', '309', '2006', 'Islam', 'Survey of Islamic history from the inception in 7th century Arabia to the present day. Special emphasis on the religious principles and the institutions of the civilizations to which it gave rise. Attention is given to those factors that continue to make Islam an important force in the world today. (Same as RELG 309.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('679', '273299', 'HIST', '310', '2006', 'AFRICAN-AMERICAN HISTORY', 'Black American history beginning with early West African civilization to the present. Attention will be devoted to immigration, the slave system, civil rights, and modern black movements with emphasis on significant African-American leaders. May not be substituted to meet History requirements in 111-112, 201-202, or 305. (Formerly 105.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('680', '64111', 'HIST', '312', '2006', 'EASTERN CIVILIZATION', 'An introduction to the history of non-western cultures. The civilizations of the Far East, Africa, and the Middle East are examined from their beginnings to the present.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('681', '72261', 'HIST', '399', '2006', 'HISTORIAN’S CRAFT', 'Historical research methods and an introduction to historiography. Prerequisite: History majors only or instructor permission.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('682', '25274', 'HIST', '408', '2006', 'CONTEMPORARY HISTORY', 'A historical perspective on issues and events of the contemporary world. Prerequisites: HIST 112 or 202 or instructor consent.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('683', '182300', 'HIST', '420', '2006', 'THE HOLOCAUST', 'This course will confront the background, events, and consequences of the extermination of European Jews during World War II. Prerequisite: HIST 112, 202 or consent of instructor.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('684', '859882', 'HIST', '430', '2006', 'WORLD WAR II', 'An examination of the causes, conduct, and immediate aftermath of World War II giving attention to major military campaigns and experiences of war as seen by military leaders and combatants in Asia, Europe, and the Pacific. Prerequisite: HIST 112 or 202.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('685', '558421', 'HIST', '436', '2006', 'THE AMERICAN CIVIL WAR', 'The growing sectional conflict between the industrial North and the agrarian South; secession; major wartime problems of the Union and the Confederacy; principal military campaigns in the War’s various theaters; the significance of the Civil War in American History. Prerequisite: HIST 201.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('686', '736089', 'HIST', '437', '2006', 'RECONSTRUCTION AND THE NEW SOUTH, 1865-1939', 'Reconstruction represented a larger change in the nation’s history than the Civil War as the South experienced its evolving stages, but more particularly, dealing with new relationships between the races. Efforts to revive agriculture and industry; the development of politics, education, religion, society, and culture from the postbellum period through the Depression and the New Deal. Prerequisite: HIST 202.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('687', '580551', 'HIST', '439', '2006', 'THE SOUTH, WORLD WAR II TO THE PRESENT', 'World War II proved to be a significant watershed in the South. Emphasis will be placed on its industrialization, agricultural mechanization, and, especially the Second Reconstruction in Civil Rights, as well as its political, economic, religious, social, and cultural development. The South as a section will be placed in its proper perspective within the national experience. Prerequisite: HIST 202.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('688', '362211', 'HIST', '446', '2006', 'US MILITARY HISTORY 1775-1865', 'This course examines the development of the U.S. Military and the wars it fought from the American Revolution through the Civil War.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('689', '429868', 'HIST', '447', '2006', 'US MILITARY HISTORY 1865-PRESENT', 'This course examines the development of the U.S. Military and the wars it fought from the end of the Civil War to the present.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('690', '860894', 'HIST', '450', '2006', 'AMERICAN COLONIAL', 'A social history class which will concentrate on four main communities established in North America: New England, Pennsylvania, Virginia, and the Appalachian frontier. The role that religion, Trans-Atlantic influences, and the environment played in the formation of these communities will be considered, as will their different reactions to Native Americans, slaves, and the role of women. The Anglo- American imperial relationship to 1775 will also be reviewed. Prerequisite: HIST 201.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('691', '323216', 'HIST', '451', '2006', 'THE REVOLUTIONARY GENERATION', 'The history of the American Revolution, placing it within the Trans-Atlantic community, its impact upon domestic society, and its influence upon the revolutionary generation of England, Ireland, and Europe generally. In considering political developments, attention will also focus upon the Revolution’s impact upon women, Native Americans and the institution of slavery. The course will conclude with a review of the Articles of Confederation and the weaknesses of the early Federal Union. Prerequisite: HIST 201.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('692', '523793', 'HIST', '452', '2006', 'THE AGE OF JEFFERSON AND JACKSON', 'An investigation into the complex character of Jefferson, his political impact upon the Republic and the ambiguous legacy he left after 1826. Andrew Jackson will be considered as a symbol of a new democratic age. The new Jacksonian spirit will be reviewed in the light of social reform movements, 1815 to 1840. The course will also consider Native-American cultures and the changing attitudes towards slavery. Prerequisite: HIST 201.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('693', '176297', 'HIST', '456', '2006', 'THE GILDED AGE', 'Economic, political, and social changes during the rise of industrial America between Reconstruction and the coming of World War I. Prerequisite: HIST 202. (Formerly 438.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('694', '391502', 'HIST', '458', '2006', 'RECENT AMERICA', 'The position of America in world affairs since 1914; the political and economic forces that led to the Depression; World War II and the changing concept of American democracy. Prerequisite: HIST 202. (Formerly 415.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('695', '607384', 'HIST', '460', '2006', 'THE OLD SOUTH', 'The political, economic, and social development of the ante-bellum South. Settlement patterns; sectional distinctiveness; political ideology; development of slavery and the plantation system; abolitionism and slavery defense; and the growth of southern nationalism. Prerequisite: HIST 201. (Formerly 403.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('696', '100014', 'HIST', '463', '2006', 'ADVANCED LOUISIANA HISTORY', 'each. Specialized study of Louisiana history, focusing on personalities and the political, military and socio-economic developments that have shaped the state’s history. A. Beginnings to 1803; B. 1803 - 1865; C. 1865 to Present. No more than 6 semester hours may be applied toward degree requirements.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('697', '186839', 'HIST', '464', '2006', 'THE AMERICAN FRONTIER', 'The westward movement in America from 1607 to 1890, emphasizing the process of pioneering and the influence of the frontier on American institutions. (Formerly 315.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('698', '929379', 'HIST', '465', '2006', 'US DIPLOMATIC HISTORY', 'American foreign relations from the Revolutionary era to the present with emphasis on the forces that have shaped American foreign policy and on the diplomatic history of the United States from 1898 to the present. Prerequisite: HIST 202. (Formerly 417.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('699', '825662', 'HIST', '469', '2006', 'AMERICAN CONSTITUTIONAL AND LEGAL HISTORY', 'The development of American constitutional and legal systems from the Colonial period to the present. An emphasis will be placed on the Constitutional Convention of 1787 and the intent of the Founding Fathers. Important court decisions will be reviewed up to the Reconstruction period. Later topics, such as civil rights, the imperial presidency and constitutional developments within this century, will also be considered. Prerequisites: HIST 201, 202.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('700', '881842', 'HIST', '470', '2006', 'HISTORY OF BIBLICAL LANDS', 'This course is an introduction to the material culture, cultural history and cultural process of the biblical world, with an emphasis on the connections between archaeology and the Bible. Prerequisites: History 111.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('701', '130528', 'HIST', '472', '2006', 'COLONIAL LATIN AMERICA', 'Discovery, exploration, and settlement; Spanish and Portuguese colonial systems; political, economic, and social development including the wars of independence. (Formerly 321.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('702', '333631', 'HIST', '473', '2006', 'MODERN LATIN AMERICA', 'The Latin American states from independence to the present; internal problems and progress; international relations. (Formerly 322.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('703', '349901', 'HIST', '474', '2006', 'HISTORY OF ANCIENT EGYPT', 'This course is intended as a broad survey of Egyptian history, culture and archaeology from the Old Kingdom down to the Roman period. It is an introduction to the social, economic, cultural and religious developments of the first nation state in the world. Prerequisites: History 111.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('704', '579666', 'HIST', '475', '2006', 'ANCIENT NEAR EAST AND GREECE', 'Egypt, Sumer, Assyria, Babylonia and other civilization in the Ancient Near East from 3100 B.C.E.; Greece from the Mycenaean Period through Periclean Athens. Emphasis on archaeological evidence, political developments, cultural accomplishments. Prerequisite: HIST 111.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('705', '172214', 'HIST', '476', '2006', 'ANCIENT ROME', 'Ancient Rome from Romulus through the early Empire, with an emphasis on the development of Roman traditions during the Republic and the transition from Republic to Empire under the Julio-Claudians. Roman government, class structure, and the significance of important historical figures will be emphasized. Prerequisite: HIST 111.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('706', '864845', 'HIST', '478', '2006', 'RENAISSANCE AND REFORMATION', 'The transition in Western civilization from the medieval to the modern era, 1300 to 1648; cultural, economic, social, religious, and political developments of the era. Prerequisite: HIST 111. (Formerly 411.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('707', '88257', 'HIST', '479', '2006', 'ABSOLUTISM AND REVOLUTION, 1610-1815', 'This course surveys the political, social, economic and cultural history of Europe from the birth of Absolutism to the downfall of Napoleon Bonaparte. In addition to addressing the cultural and intellectual changes of the 17th and 18th centuries, this course will focus on the social and economic developments of the period, changes that ultimately shaped the modern world. Particular emphasis will be given to the emerging nation-state, the new scientific world-view, Enlightenment ideology, and the revolutionary turmoil of the late 18th century. Prerequisite: HIST 112.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('708', '126949', 'HIST', '480', '2006', 'EUROPE, 1815-1890', 'This course surveys the political, social, economic and cultural history of Europe from the downfall of Napoleon to the golden age of imperial Europe at the end of the 19th century. Topics considered include the effects of industrialization, the impact of widespread revolution, the unification of Bismarckian Germany, and the spread of European Imperialism. Prerequisite: HIST 112.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('709', '585197', 'HIST', '481', '2006', 'EUROPE, 1890-1945', 'This course surveys European history from the close of the 19th century to the end of World War II. The focus is primarily upon national and international politics, but with careful attention to economic, social and cultural developments in their relationship to political change. Prerequisite: HIST 112.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('710', '361555', 'HIST', '485', '2006', 'ENGLISH CONSTITUTIONAL AND LEGAL HISTORY', 'Emphasis on the unique legal and constitutional developments of the English system in Western Europe and their impact on American political thought. The class will consider the notion of limited government, the rule of law and the development of political party government from 1730 to 1979. The latter part of the class will concentrate on the decline of the Liberal Party, the rise of the Labor Party and the important legal reforms since World War II. Prerequisities: HIST 111, 112.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('711', '728139', 'HIST', '487', '2006', 'FRENCH HISTORY', 'A survey of French political institutions, cultural developments, and social structure from approximately 800 to the present time. Topics will include Charlemagne, the French Revolution and Napoleon, and France’s position in the modern world. Prerequisites: HIST 111, 112. (Formerly 419.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('712', '657457', 'HIST', '488', '2006', 'REVOLUTIONARY FRANCE, 1789-1871', 'A survey of the political, social, economic and cultural history of France from the origins of the French Revolution to the collapse of the radical Paris Commune in 1871. The course will explore the vast changes in France that transformed it from a pre-industrial state of peasants and aristocrats to a world of modern industry and technology. Prerequisite: HIST 112.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('713', '386829', 'HIST', '490', '2006', 'EAST CENTRAL EUROPE SINCE 1400', 'This course will examine the social, political, economic, and cultural development of the lands traditionally known as Poland, Hungary, Bohemia, and Ukraine from the late Middle Ages to the present. This course will introduce the main forces and personalities that shaped Eastern European history in the past and continue to shape it today.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('714', '373358', 'HIST', '492', '2006', 'RUSSIA TO 1917', 'Founding of the nation, tsarist institutions, conditions leading to the revolutions of 1917. Prerequisite: HIST 112. (Formerly 333.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('715', '517339', 'HIST', '493', '2006', 'SOVIET UNION AND COMMONWEALTH OF INDEPENDENT STATE', 'The revolutions of 1917 and the development of the Soviet system. Political, social, and economic developments in the U.S.S.R. with special attention to the role of the Soviet Union in world affairs. The collapse of the U.S.S.R. and the emergence of the Commonwealth of Independent States. Prerequisite: HIST 112. (Formerly 425.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('716', '945250', 'HORT', '323', '2006', 'POST HARVEST PHYSIOLOGY', 'Physiological changes and problems during artificial ripening, storage and movement of plant materials. Emphasis placed on maintenance and extension of shelf life in relation to changes in the environment. Three hours lecture and two hours laboratory. Prerequisite: AGRI 101 or 103. (Formerly HORT 321, 322).', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('717', '109447', 'HORT', '350', '2006', 'FLORAL ARRANGING', 'A survey of the art of floral arranging including the basic materials, equipment, design dish and bottle gardens, drying flowers, judging, corsages and specialty pieces. Prerequisites: AGRI 101 or 103.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('718', '97891', 'HORT', '362', '2006', 'ORNAMENTAL HORTICULTURE', 'Principles and practices involved in the production, usage and maintenance of woody ornamentals, florist, and greenhouse plants. Three hours lecture and two hours laboratory. Prerequisite: AGRI 101 or 103. (Formerly HORT 360, 361).', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('719', '307461', 'HORT', '412', '2006', 'VEGETABLE AND FRUIT INDUSTRY', 'Management practices concerning establishment, production, storage and marketing of vegetables and fruits. Three hours lecture and two hours laboratory. Prerequisite: AGRI 101 or 103. (Formerly HORT 410, 411).', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('720', '539315', 'HUMN', '101', '2006', 'HUMANITIES', 'An interdisciplinary study of man and man’s relationship to the world. Beginning with pre-history through the Renaissance. Designed for superior students, the course may be scheduled in lieu of English 101 and History 101. Class meets 6 hours per week.', '6', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('721', '958784', 'HUMN', '102', '2006', 'HUMANITIES', 'A continuation of 101. Beginning with the Age of Reason and concluding with the Twentieth Century. Designed for superior students, the course may be scheduled in lieu of English 102 and History 102. Class meets 6 hours per week. Prerequisite: 101.', '6', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('722', '630677', 'INSU', '205', '2006', 'RISK AND INSURANCE', 'A study of pure risk and risk- bearing, including insurance and non-insurance methods of handling risks; introduction to life, disability, property, marine, and liability insurance. F,Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('723', '63108', 'INSU', '307', '2006', 'PROPERTY AND CASUALTY INSURANCE', 'The fundamentals of property and casualty insurance, including analysis of contracts, rating, underwriting, regulation, and financial strength of insurers. Prerequisite: INSU 205 or permission of department head. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('724', '135082', 'INSU', '308', '2006', 'BUSINESS RISK MANAGEMENT', 'A study of the analysis and treatment of the pure, nonspeculative risks faced by the business firm. The development of the risk management process, exploration of the use of various risk management techniques on identified exposures, and application of the process and those techniques in a risk management case study. Applicable to any business major and to various non-business majors. Introduction to the concept of pure risk and the minimization of its costs to businesses, nonprofit organizations, and government bodies. Prerequisite: INSU 205 or permission of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('725', '22179', 'INSU', '309', '2006', 'LIFE INSURANCE', 'Development of the life insurance business; carriers, types of insurance and annuity contracts and their uses, premium and reserve computation and state regulation, and problems on life insurance programs for individuals, families, and institutions. Prerequisite: INSU 205 or permission of department head. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('726', '670492', 'INSU', '311', '2006', 'HEALTH AND SOCIAL INSURANCE', 'Analysis of the nature of health and social insurance; hospitalization and medical insurance, surgical benefits, major-medical coverages, disability income contracts; causes, extent, and economic consequences of old age dependency, unemployment, and disability. Prerequisite: INSU 205 or permission of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14143', '782259', 'INSU', '319', '2006', 'Insurance Accounting', 'The use of accounting information to evaluate the financial stability and financial strength of insurance companies. Not open to accounting majors. (Same as ACCT 319). Prerequisite: INSU 205; Junior standing or permission of department head. Sp', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13572', '209018', 'KINS', '201', '2006', 'First Aid And CPR', 'First aid to the victims of accidents, sudden illness, cardiac or respiratory emergencies. First aid and CPR certification granted to those meeting agency requirements. Two hours laboratory. (Formerly First Aid and Personal Safety.)', '1', '1', '1', '0', '', '0');
INSERT INTO `courses` VALUES ('729', '599871', 'KINS', '202', '2006', 'Strength Training For School Age Children', 'This course is designed to enhance the candidates’s ability to assess and implement safe and appropriate methods in resistance training programs in the school system for children and the adolescent in physical education/athletics. Prerequisite: KINS 211.', '2', '2', '2', '0', '', '0');
INSERT INTO `courses` VALUES ('13579', '60759', 'KINS', '211', '2006', 'Foundations Of Physical Education', 'Basic understanding and familiarization of health, physical education and recreation as a profession; including historical background, principles, trends, curriculum and professional preparation. For Health and Physical Education majors only.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13585', '90860', 'KINS', '220', '2006', 'Lifeguard Training', 'Designed to train and certify an individual to serve as a non-surf lifeguard and as an aquatic facility manager.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13591', '730398', 'KINS', '221', '2006', 'Concepts Of Personal Wellness', 'The purpose of this course is to provide learning opportunities for the development of conceptual and functional knowledge of health-related physical fitness and the role it plays in the development and maintenance of human wellness. The health-behavior-gap is examined, and specific emphasis is placed on the individual long-term preventive and rehabilitative benefits derived from lifelong participation in physical activity. Prerequisite: Kinesiology majors only.', '2', '2', '2', '0', '', '0');
INSERT INTO `courses` VALUES ('13597', '394389', 'KINS', '233', '2006', 'Water Safety Instruction', 'Qualifies individuals to teach and certify American Red Cross Aquatic courses. Prerequisite: 220.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13603', '440761', 'KINS', '241', '2006', 'Personal And Community Health', 'An orientation course in health which deals with nutrition, stress, exercise, diseases and their prevention.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13615', '310064', 'KINS', '248', '2006', 'Officiating', 'Principles, techniques and practices of officiating with emphasis on rules, rule interpretations and mechanics of basketball and football. Practical experience will be arranged.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13621', '566603', 'KINS', '249', '2006', 'Officiating Spring Sports', 'This course is designed to enhance the student’s ability to understand the basic principles, techniques, and practices of officiating youth sports with emphasis on rules, rule interpretations, and mechanics for Dixie League softball/baseball, FIFA U6-U19 soccer, and USTA tennis.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13633', '305606', 'KINS', '256', '2006', 'Living With Health', 'Content and theory related to basic health information; critical health issues; improving and maintaining optimal health and wellness. Students with prior credit in Kinesiology 241 are not eligible to receive credit in Kinesiology 256. Cannot be used to satisfy major or minor requirements.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13639', '398321', 'KINS', '301', '2006', 'Coaching Football', 'Theory, techniques, and skills of coaching football. Prerequisites: KINS 211 and junior standing.', '2', '2', '2', '0', '', '0');
INSERT INTO `courses` VALUES ('13645', '693551', 'KINS', '302', '2006', 'Coaching Basketball', 'Theory, techniques, and skills of coaching basketball. Prerequisites: KINS 211 and junior standing. INSERT INTO `courses` VALUES ('13651', '890802', 'KINS', '303', '2006', 'Coaching Baseball And Softball', 'Theory, techniques, and skills of coaching baseball and softball. Prerequisites: KINS 211 and junior standing.', '2', '2', '2', '0', '', '0');
INSERT INTO `courses` VALUES ('13657', '759876', 'KINS', '304', '2006', 'Coaching Track And Field', 'Theory, techniques, and skills of coaching track and field. Prerequisites: KINS 211 and junior standing.', '2', '2', '2', '0', '', '0');
INSERT INTO `courses` VALUES ('13663', '884610', 'KINS', '305', '2006', 'Techniques In Group Exercise', 'This course is designed to examine current trends and techniques used in the field of group exercise. Prerequisite: KINS 211. Kinesiology majors only.', '2', '2', '2', '0', '', '0');
INSERT INTO `courses` VALUES ('743', '548259', 'KINS', '306', '2006', 'INTRODUCTION TO TEACHING HEALTH AND PHYSICAL EDUCA', 'Clinical based course where physical education majors examine teaching physical education in diverse settings. Candidates will examine how students and teachers spend their time during physical education and the impact it has on learning. Prerequisite: KINS 211.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('13669', '146706', 'KINS', '318', '2006', 'Fitness Assessment Techniques', 'This course is designed to provide the exercise professional with the necessary cognitive and laboratory experiences essential for developing safe and accurate physical fitness testing practices. Prerequisite: KINS 211. Kinesiology majors only.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('745', '257969', 'KINS', '319', '2006', 'INTRODUCTION TO MOTOR LEARNING', 'The learning of motor skills to facilitate the implementing of appropriate strategies and techniques for instructional, training, and rehabilitation applications. Prerequisite: KINS 211. Kinesiology majors only.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('58669', '116037', 'KINS', '010', '2006', 'Varsity Sports', 'There is no course description available at this time.', '1', '1', '10', '0', '', '0');
INSERT INTO `courses` VALUES ('747', '91956', 'KINS', '322', '2006', 'SEXUALITY EDUCATION', 'Enhances the understanding of human sexuality and delivery of age-appropriate sexuality education. Prerequisite: KINS 241.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('13675', '367416', 'KINS', '325', '2006', 'Driver Education And Traffic Safety', 'Administrative problems and policies, instructional methods and materials, accident causes and prevention, and content of a high school course in driver education. Prerequisite: Junior standing.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13681', '163489', 'KINS', '333', '2006', 'Activities For Elementary School', 'Materials, techniques and methods of teaching physical education in elementary schools. Sophomore level and above.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('750', '399417', 'KINS', '334', '2006', 'METHODS AND MATERIALS IN PHYSICAL EDUCATION', 'Materials, techniques, and methods of teaching physical education in the three different school levels. Prerequisite: Must be admitted to teacher education.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('13687', '906730', 'KINS', '335', '2006', 'TEAM SPORTS', 'Basic skills, strategies, rules, and teaching procedures of selected team sports. Prerequisite: Health and physical education major.', '2', '2', '2', '0', '', '0');
INSERT INTO `courses` VALUES ('752', '122274', 'KINS', '355', '2006', 'LIFETIME SPORTS', 'Basic skills, rules, strategies, and teaching procedures of selected individual lifetime sports. Prerequisite: Health and physical education major.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('753', '30095', 'KINS', '365', '2006', 'FITNESS', 'Basic knowledge, values, understanding, and teaching procedures of selected fitness programs. Courses also includes a review of health related fitness testing, risk factors, risk stratification, medical history and HPAA compliance. Prerequisite: KINS 211, KINS 221. Kinesiology majors only.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('13693', '969837', 'KINS', '402', '2006', 'Exercise Program Design', 'This course is designed to apply the principles of exercise leadership to clinical and preventive settings, with particular emphasis on formulation of the exercise prescription. Prerequisite: KINS 318. KINS majors only.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('755', '257356', 'KINS', '403', '2006', 'TESTS AND MEASUREMENTS IN PHYSICAL EDUCATION', 'Purposes and needs of testing with emphasis on techniques and administration of tests in physical education. Prerequisite: Must be admitted to teacher education.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('13711', '52273', 'KINS', '411', '2006', 'Athletic Injuries', 'The prevention and emergency care of athletic injuries. Prerequisites: KINS 211 and junior standing. Kinesiology majors only.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('58679', '792910', 'KINS', '437', '2006', 'Physiology Of Exercise', 'Acute and chronic physiological changes in response to exercise. Emphasis on the practical application of exercise training for health, fitness, and- performance. Prerequisite: Junior standing. Kinesiology majors only. Concurrent enrollment in KINS 438.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13699', '492451', 'KINS', '406', '2006', 'Advanced Driver Education', 'Curriculum innovations and instructional devices (with lab). In-depth study of curriculum materials and instructional devices and techniques including simulation, multi-media, driving range, on-street instruction, and motorcycle. Instruction in the use of all forms supplied by the State Department of Education. Prerequisites: KINS 325 and junior standing.', '4', '4', '4', '0', '', '0');
INSERT INTO `courses` VALUES ('13705', '261290', 'KINS', '408', '2006', 'Physical Activity For Students With Special Needs', 'This course examines how physical activities and motor skills can be tasked analyzed and modified to include a wide range of individuals with special needs. Basic knowledge, skills and competencies in Special Education are covered with an emphasis in pedagogical principles.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13717', '240201', 'KINS', '413', '2006', 'Management Of Health, Physical Education, And Recr', 'The purpose and functions of the management process in the instructional and related area programs in today’s classroom as well as experiences in those areas outside the normal classroom setting. Federal and state regulations in education, alternative programs, discipline and other current problems are discussed. Ten (10) hours of classroom observation will be completed during this course. Prerequisite: KINS 211 and Junior standing. (Formerly Administration and Health, Physical Education, and Recreation.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('58665', '633125', 'KINS', '419', '2006', 'Modern Fitness', 'Lectures and exercises designed to convert deconditioned people to conditioned status with sufficient basic knowledge to remain conditioned for a lifetime. Prerequisite: Junior standing.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('58673', '970598', 'KINS', '431', '2006', 'Methods And Materials In Health Education', 'Materials, techniques, and methods of teaching health in schools. Ten (10) hours of classroom observation will be completed during this course. Prerequisite: Must be admitted to teacher education.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('58675', '947598', 'KINS', '432', '2006', 'Anatomical And Physiological Kinesiology', 'Application of anatomy and physiology to analysis and improvement of human performance in gross body movement. Prerequisite: Junior standing and KINS 211. KINS majors only.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('58677', '640330', 'KINS', '435', '2006', 'Practicum In Physical Education (general)', 'Observation and participation in non-teaching physical education, recreational or health related programs for one semester. Prerequisite: Health and physical education major (general).', '6', '6', '6', '0', '', '0');
INSERT INTO `courses` VALUES ('58681', '280663', 'KINS', '438', '2006', 'Exercise Physiology Laboratory', 'A laboratory course to accompany KINS 437. Emphasis is given to demonstration of physiological responses to exercise testing, as well as other laboratory procedures unique to exercise science. Prerequisite: Credit for or concurrent enrollment in KINS 437. Kinesiology majors only.', '1', '1', '1', '0', '', '0');
INSERT INTO `courses` VALUES ('58685', '194376', 'KINS', '442', '2006', 'Health And Physical Education Program Development', 'This course is designed to foster the examination, discussion and creation of health physical education program development. Prerequisite: Permission of instructor. Education majors only.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('768', '98516', 'KINS', '443', '2006', 'EPIDEMIOLOGY FOR THE COMMUNITY HEALTH EDUCATOR', 'Introduction to community health education/promotion epidemiology. Prerequisite: KINS 241.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('769', '678984', 'KINS', '444', '2006', 'HEALTH EDUCATION AND PROMOTION PLANNING', 'This course will enhance student understanding of community health planning. Prerequisite: KINS 241.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('770', '207752', 'KINS', '445', '2006', 'CONTROVERSIAL ISSUES IN HEALTH', 'Enhances student ability to research and debate controversial issues in health. Prerequisite: KINS 322.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('771', '989317', 'KINS', '446', '2006', 'HEALTH EDUCATION/PROMOTION FOR MULTICULTURAL POPUL', 'Enhances student ability to deliver health education to multicultural populations. Prerequisite: KINS 321.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('772', '438860', 'KINS', '447', '2006', 'COMMUNITY HEALTH EDUCATION AND PROMOTION PRACTICUM', 'Provides student the opportunity to participate in a community health education or promotion programs for one semester. Prerequisite: KINS 441.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('773', '92362', 'LATN', '101', '2006', 'ELEMENTARY LATIN', 'Cultural readings accompanied by the basics of grammar. Work in the computerized Language Learning Center is required.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('774', '537577', 'LATN', '102', '2006', 'ELEMENTARY LATIN', 'A continuation of Latin 101. Cultural readings accompanied by the basics of grammar. Work in the computerized Language Learning Center is required. Prerequisite: A &quot;C&quot; or better in LATIN 101 or approval of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('775', '585566', 'LATN', '105', '2006', 'ELEMENTARY LATIN', 'Cultural readings accompanied by the basics of grammar. (Same as 101-102.)', '6', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('776', '350330', 'LATN', '201', '2006', 'INTERMEDIATE LATIN', 'Cultural readings accompanied by analysis of grammatical content. Prerequisite: LATN 102 or approval of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('777', '603176', 'LATN', '202', '2006', 'INTERMEDIATE LATIN', 'Selections from Caesar, Catullus and Cicero. Prerequisite: LATN 201 or approval of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('778', '677522', 'LATN', '203', '2006', 'CLASSICAL STUDIES', 'Topics in Roman civilization, culture, language, literature, and religion. Prerequisite: LATN 102 or permission of the department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('13945', '411755', 'LATN', '303', '2006', 'Classical Mythology', 'Mythology of the Greeks and Romans from the creation of the world: tales of the gods, heroes, and great families that have influenced literature, art, and music. (Same as English 393).', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('780', '766665', 'LIBR', '301', '2006', 'CHILDREN’S LITERATURE', 'Survey of literature for children preschool - grade eight; application of criteria in selecting and evaluating types of literature for developmental and curriculum needs. Extensive reading of children’s books. Prerequisite: Approval of instructor.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('781', '76940', 'LIBR', '303', '2006', 'YOUNG ADULT LITERATURE', 'Study of literature for young adults in grades seven - twelve. Emphasis on reading needs and interests of youth; principles of selection and evaluation; and extensive reading of books.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('782', '624476', 'LIBR', '307', '2006', 'SCHOOL LIBRARY MEDIA ADMINISTRATION', 'Administration of school library media centers with emphasis on programs of service, objective and standards, and such management problems as acquisitions, mechanical preparation of materials, budgets, facilities, and evaluation. Includes clinical experiences and six hours of observations in library settings.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('784', '107034', 'MGMT', '301', '2006', 'MANAGEMENT CONCEPTS AND PRACTICE', 'Survey of the administrative and behavioral processes fundamental to the successful operation of various types of enterprise. Planning, organizing, leading and controlling in organizations which interact with their economic, technological, social, political, ethical and global environment. Prerequisite: Junior standing. F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('785', '594313', 'MGMT', '305', '2006', 'ORGANIZATIONAL BEHAVIOR', 'Study of human behavior in organizational settings; perception, motivation, decision making, creativity, communication, leadership, group processes, behavioral change, and related topics. Prerequisite: Junior standing or approval of department head. F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('786', '146295', 'MGMT', '307', '2006', 'HUMAN RESOURCE MANAGEMENT', 'Manpower planning, recruitment, selection, appraisal, compensation, transfer and promotion, safety; other personnel topics. Prerequisite: 301 or approval of department head. F, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('787', '159306', 'MGMT', '308', '2006', 'COMPENSATION AND BENEFITS', 'Compensation and benefits administration in public and private organizations, emphasizing determinants of compensation and benefits including corporate policy, job analysis and job evaluation. Prerequisite: MGMT 301 or 305 or consent of instructor. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('788', '234644', 'MGMT', '309', '2006', 'OPERATIONS MANAGEMENT', 'The planning and controlling of key business operations: forcasting, designing worker tasks, designing quality into the processes and into the service/product, capacity management, and total resource management (personnel, materials, equipment, capital). Prerequisites: 301; MATH 116; or consent of instructor. F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('789', '638745', 'MGMT', '315', '2006', 'STAFFING', 'Analysis of staffing systems including personnel planning, recruitment, selection, and placement practices with emphasis on laws and regulations influencing the staffing function. Prerequisite: MGMT 301 or 305 or consent of instructor. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('790', '420596', 'MGMT', '320', '2006', 'HUMAN RESOURCES DEVELOPMENT', 'Human resources development (HRD) from both an organizational and an individual point of view. Includes methods and media used in HRD and employee performance appraisal as a means of identifying HRD needs. Prerequisite: MGMT 301 or 305 or consent of instructor. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('791', '474844', 'MGMT', '402', '2006', 'INTERNATIONAL BUSINESS MANAGEMENT', 'The spheres, growth, management challenges, and importance of international business to include: the development of international business, the global environment, production and distribution logistics, technology, risk analysis, transfer pricing, strategic planning, human resources, public policy and joint ventures. Prerequisite: MGMT 301 or 305 or consent of instructor. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('792', '191955', 'MGMT', '409', '2006', 'STRATEGIC MANAGEMENT', 'Policy and strategy formulation and implementation from an overall management perspective requiring integrative analysis of business functions and disciplines as affected by social, ethical, environmental, and international competitive influences. Prerequisites: Business Law 401, Computer Science 170, Finance 315, Management 301, Marketing 301, Business 305, Quantitative Methods 210, and Senior standing in the College of Business Administration. F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('793', '772745', 'MGMT', '410', '2006', 'SEMINAR IN HUMAN RESOURCE MANAGEMENT', 'Contemporary personnel and human resources problems such as staffing, compensation and benefits, human resources development, health and safety, employee and labor relations, and personnel research. Prerequisite: MGMT 301 or 305 or consent of instructor. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('794', '445442', 'MGMT', '411', '2006', 'ADVANCED PRODUCTION AND OPERATIONS MANAGEMENT I', 'Inventory management including objectives, policies, systems and techniques. Just-in-time techniques and implementation, JIT effects on quality and human resources. Forecasting, order servicing, production and resource planning and master scheduling. Prerequisite: MGMT 309. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('795', '139553', 'MGMT', '412', '2006', 'ADVANCED PRODUCTION AND OPERATIONS MANAGEMENT II', 'Material and capacity requirements planning, including MRP and CRP techniques and concepts. Production activity control which focuses on lead-time management and measurement of controls. Implementation of production systems and technologies. Prerequisite: MGMT 309. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('796', '565655', 'MGMT', '414', '2006', 'QUALITY MANAGEMENT', 'Management of the continuous quality improvement process. Emphasis is on service and manufacturing industries. Covers a variety of methods and procedures for quality analysis and improvement. Prerequisite: MGMT 309 or consent of instructor. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('797', '726105', 'MNSC', '201', '2006', 'INTRODUCTION TO MARINE SCIENCE', 'Introduction to physical, chemical, geological, and biological processes and interactions in the oceans and coastal environments; interrelationships of man and the marine environment; emphasis on field work. Prerequisites: Introductory science course and permission of instructor. Offered summers only. Five weeks at a Louisiana Universities Marine Consortium coastal laboratory.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('798', '653754', 'MNSC', '301', '2006', 'INTRODUCTION TO MARINE ZOOLOGY', 'Field and laboratory survey of marine animals, particularly those of the Louisiana Gulf Coast, including classification, morphology, physiology, and ecology. Prerequisites: Biology 204, 205, 208, 209; permission of instructor. Offered summer only. Five weeks at a Louisiana Universities Marine Consortium coastal laboratory.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('799', '664170', 'MNSC', '401', '2006', 'MARINE BOTANY', 'Study of marine and coastal algae and vascular plants, including classification, morphology, life cycles, and ecology; emphasis on field and laboratory studies. Prerequisites: 12 hours botany including Biology 438; permission of instructor. Offered summer only. Five weeks at a Louisiana Universities Marine Consortium coastal laboratory.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('800', '405089', 'MNSC', '410', '2006', 'MARINE ECOLOGY', 'Relationships of marine and estuarine organisms to environmental factors; interactions among organisms; ecological processes of energy and materials flow; field studies of communities and ecosystems of the Louisiana coastal zone. Prerequisite: Biology 204, 205, 330, 332; Chemistry 107, 109; permission of the instructor. Offered summer only. Five weeks at a Louisiana Universities Marine Consortium coastal laboratory.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('801', '861505', 'MNSC', '420', '2006', 'MARINE MICROBIOLOGY', 'Introduction to the estuarine and marine microbes, especially bacteria and fungi; classification, methodology, role in marine ecosystems, biogeochemical cycles, and diseases of marine animals. Prerequisites: 12 hours of biology, including Biology 214, 215; permission of instructor. Offered summer only. Five weeks at a Louisiana Universities Marine Consortium coastal laboratory.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('802', '653488', 'MNSC', '450', '2006', 'MARINE INVERTEBRATE ZOOLOGY', 'General study of the classification, structure, function, and ecology of marine and estuarine invertebrates; emphasis on field studies of invertebrates of the Louisiana Gulf Coast. Prerequisites: 8 semester hours of zoology and permission of instructor. Offered summer only. Five weeks at a Louisiana Marine Consortium coastal laboratory.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('803', '843949', 'MNSC', '455', '2006', 'MARINE VERTEBRATE ZOOLOGY', 'Field and laboratory studies of marine vertebrates with particular emphasis on the fishes, including classification, structure, function, and ecology. Prerequisites: 16 semester hours of zoology and permission of instructor. Offered summer only. Five weeks at a Louisiana Universities Marine Consortium coastal laboratory.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('804', '953867', 'MNSC', '460', '2006', 'COASTAL MARINE GEOLOGY', 'Geomorphologic features of estuarine, coastal, and continental shelf environments; erosional, depositional, and geochemical processes; field and laboratory methods. Prerequisites: physical and historical geology; general chemistry or mineralogy; and permission of instructor. Offered summer only. Five weeks at a Louisiana Universities Marine Consortium coastal laboratory.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('805', '191066', 'MNSC', '470', '2006', 'MARINE ENVIRONMENTAL CHEMISTRY', 'Chemical composition of the oceans; chemical, biological, and geological interactions. Prerequisites: 16 semester hours of chemistry and permission of instructor. Offered summer only. Five weeks at a Louisiana Universities Marine Consortium coastal laboratory.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('806', '429515', 'MRKT', '301', '2006', 'FUNDAMENTALS OF MARKETING', 'Marketing by the firm in a competitive economy at the local, national, and international level. Emphasis on the development, implementation, and control of marketing strategies including market target identification and the design of a marketing mix. Prerequisite: Junior standing. F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('807', '304198', 'MRKT', '304', '2006', 'SALES MANAGEMENT', 'Organization of sales departments; selecting; training, compensating, motivating, and maintaining sales personnel; budgeting; distribution cost analysis; special problems in managing an outside sales force. Prerequisite: MRKT 301. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('808', '794241', 'MRKT', '305', '2006', 'PERSONAL SELLING', 'The fundamental principles of selling; the economic problems connected with personal selling; an analysis of the product and the market; the interview; and building good will. Prerequisite: MRKT 301. F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('809', '107037', 'MRKT', '307', '2006', 'RETAILING', 'Marketing to ultimate consumers. Operation of a retail outlet; store organization, displays, location, layout, purchasing, retail advertising, product analysis, personnel management, store services, and operating controls. Prerequisite: MRKT 301. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('810', '21944', 'MRKT', '308', '2006', 'MARKETING RESEARCH', 'Modern marketing research techniques and their application by management to problems involving marketing strategy development, implementation, and control. Prerequisites: 301; six hours of Marketing electives; MATH 116 or consent of instructor. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14152', '113178', 'MRKT', '330', '2006', 'Entrepreneurial Marketing', 'Marketing during the start up and growth stage of new ventures in a dynamic economy at the local, national, and international level. Emphasis on the application of marketing fundamentals in new growing ventures. Prerequisite: Junior standing. (Same as ENTR 353)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('812', '183976', 'MRKT', '407', '2006', 'ADVERTISING AND SALES PROMOTION', 'The nonpersonal promotion strategies of modern business. Consideration of budgets, media, messages, and timing of advertising effort. Prerequisite: MRKT 301. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('813', '646419', 'MRKT', '409', '2006', 'MARKETING MANAGEMENT', 'Analysis of actual business situations requiring decisions by marketing executives in the marketing mix and environmental areas of marketing. Prerequisites: Six hours of Marketing above MRKT 301. Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('814', '449845', 'MRKT', '410', '2006', 'PROFESSIONAL SELLING', 'An in-depth study of professional selling. Emphasis is placed on negotiating skills, customer relationship management, and general sales-related topics including sales automation, team selling, conflict resolution, ethical issues, and time/territory management. Prerequisites: MRKT 301 and MRKT 305. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('815', '291010', 'MRKT', '401', '2006', 'INTERNATIONAL', 'All phases of marketing as applied in an international operation: research, marketing channels, promotion, financing, pricing, licensing, organization requirements, and methods of coordination. Prerequisite: MRKT 301. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('816', '240733', 'MRKT', '403', '2006', 'SERVICES MARKETING', 'Study of the special characteristics of service enterprises and the development of marketing strategies for such organizations. Prerequisites: MRKT 301. Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('817', '596139', 'MCOM', '101', '2006', 'SURVEY OF MASS MEDIA FOR USERS', 'A critical survey of print and electronic media designed to enhance the user’s media literacy and media consumer skills. For non-MCOM majors only.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('818', '450315', 'MCOM', '201', '2006', 'MASS COMMUNICATIONS AND SOCIETY', 'History, structure and practices of media industries and professions, and their roles in society; introduction to issues, vocabulary and technological trends for media producers.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('819', '475376', 'MCOM', '202', '2006', 'ANNOUNCING', 'The role of the announcer; principles of communication in the electronic media; voice and diction, including foreign pronunciation; techniques for various program types A $15 fee is charged each student to help defray expenses related to this course.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('820', '234885', 'MCOM', '210', '2006', 'WRITING FOR MEDIA', 'Introduction to writing for mass media, including basics of print and broadcast newswriting, print and broadcast copywriting, and writing for online media, with special attention to the AP Stylebook. Prerequisite: ENGL 101.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('821', '870910', 'MCOM', '220', '2006', 'EVOLUTION OF AMERICAN JOURNALISM', 'The struggle of American journalists from colonial to modern times to develop, preserve and extend responsible reporting in print and electronic media.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('822', '950219', 'MCOM', '221', '2006', 'EVOLUTION OF BROADCASTING', 'History of the development of radio, television, cable, satellite and other electronic media industries and media production practices.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('824', '643656', 'MCOM', '300', '2006', 'DIRECTED STUDY', 'Selected studies in mass communications. Content varies. Prerequisite: Consent of advisor and instructor.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('825', '395662', 'MCOM', '312', '2006', 'BROADCAST COPYWRITING', 'Preparation of short-form copy for radio and television, including public service, advertising and promotional material, with emphasis on the relationship between writers and users of electronic media. Prerequisite: MCOM 210.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('826', '566393', 'MCOM', '330', '2006', 'JOURNALISM I', 'Concepts, theories, and practices of writing news for print and broadcast media, including news judgement, use of sources, organizing information, and copy editing. Introduction to news reporting for print and broadcast. Prerequisites: MCOM 210, ENGL 102, passing grade on departmental grammar exam.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('827', '209311', 'MCOM', '331', '2006', 'JOURNALISM II', 'Builds upon MCOM 330 with development of news gathering, writing and editing for print and broadcast media, emphasizing interviewing techniques, newscasting and special events and stories. Students will be assigned campus beats. Prerequisite: MCOM 330.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('828', '121767', 'MCOM', '340', '2006', 'ADVERTISING PRINCIPLES', 'Survey of the role and function of advertising in society: structures, media roles, and basic techniques and creative strategies as they apply to print and electronic media. Prerequisite: Sophomore standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('829', '220147', 'MCOM', '341', '2006', 'BROADCAST PROGRAMMING', 'Analysis of program forms used in the electronic media, effective program structure, and consideration of the audience in relation to programming. Prerequisite: Sophomore standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('830', '873481', 'MCOM', '350', '2006', 'AUDIO PRODUCTION', 'Introduction to audio production for radio, video, film and music recording, including principles, equipment, pre-production, production, and post-production. A $15 fee is charged each student to help defray expenses related to this course. Prerequisite: Sophomore standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('831', '526856', 'MCOM', '351', '2006', 'PHOTOCOMMUNICATION', 'Introduction to photo- communication for print media, using digital technology in news, public relations, and related mass communications applications. Prerequisite: Sophomore standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('832', '81652', 'MCOM', '352', '2006', 'VIDEO PRODUCTION', 'Introduction to in-studio video production, including camera techniques and effects, equipment utilization and lighting. A $15 fee is charged each student to help defray expenses related to this course. Prerequisite: Sophomore standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('833', '526970', 'MCOM', '353', '2006', 'FILM PRODUCTION', 'Introduction to cinematography, production techniques, equipment utilization and editing in 8mm and 16mm formats. A $15 fee is charged each student to help defray expenses related to this course. Prerequisite: Sophomore standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('834', '370805', 'MCOM', '354', '2006', 'ELECTRONIC MEDIA DESIGN', 'Graphics, typography, layout, design and related creative techniques for a variety of mass communications applications. Prerequisite: MCOM 330 or approval of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('835', '35520', 'MCOM', '360', '2006', 'PUBLIC RELATIONS PRINCIPLES', 'Overview of the evolution, structure, principles and practices of the public relations profession, emphasizing human behavior in social situations and the role of public opinion in contemporary society. Prerequisite: MCOM 330.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('836', '718035', 'MCOM', '361', '2006', 'PUBLIC RELATIONS WRITING AND PLANNING', 'Publicity planning, writing for all media, and developing techniques for working with mass media and other publics. Prerequisite: MCOM 360.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('837', '800320', 'MCOM', '370', '2006', 'MASS COMMUNICATION THEORY AND RESEARCH', 'Theories and research techniques for the study of mass communications processes and effects, emphasizing methods of audience identification, measurement and evaluation for print and electronic media. Prerequisites: MATH 111 or higher; junior standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('838', '339718', 'MCOM', '380', '2006', 'MASS COMMUNICATION ETHICS', 'Theories of ethics and the practice of ethical decision-making in mass communications professions and situations. Prerequisite: Junior standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('839', '512277', 'MCOM', '401', '2006', 'MASS COMMUNICATIONS SEMINAR (NON-REPEATABLE)', 'Scholarly approaches to contemporary topics in mass communications. Content varies. Prerequisite: Junior standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('840', '907357', 'MCOM', '411', '2006', 'OPINION WRITING', 'Analysis of news media editorial policy and study of the principles of interpretive comment on current affairs, with emphasis on editorial writing and presentation. Prerequisite: MCOM 210 or consent of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('841', '361661', 'MCOM', '412', '2006', 'SCRIPTWRITING', 'Preparation of scripts for all long-program formats, with emphasis on writing and marketing radio, television, and film drama and documentaries. Prerequisite: MCOM 210 or consent of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('842', '73184', 'MCOM', '432', '2006', 'ADVANCED REPORTING FOR PRINT MEDIA', 'Depth reporting and critical writing on police, court, political, financial, governmental and other social events and issues. Prerequisite: MCOM 331.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('843', '91333', 'MCOM', '433', '2006', 'ELECTRONIC NEWS GATHERING', 'Concepts and practices of news coverage for the electronic media, with special attention to field techniques, visualization and editing. A $15 fee is charged each student to help defray expenses related to this course. Prerequisite: MCOM 331.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('844', '8081', 'MCOM', '435', '2006', 'JOURNALISM MANAGEMENT', 'Methods and practices for the production, promotion, and management of print and broadcast news; issues and techniques of managing and leading media companies in rapidly changing business and cultural environments. Prerequisite: Junior standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('845', '523029', 'MCOM', '440', '2006', 'MEDIA SALES AND PROMOTIONS', 'In-depth study of selling for media, from prospecting to servicing accounts, with emphasis on adapting sales techniques to the special needs of print and broadcast media. Prerequisite: Junior standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('846', '382343', 'MCOM', '445', '2006', 'BROADCAST MANAGEMENT', 'Organization and administration of broadcast stations and other electronic media systems, with attention to operations, community relations, and financial management. Prerequisite: Junior standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('847', '248813', 'MCOM', '450', '2006', 'ADVANCED AUDIO PRODUCTION', 'Theory and advanced techniques of audio production for radio, video, film and music recording, from conception through completion, emphasizing multitrack, non-destructive, and non-linear production. A $15 fee is charged each student to help defray expenses related to this course. Prerequisite: MCOM 350.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('848', '119169', 'MCOM', '451', '2006', 'ADVANCED PHOTOCOMMUNICATION', 'Theory and tecniques of photocommunication for print media, emphasizing use of still photography for in-depth investigating and reporting. Prerequisite: MCOM 351.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('849', '832657', 'MCOM', '452', '2006', 'ADVANCED VIDEO PRODUCTION', 'Theory and techniques of video production from conception to completion, for a variety of mass communications applications. A $15 fee is charged each student to help defray expenses related to this course. Prerequisite: MCOM 352.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('850', '724189', 'MCOM', '453', '2006', 'ADVANCED FILM PRODUCTION', 'Theory and principles of filmmaking from conception to completion, for a variety of mass communications applications. A $15 fee is charged each student to help defray expenses related to this course. Prerequisite: MCOM 353.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('851', '354053', 'MCOM', '454', '2006', 'ADVANCED ELECTRONIC MEDIA DESIGN', 'Theory and principles of electronic media design from conception to completion, for a variety of mass communications applications emphasizing multimedia. Prerequisite: MCOM 354.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('852', '703568', 'MCOM', '460', '2006', 'PUBLIC RELATIONS CAMPAIGNS', 'Use of research to identify problems and development of strategies to meet public relations needs for actual clients. Prerequisites: MCOM 361 and 370.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('853', '674409', 'MCOM', '465', '2006', 'PUBLIC RELATIONS CASE STUDIES', 'Case studies in managing public relations problems in industry, labor, education, government, social welfare, and trade organizations. Prerequisite: Junior standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('854', '780893', 'MCOM', '471', '2006', 'VISUAL COMMUNICATION', 'Theories of perception, methods of analysis of visual media, and principles and strategies of visual communication, emphasizing critical thinking about the role and function of media in society.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('855', '347224', 'MCOM', '472', '2006', 'ELECTRONIC MEDIA ANALYSIS AND CRITICISM', 'Critical methodologies and analysis of electronic media content and programming, emphasizing aesthetic, historical, social, cultural, and industrial factors affecting audience response. Prerequisite: Junior standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('857', '347286', 'MCOM', '481', '2006', 'MASS COMMUNICATIONS LAW', 'Legal limitations and privileges affecting publishing, advertising, broadcasting, telecasting; consideration of legal philosophy bearing on mass communications media. Prerequisite: Junior standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('858', '556534', 'MCOM', '482', '2006', 'BROADCAST REGULATION', 'The study of electronic media law and the Federal Communication’s rules and procedures, including an examination of current trends of the Commission and their possible effects on the industry and society. Prerequisite: MCOM 481.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('859', '191838', 'MATH', '093', '2006', 'INTRODUCTORY ALGEBRA', 'Review of fundamentals; operations and properties of rational and real numbers; solutions and linear equations and applications; graphing linear equations and inequalities in two variables; solving systems of linear equations; exponents and polynomials; factoring polynomials; simplifying rational and radical expressions; solving fractional, radical, and quadratic equations. Three semester hours will be added to the degree program of any student required to take this course. Open only to students whose Math ACT is less than 18.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14690', '141094', 'MUSC', '330', '2006', 'University Chorale', 'Prerequisite: Approval of the director. May be repeated for credit. Total hours allowed vary with each degree program.', '1', '1', '10', '0', '', '0');
INSERT INTO `courses` VALUES ('861', '430016', 'MATH', '111', '2006', 'COLLEGE ALGEBRA', 'Real number system, functions, linear equations and inequalities, systems of equations and inequalities, quadratics, complex numbers, polynomial equations, arithmetic and geometric series, permutations and combinations. Prerequisite: Open only to students whose Math ACT is 21 or higher.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('862', '718694', 'MATH', '112', '2006', 'TRIGONOMETRY', 'Trigonometric functions, trigonometric identities and equations, logarithms, vectors, complex numbers. Prerequisite: A grade of &quot;C&quot; or better in MATH 110 or 111.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('863', '649084', 'MATH', '113', '2006', 'ELEMENTARY FUNCTIONS', 'A pre-calculus course emphasizing functions of algebraic, trigonometric, and transcendental nature. This course is designed for those students who intend to enroll in calculus. Prerequisite: Open only to students whose Math ACT is 21 or higher.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('864', '956985', 'MATH', '114', '2006', 'APPLIED CALCULUS FOR THE LIFE SCIENCES', 'A study of the concepts of differential and integral calculus with applications from the life sciences. Prerequisite: A grade of C or better in Math 110 or 111.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('865', '89499', 'MATH', '116', '2006', 'ELEMENTARY STATISTICS', 'Introduction to descriptive statistics, probability, binomial and normal distributions, hypothesis testing, correlation and regression. Not applicable to a major or minor in mathematics or computer science. Prerequisites: A grade of &quot;C&quot; or better in MATH 110, 111, 113 or 114.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('866', '684603', 'MATH', '118', '2006', 'THE NATURE OF MATHEMATICS', 'Selected topics from logic, set theory, number theory, graph theory, combinatorics, and geometry. Intended for liberal arts degree programs. Prerequisite: A grade of &quot;C&quot; or better in MATH 110, 111, 113, or 114.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('867', '675021', 'MATH', '131', '2006', 'CALCULUS', 'Functions, limits and continuity, derivatives, applications of the derivative, the integral, conic sections. Prerequisite: A grade of &quot;C&quot; or better in MATH 113 or permission of instructor.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('868', '889818', 'MATH', '132', '2006', 'CALCULUS', 'Inverse functions, techniques of integration, applications of the integral, sequences and series. Prerequisite: A grade of &quot;C&quot; or better in MATH 131.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('869', '24321', 'MATH', '201H', '2006', 'HONORS MATHEMATICS', 'A course in the aesthetics of mathematics, including such studies as logic, the history of mathematics, famous impossibilities and unsolved problems. Prerequisites: Five hours of Mathematics at the lll level or higher and admission to the University Honors Program or permission of the University Honors Council.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('870', '187298', 'MATH', '202', '2006', 'APPLIED LINEAR ALGEBRA', 'An introduction to the application of matrix algebra. Emphasis on the application of procedures and algorithms. Prerequisite: A grade of &quot;C&quot; or better in MATH 131 or 114. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('871', '797175', 'MATH', '209', '2006', 'DYNAMICAL SYSTEMS AND CHAOS', 'Covers fractals, the historical perspectives of Chaos, Koch curves, Cantor, Julia, and Mandelbrot sets, time series, population studies, and how Chaos involves other branches of science. Students should have access to either a graphing calculator of portable computer. Prerequisite: A grade of &quot;C&quot; or better in MATH 131 or MATH 114 or approval of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('872', '385982', 'MATH', '232', '2006', 'CALCULUS', 'Vectors, lines, and planes, vector valued functions, partial derivatives, multiple integrals, calculus of vector fields. Prerequisite: A grade of &quot;C&quot; or better in MATH 132. Sp', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('873', '260482', 'MATH', '240', '2006', 'FOUNDATIONS OF MATHEMATICS', 'Introduction to logic and proofs, set theory, functions and structures relating to algebra and analysis. Prerequisite: A grade of &quot;C&quot; or better in MATH 131. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('874', '888508', 'MATH', '250', '2006', 'NUMERICAL FOUNDATIONS FOR TEACHERS', 'A survey of elementary number theory, operations, and algorithms. For Elementary Education majors only. Prerequisite: A grade of &quot;C&quot; or better in MATH 110 or MATH 111.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('875', '394062', 'MATH', '251', '2006', 'PROBABILITY AND DATE ANALYSIS FOR ELEMENTARY TEACH', 'A survey of descriptive statistics and probability. For Elementary Education majors only. Prerequisite: A grade of &quot;C&quot; or better in MATH 250.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('876', '783510', 'MATH', '252', '2006', 'MATHEMATICAL FOUNDATIONS FOR ELEMENTARY TEACHERS', 'A survey of patterns, functions, and algebraic underpinnings. For Elementary Education majors only. Prerequisite: A grade of &quot;C&quot; or better in MATH 251.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('877', '270851', 'MATH', '301', '2006', 'DIFFERENTIAL EQUATIONS', 'An introduction to ordinary differential equations, emphasizing equations of first order and first degree, linear differential equations of higher order, and applications of equations which arise in geometry, chemistry, and physics. Prerequisite: A grade of &quot;C&quot; or better in MATH 132. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('878', '642875', 'MATH', '303', '2006', 'ELEMENTARY MATHEMATICAL STATISTICS', 'Random variables, probability, distribution functions, mathematical models, testing hypotheses, frequency distributions, correlations, regression, goodness of fit, derived distributions, non-parametric methods. Prerequisite: MATH 131.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('879', '902678', 'MATH', '307', '2006', 'COLLEGE GEOMETRY', 'Selected topics from finite, projective, or non-Euclidean geometries. Advanced topics in Euclidean geometry, including constructions. Prerequisite: A grade of &quot;C&quot; or better in MATH 240. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('880', '103509', 'MATH', '350', '2006', 'GEOMETRY AND MEASUREMENT FOR ELEMENTARY TEACHERS', 'A survey of Euclidian geometry and measurement. For Elementary Education majors only. Prerequisite: A grade of &quot;C&quot; or better in MATH 252.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14122', '367065', 'MATH', '373', '2006', 'Numerical Methods', 'Numerical algorithms fundamental to scientific work including discussion or error. Prerequisite: CSCI 203, a grade of &quot;C&quot; or better in MATH 202, and MATH 132. (Dual listed as CSCI 373).', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('882', '256732', 'MATH', '380', '2006', 'REAL ANALYSIS', 'Sequences and series of real numbers and their convergence; continuity, differentiation, and integration of real valued functions of one real variable. Prerequisites: A grade of &quot;C&quot; or better in MATH 232, MATH 240.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('883', '807076', 'MATH', '386', '2006', 'MODERN ALGEBRA', 'Axioms and the nature of proof, groups, rings, integral domains, fields, homomorphisms and isomorphisms. Prerequisite: A grade of &quot;C&quot; or better in MATH 202 and MATH 240.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('884', '41474', 'MATH', '407', '2006', 'HISTORY OF MATHEMATICS', 'Selected topics on the development of mathematics as a human endeavor; numeration systems; growth of algebra, trigonometry, geometry, and the calculus; contributions from various cultures; selected biographies of mathematicians. Prerequisites: A grade of &quot;C&quot; or better in MATH 240 and either MATH 380 or MATH 386.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('885', '37625', 'MATH', '409', '2006', 'CRYPTOLOGY', 'Introduction to modern applications of mathematics. Private key ciphers, including shift, affine and vigenere; attacks on these ciphers. Modern public key ciphers including RSA and Knapsack. Prerequisite: A grade of &quot;C&quot; or better in Math 240 or permission of the instructor.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('886', '154301', 'MATH', '417', '2006', 'REAL ANALYSIS', 'Sequences and series of vector-valued functions of several real variables and their convergence; continuity, differentiation, and integration of vector- valued functions of several real variables. Prerequisite: A grade of &quot;C&quot; or better in MATH 380.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14134', '111545', 'MATH', '421', '2006', 'Introduction To Biostatistics II', 'A continuation of 420. Topics include linear and multiple regression, logistics regression, longitudinal data analysis, nonparametric methods and principle component techniques. Prerequisite: MATH 420 or CSCI 420 and junior standing or above. (Same as Computer Science 421).', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('888', '384911', 'MATH', '425', '2006', 'MODERN ALGEBRA', 'Continuation of Math 386; Euclidean domains, unique factorization domains, principal ideal domains, Galois theory and related topics. Prerequisite: A grade of &quot;C&quot; or better in MATH 386.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('889', '710835', 'MATH', '431', '2006', 'FUNDAMENTALS OF HIGH SCHOOL MATHEMATICS I', 'Emphasis is on teaching at the high school level. Topics include, but are not restricted to, number concepts and relationships, geometry and measurement. Not applicable toward a major or minor in mathematics. Applies to middle and high school certification.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('890', '303383', 'MATH', '401', '2006', 'DIFFERENTIAL EQUATIONS', 'Series solutions to ordinary differential equations, Laplace transforms, systems of differential equations, numerical methods and applications which arise in geometry, chemistry, and physics. Prerequisite: MATH 301.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('891', '952342', 'MATH', '402', '2006', 'LINEAR SPACES', 'A study of abstract vector spaces including function spaces, linear transformations, operators, functionals and duality, bilinear and quadratic forms. Prerequisite: MATH 202, 132.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('892', '140851', 'MATH', '405', '2006', 'PARTIAL DIFFERENTIAL EQUATIONS', 'Fourier series, boundary value problems, partial differential equations, special functions, with an emphasis on applications. Prerequisite: MATH 301.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('893', '22077', 'MATH', '406', '2006', 'THEORY OF FUNCTIONS OF A COMPLEX VARIABLE', 'Real and complex numbers, differentiation and integration of functions of a complex variable, mapping by elementary functions, transformations, infinite series, and theory of residues and poles. Prerequisite: MATH 232.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('894', '601426', 'MATH', '411', '2006', 'FUNDAMENTALS OF MIDDLE SCHOOL MATHEMATICS I', 'For the middle-school teacher; number concepts and relationships; geometry and measurement; algebra and algebraic structures. Emphasis is on teaching mathematics at the middle school level. Not applicable toward a major nor minor in mathematics at undergraduate or graduate level. Applies to elementary and middle school certification.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('895', '97837', 'MATH', '412', '2006', 'FUNDAMENTALS OF MIDDLE SCHOOL MATHEMATICS II', 'Probability and statistics for the middle-school teacher. Topics include, but are not restricted to, data analysis and basic concepts of probability and statistics. Not applicable toward a major nor minor in mathematics at undergraduate or graduate level. Applies to elementary and middle school certification.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('896', '111575', 'MATH', '419', '2006', 'TOPOLOGY', 'Point sets, axiomatic bases, topology of the line and the plane. Emphasis on connectedness, compact sets and continuous transformations. Prerequisite: MATH 202 and 232.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14116', '286030', 'MATH', '436', '2006', 'Introduction To Mathematical Concepts In Operation', 'A mathematical approach to concepts in advanced linear and nonlinear programming, dynamic programming, game theories, queueing theory and other standard topics in operations research. Prerequisites: MATH 303 and CSCI 435. (Same as CSCI 436.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('898', '772857', 'MILS', '110', '2006', 'INTRODUCTION TO LEADERSHIP', 'Introduction to the Army’s mission, role, organization, customs and traditions, and military skills.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('899', '1394', 'MILS', '111', '2006', 'BASIC LEADERSHIP LAB I', 'Practical laboratory of applied leadership and tactical skills taken with MILS 110. Activities include basic drill, land navigation, physical fitness, communications, basic rifle marksmanship, and rappelling.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('900', '310351', 'MILS', '112', '2006', 'LEARNING TO LEAD', 'Introduction to the Army fundamentals of leadership and first aid.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('901', '960155', 'MILS', '113', '2006', 'BASIC LEADERSHIP LAB II', 'Practical laboratory of applied leadership and tactical skills taken with MILS 112. Activities include rifle marksmanship, first aid, rope crafts, physical conditioning, individual tactical skills, and team development skills.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('902', '798569', 'MILS', '210', '2006', 'APPLIED LEADERSHIP', 'The study of leadership principles and fundamentals, team development, and tactical skills. Registration in MILS 211 required.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('903', '696333', 'MILS', '211', '2006', 'BASIC LEADERSHIP LAB III', 'Practical laboratory of leadership skills to develop individual skills and develop individuals as part of an effective team. Activities include advanced drill, military tactics, communications, land navigation, and basic rifle marksmanship.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('904', '220637', 'MILS', '212', '2006', 'LEADERSHIP DEVELOPMENT', 'The study of land navigation, individual tactical skills, and communications. Registration in MILS 211 required.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('905', '687078', 'MILS', '213', '2006', 'BASIC LEADERSHIP LAB IV', 'A practical laboratory of applied leadership and military skills to accompany MILS 212. Activities include rifle marksmanship, first aid, rope crafts, physical conditioning, individual tactical skills, and team development.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('906', '90396', 'MILS', '214', '2006', 'CAMP CHALLENGE', 'A six-week summer camp conducted at an Army post. Provides the student with core training allowing placement into the 300/400 level courses. Non-obligatory. Open to students who have not taken all four of MILS 110, 112, 210, and 212 classes, and who pass a physical examination paid for by ROTC. Prerequisite: Approval of the Professor of Military Science and completion of a minimum of 57 credit hours. To receive credit, the student must enroll in a summer term prior to departure for camp.', '6', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('907', '4148', 'MILS', '310', '2006', 'LEADERSHIP TACTICS', 'Military leadership principles, land navigation, small unit tactics, and physical training. Registration in MILS 311 required.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('908', '957929', 'MILS', '311', '2006', 'ADVANCED LEADERSHIP LAB I', 'Laboratory course to accompany MILS 310. A practical laboratory of applied leadership that is planned and executed by the MS IV cadets. Activities include drill and ceremonies, land navigation, rappelling, rifle marksmanship, tactics, first aid, water survival, physical conditioning, and communications.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('909', '733270', 'MILS', '312', '2006', 'LEADERSHIP AND MANAGEMENT', 'Military principles, small unit tactics, tactical communications and physical training. Includes participation in a three-day field training exercise. Registration in MILS 313 required.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('910', '906825', 'MILS', '313', '2006', 'ADVANCED LEADERSHIP LAB II', 'Laboratory course to accompany MILS 312. A practical laboratory of applied leadership that is planned and executed by the MS IV cadets. Activities include drill and ceremonies, land navigation, rifle marksmanship, tactics, physical conditioning, and communications.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('911', '61438', 'MILS', '314', '2006', 'ROTC ADVANCED CAMP', 'Five weeks of intensified instruction and practical exercise in tactics, leadership, and weapons conducted at an Army post under the control of ROTC INSERT INTO `courses` VALUES ('912', '100335', 'MILS', '410', '2006', 'ADVANCED LEADERSHIP AND MANAGEMENT', 'Study of command and staff activities, customs and courtesies, ethics, platoon operations, oral presentation techniques, and counseling.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('913', '163558', 'MILS', '411', '2006', 'ADVANCED LEADERSHIP LAB III', 'Laboratory course to accompany MILS 410. A practical laboratory of applied leadership that is planned and executed by the MS IV cadets. Activities include drill and ceremonies, land navigation, rappelling, rifle marksmanship, tactics, first aid, water survival, physical conditioning, and communications.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('914', '868514', 'MILS', '412', '2006', 'OFFICERSHIP', 'Study of administrative skills, military law, responsibilities of an officer, and applied management techniques.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('915', '141809', 'MILS', '413', '2006', 'ADVANCED LEADERSHIP LAB IV', 'Laboratory course to accompany MILS 412. A practical laboratory of applied leadership that is planned and executed by the MS IV cadets. Activities include drill and ceremonies, land navigation, rifle marksmanship, tactics, physical conditioning, and communications.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('916', '201182', 'MUSC', '101', '2006', 'FUNDAMENTALS OF MUSIC THEORY', 'A non-technical course designed to provide the layman with an understanding and utilization of the basic elements of music including sound, melody, harmony, rhythm, and form. (Formerly 170)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('917', '22816', 'MUSC', '102', '2006', 'MUSIC THEORY I', 'Course covers basic fundamentals of music theory including intervals, chordal structures and simple harmonic function. Prerequisite: MUSC 101 or equivalent. (Formerly 171)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('918', '253353', 'MUSC', '103', '2006', 'AURAL SKILLS I', 'Course covers fundamental elements of melodic, harmonic, and rhythmic dictation and sight-singing. Prerequisite: MUSC 101 or equivalent. (Formerly 171)', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('919', '586092', 'MUSC', '104', '2006', 'MUSIC THEORY II', 'Course covers the use and function of cadences, phrases, non-chord tones and seventh chords. Prerequisite: MUSC 102 or equivalent. (Formerly 172)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('920', '733650', 'MUSC', '105', '2006', 'AURAL SKILLS II', 'Course covers intermediate melodic dictation, rhythmic dictation and sight-singing. Fundamentals of harmonic dictation are introduced. Prerequisite: MUSC 103 or equivalent. (Formerly 172)', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('921', '556735', 'MUSC', '117', '2006', 'BEGINNING AND INTERMEDIATE RECORDER CLASS', '', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('922', '538435', 'MUSC', '191', '2006', 'ENJOYMENT', 'A non-technical course designed to provide the layman with the understanding necessary for the enjoyment of music of various types, including music of different races and ethnic groups.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('923', '874501', 'MUSC', '192', '2006', 'ENJOYMENT OF JAZZ', 'Logical musical derivations and developments of American Jazz, both of itself as an art form and its influence on other areas of music. Ethnic influences such as Afro-American Jazz, New Orleans ragtime, and Memphis influences will be explored.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('924', '578811', 'MUSC', '193', '2006', 'LITERATURE AND APPRECIATION', 'For music majors; open to others by approval of the Head of the Division of Music.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('925', '139861', 'MUSC', '194', '2006', 'INTRODUCTION TO ELECTRONIC MUSIC', 'Overview of the history and techniques of electro-acoustic musc with hands-on laboratory work in the electro-acoustic lab.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('926', '972337', 'MUSC', '202', '2006', 'MUSIC THEORY III', 'Course covers theoretical aspects of secondary chords, modulations and chromatically altered chords in common practice tonal music. Prerequisite: MUSC 104 or equivalent. (Formerly 271)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('927', '690386', 'MUSC', '203', '2006', 'AURAL SKILLS III', 'Course covers intermediate to advanced melodic and rhythmic dictation and sight-singing and intermediate harmonic dictation. Prerequisite: MUSC 105 or equivalent. (Formerly 271)', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('928', '425891', 'MUSC', '204', '2006', 'MUSIC THEORY IV', 'Course covers theoretical aspects of late 19th and early 20th century tonal music. Overview of 20th century post-tonal techniques provided. Prerequisite: MUSC 202 or equivalent. (Formerly 272)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('929', '745195', 'MUSC', '205', '2006', 'AURAL SKILLS IV', 'Course covers advanced melodic, rhythmic and harmonic dictation and sight-singing. Prerequisite: MUSC 203 or equivalent. (Formerly 272)', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('930', '691780', 'MUSC', '217', '2006', 'ADVANCED RECORDER CLASS', 'Prerequisite: 117 or approval of instructor.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('931', '736242', 'MUSC', '220', '2006', 'ADVANCED GUITAR CLASS', '', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('932', '705350', 'MUSC', '263', '2006', 'PIANO TECHNOLOGY', 'An introductory course for students interested in various aspects of piano technology; tuning, regulating, and repairing. Beneficial for those who are preparing for piano teaching, piano selling, or piano servicing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('933', '490349', 'MUSC', '273', '2006', 'ELEMENTARY CONDUCTING', 'Fundamentals of conducting vocal and instrumental music organizations.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('934', '432575', 'MUSC', '291', '2006', 'ENGLISH DICTION FOR SINGERS', 'A study of specific needs related to singing in English, through a study of the International Phonetic Alphabet.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('935', '925987', 'MUSC', '292', '2006', 'ITALIAN DICTION FOR SINGERS', 'A study of Italian pronunciation using the International Phonetic Alphabet.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('936', '177428', 'MUSC', '293', '2006', 'FRENCH DICTION FOR SINGERS', 'A study of French pronunciation using the International Phonetic Alphabet.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('937', '522970', 'MUSC', '294', '2006', 'GERMAN DICTION FOR SINGERS', 'A study of German pronunciation using the International Phonetic Alphabet.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('938', '930134', 'MUSC', '337', '2006', 'PIANO PEDAGOGY I', 'A course designed to introduce the basic principles and techniques of private piano teaching; survey and evaluation of elementary teaching materials and teaching philosophies. (Formerly 260.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('939', '135357', 'MUSC', '338', '2006', 'PIANO PEDAGOGY II', 'A course designed to introduce procedures and materials for group piano instruction with an emphasis on the intermediate level. (Formerly 261.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('940', '256241', 'MUSC', '390', '2006', 'HALF RECITAL', 'The preparation of a half-recital for public performance including music appropriate for the junior level of applied study or composition.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('941', '836959', 'MUSC', '471', '2006', 'COMPOSITION', 'Composition based on twentieth century techniques and along original lines. Media used are vocal and instrumental solo and ensemble, including at least one composition for the student’s major instrument. Prerequisite: Approval of the Head of the Division of Music.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('942', '196795', 'MUSC', '487', '2006', 'VOICE PEDAGOGY', 'A study of the teaching methods, techniques, and materials for the voice studio. Prerequisite: Music 341 or senior standing. Required of all vocal performance majors.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('943', '356576', 'MUSC', '490', '2006', 'FULL RECITAL', 'The preparation of a full recital for public performance including music appropriate to the senior level of applied study or composition.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('944', '518', 'MUSC', '401', '2006', 'OPERA PERFORMANCE AND PRODUCTION', 'Musical and dramatic preparation of opera roles, and basic techniques of staging and presentation. Participation in opera performances. May be repeated for credit.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('945', '65310', 'MUSC', '437', '2006', 'PIANO PEDAGOGY III', 'A survey of music for the piano from the Baroque and Classical periods suitable for students at the intermediate level. (Formerly 360.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('946', '498384', 'MUSC', '438', '2006', 'PIANO PEDAGOGY IV', 'A survey of music for the piano from the Romantic and Contemporary periods suitable for students at the intermediate level. (Formerly 361.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('947', '201699', 'MUSC', '464', '2006', 'THE BAROQUE ERA', 'Music of the Baroque period up to and including Bach. Prerequisite: 492 or approval of the Head of the Division of Music.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('948', '88126', 'MUSC', '465', '2006', 'PIANO HISTORY AND LITERATURE', 'A survey of pianos and their predecessors, and piano literature from the early English school to the present. Prerequisite: MUSC 193.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('949', '751736', 'MUSC', '467', '2006', 'SURVEY OF THE MUSIC INDUSTRY', 'An overview of the music industry, including careers, publishing, recording, and merchandising.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('950', '787791', 'MUSC', '468', '2006', 'SURVEY OF ENSEMBLE MUSIC FOR THE PIANO', 'Survey of piano concert and ensemble music written for one or more pianos. Music for elementary and intermediate level students will be emphasized.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('951', '821775', 'MUSC', '469', '2006', 'BAND REPERTOIRE', 'Introduction to band composers and their literature. A chronological approach to the available band literature and its composers.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('952', '308472', 'MUSC', '470', '2006', 'BAND HISTORY AND LITERATURE', 'History of bands, original compositions and transcriptions for band. Prerequisite: MUSC 193. (Formerly 395.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('953', '326226', 'MUSC', '475', '2006', 'ELECTRONIC MUSIC COMPOSITION', 'A &quot;hands-on&quot; course utilizing the electronic music lab and oriented toward skills in composing art music. Prerequisite: Music 194, or approval of the Head of the Division of Music.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('954', '696277', 'MUSC', '476', '2006', 'CHORAL CONDUCTING', 'Prerequisite: MUSC 273.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('955', '887283', 'MUSC', '477', '2006', 'ORCHESTRATION', 'Technical and tonal characteristics of orchestral instruments. Arranging for string, woodwind and brass choirs and for full orchestra. Prerequisite: MUSC 372.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('956', '459115', 'MUSC', '478', '2006', 'EIGHTEENTH CENTURY COUNTERPOINT', 'The Study of contrapuntal compositional techniques concentrating on 18th Century Counterpoint. Prerequisite: MUSC 204 or equivalent.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('957', '668614', 'MUSC', '479', '2006', 'INSTRUMENTAL CONDUCTING', 'Prerequisite: 273.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('958', '577669', 'MUSC', '480', '2006', 'TWENTIETH CENTURY COUNTERPOINT', 'Twentieth century contrapuntal practice culminating with the composition of one or more major polyphonic work.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('959', '891978', 'MUSC', '481', '2006', 'ANALYSIS OF CONTEMPORARY MUSIC', 'An overview of techniques used in the analysis of the music of the 20th century and beyond.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('960', '413809', 'MUSC', '482', '2006', 'ORGAN HISTORY AND LITERATURE', 'Organs and organ literature from the Gothic era to the present. Prerequisite: MUSC 193.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('961', '269450', 'MUSC', '483', '2006', 'WOODWIND LITERATURE', 'Solo and chamber music written for woodwind instruments. Prerequisite: MUSC 193.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('962', '628220', 'MUSC', '484', '2006', 'BRASS LITERATURE', 'Solo and chamber music written for brass instruments. Prerequisite: MUSC 193.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('963', '119160', 'MUSC', '485', '2006', 'AMERICAN', '(Formerly 591.) American music from early Colonial times to the present, including music of different races INSERT INTO `courses` VALUES ('964', '759799', 'MUSC', '486', '2006', 'SURVEY OF MUSIC THEATRE', 'A survey of the history and literature of the music theatre, 1800 to the present, with emphasis on 20th century works and composers.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('965', '60795', 'MUSC', '493', '2006', 'THE ROMANTIC ERA', 'Music of the Romantic era and its place in the general culture of the period. Prerequisite: 492 or approval of the Head of the Division of Music.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('966', '45147', 'MUSC', '494', '2006', 'CONTEMPORARY', 'Music from the period of Impressionism to the present. Prerequisite: MUSC 492 or approval of the Head of the Division of Music.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('967', '937226', 'MUSC', '495', '2006', 'ORCHESTRAL LITERATURE', 'Orchestral music from the Baroque period to the present. Prerequisite: MUSC 193.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('968', '583765', 'MUSC', '496', '2006', 'SONG LITERATURE', 'Music for solo voice from the Baroque period to the present with particular attention to the Romantic period. Prerequisite: MUSC 193.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('969', '975280', 'MUSC', '497', '2006', 'KEYBOARD LITERATURE', 'Music for keyboard instruments from the English virginal school to the present.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('970', '72584', 'MUSC', '498', '2006', 'CHORAL LITERATURE', 'Choral music from the Renaissance to the present. Prerequisite: MUSC 193.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('971', '840005', 'MUSC', '499', '2006', 'THE CLASSIC ERA', 'Music of the Classic era and its place in the general culture of the period. Prerequisite: MUSC 492 or approval of the Head of the Division of Music. MUSIC EDUCATION', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('972', '812240', 'MSED', '454', '2006', 'MULTICULTURAL MUSIC EDUCATION', 'Overview and application of characteristics of music from other cultures. Course explores the role of music in the social life of all cultures.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('973', '269379', 'MSED', '403', '2006', 'INSTRUMENT REPAIR', 'Practical methods of maintenance, adjustment, and repairs of musical instruments.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('974', '196581', 'MSED', '411', '2006', 'Music Education Workshop', 'A course designed to instruct teachers and supervisors of elementary music in singing, playing instruments, moving, creating, arranging, and selecting music for A. Basic; B. Intermediate; C. Advanced.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('975', '812757', 'MSED', '417', '2006', 'GENERAL MUSIC PROGRAM IN ELEMENTARY AND SECONDARY', 'Detailed consideration of the general music program, with special attention given to materials and methods of teaching.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('976', '334689', 'MSED', '419', '2006', 'THE MARCHING BAND', 'Fundamentals organization, precision drill and pageantry; charting and rehearsal procedures and musical problems.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('977', '694964', 'MSED', '420', '2006', 'DRILL DESIGN TECHNIQUES', 'A course designed to provide advanced drill design techniques including music analysis, interpretive writing, horn placement, drill teaching techniques, and element correlation. Prerequisite: MSED 419 or equivalent.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('978', '14457', 'MSED', '490', '2006', 'CHORAL REPERTOIRE', 'A survey of the choral octavo literature at the high school through collegiate level; in conjunction with selection of materials and programming.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('979', '422814', 'MSED', '491', '2006', 'INTRODUCTORY WORKSHOP IN ORFF-SCHULWERK', 'Basic Orff-Schulwerk techniques, including body movement, soprano recorder, percussion, vocal performance, improvisation, and arranging.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('980', '446701', 'MSED', '492', '2006', 'INTERMEDIATE WORKSHOP IN ORFF-SCHULWERK', 'Intermediate level Orff-Schulwerk techniques, including modal harmonization, irregular rythms, alto recorder performances, and more extensive improvisation and arranging. Prerequisite: MSED 491 or equivalent.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('981', '802247', 'MSED', '493', '2006', 'ADVANCED WORKSHOP IN ORFF-SCHULWERK', 'Advanced Orff-Schulwerk techniques including original composition, complex form, movement, and instrumental arrangements, tenor and bass recorder performances, and advanced improvisation. Prerequisite: MSED 492 or equivalent.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('982', '244590', 'MSED', '495', '2006', 'THE USE OF MUSICAL INSTRUMENTS IN GENERAL MUSIC ED', 'A course designed to provide basic playing ability of the following instruments: autoharp, melody bells, recorder, guitar, piano, dulcimer, and the various Orff melodic and percussion instruments. Prerequisite: MSED 322 or 335 or equivalent.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('983', '755172', 'NURS', '202', '2006', 'TRANSITIONS IN NURSING', 'An introduction to professional nursing and concepts related to role socialization and transition. Includes clinical/laboratory experience with emphasis on the nursing process, major nursing theories, and trends and issues in nursing. Prerequisites: approval of the coordinator; for Licensed Practical Nurses only.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('984', '128474', 'NURS', '204', '2006', 'HEALTH ASSESSMENT', 'Basic techniques essential to the assessment of health status in the individual across the life span. Prerequisite: Registration or credit in NURS 205, 207, 216, 280, or current Registered Nurse Licensure. (Formerly 209.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('985', '940866', 'NURS', '205', '2006', 'HEALTH INTERVENTION I', 'Theories and concepts of health intervention and their relationship to the nursing process. Emphasis is on health promotion and health maintenance in the adult with common variations across the life span. Prerequisite: NURS 200, Registration or credit in NURS 207 and 204 (Formerly 209), 216, and 280.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('986', '642455', 'NURS', '207', '2006', 'NURSING SCIENCE PRACTICUM I', 'Opportunity is given to practice basic nursing interventions in a variety of settings. Focus of care is the individual. Prerequisite: NURS 200, Registration or credit in NURS 204 (Formerly 209), 205, 216, and 280.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('987', '594561', 'NURS', '216', '2006', 'COMPUTING FOR NURSES', 'Fundamentals of computer network usage, drug dosage calculations, nursing simulations, and hospital management programs. One hour lecture and two hours laboratory.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('988', '609480', 'NURS', '217', '2006', 'CONCEPTS OF NURSING AND HEALTH CARE', 'Introduction to professional nursing, nursing history, theories, therapeutic communication techniques, and health promotion throughout the life span. Prerequisite: NURS 216.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('989', '220125', 'NURS', '220', '2006', 'PROFESSIONAL NURSING CONCEPTS', 'A transitional course to advance the registered nurse’s career in the professional role. Prerequisites: NURS 216; current RN licensure.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('990', '486539', 'NURS', '221', '2006', 'DIMEMSIONS OF PROFESSIONAL NURSING I', 'Application of nursing process in meeting basic needs of individuals across the life span including clinical experience in a variety of settings. Prerequisites: NURS 216, 217.', '6', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('991', '23290', 'NURS', '222', '2006', 'NURSING PHARMACOTHERAPEUTICS I', 'Medication dosage and calculation and drug administration. Prerequisite: NURS 216, 217.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14618', '878660', 'NURS', '280', '2006', 'Basic Principles Of Pharmacology', 'An introduction to basic pharmacological principles, therapeutics, classifications, effects, and mechanisms of drug action. Emphasis is placed on the application of knowledge of drugs used in patient care settings by health care professionals. Prerequisites: Biology 114, 115, 116, and 117 or 120. (Formerly PHAR 280)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('993', '114759', 'NURS', '300', '2006', 'NEONATAL HEALTH PROMOTION', 'Extensive study of the newborn’s adjustment to extrauterine life, including clinical experiences in Level I and II Nurseries. Prerequisites: Completion of NURS 204, 205, 207, 216, or approval of the Director, School of Nursing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('994', '142449', 'NURS', '304', '2006', 'HEALTH INTERVENTION II', 'Nursing interventions for commonly occurring problems in adults. Emphasis is on health maintenance and health restoration as it applies to adults. Prerequisites: NURS 204, 205, 207, 216, 280; registration or credit in NURS 305 and 306.', '5', '5', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('995', '249373', 'NURS', '305', '2006', 'NURSING SCIENCE PRACTICUM II', 'Clinical practice utilizing health maintenance and health restoration to individuals who have unmet needs. Clinical experiences will be provided in a variety of agency and community settings. Prerequisites: NURS 204, 205, 207, 216, 280; registration or credit in NURS 304 or 306.', '6', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('996', '175554', 'NURS', '306', '2006', 'NURSING SCIENCE SEMINAR', 'Seminar course that allows students to compare and contrast nursing roles in the delivery of health care to adults. Prerequisites: NURS 204, 205, 207, 216, 280, registration or credit in NURS 304 and 305.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('997', '187595', 'NURS', '307', '2006', 'NURSING PRACTICUM II A', 'Clinical practice with a focus on health maintenance and restoration for adults. Application of the nursing process in providing interventions to individuals. Prerequisites: NURS 202, 204, 216, 280, credit or registration in NURS 308, current Practical Nurse Licensure in Louisiana, one year of acute or long-term care experience, and approval of the coordinator.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('998', '186600', 'NURS', '308', '2006', 'NURSING PRACTICUM II B', 'Concepts and applications relative to intermediate care of the adult with a focus on health maintenance and restoration. Prerequisites: NURS 202, 204, 216, 280, credit or registration in NURS 307, current Practical Nurse Licensure in Louisiana, one year of acute or long-term care experience, and approval of the coordinator.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('999', '759319', 'NURS', '321', '2006', 'DIMENSONS OF PROFESSIONAL NURSING II', 'Application of the nursing process to chronically ill adults with commonly occurring health problems in a variety of settings. Prerequisites: NURS 204, 216, 217, 221, 222.', '7', '7', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1000', '162876', 'NURS', '323', '2006', 'CARE OF CHILDREN', 'Application of nursing process in care of healthy and ill children and their families. Prerequisites: NURS 204, 216, 217, 221, 204, 222.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1001', '259183', 'NURS', '325', '2006', 'CHILDBEARING FAMILY', 'Application of the nursing process in the care of the childbearing family. Prerequisites: NURS 204, 216, 217, 221, 222.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1002', '599324', 'NURS', '326', '2006', 'NURSING PHARMACOTHERAPEUTICS II', 'Application of pharmacological concepts to the nursing care of adults, children and childbearing families. Prerequisites: NURS 204, 216, 217, 221, and 222.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1003', '975116', 'NURS', '327', '2006', 'PATHOPHYSIOLOGY FOR NURSING I', 'Pathophysiological foundations of nursing care of adults, children, and child bearing families with chronic illness. Prerequisites: NURS 204, 216, 217, 221, 222.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1004', '528562', 'NURS', '332', '2006', 'CHILDBEARING FAMILY HEALTH', 'Application of theoretical and clinical components relative to the health and illness of childbearing families. A progressive score in both the classroom and clinical component is needed for progression. A failure in either component will require that both components (theory and clinical) be repeated. Prerequisites: NURS 304, 305, 306 or 307 and 308.', '5', '5', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1005', '795905', 'NURS', '334', '2006', 'NURSING OF CHILDREN', 'Application of theoretical and clinical components in providing care for the child and family in health and illness in individuals and groups. A progressive score in both the classroom and clinical component is needed for progression. A failure in either component will require that both components (theory and clinical) be repeated. Prerequisites: NURS 304, 305 and 306, or 307 and 308.', '5', '5', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1006', '787874', 'NURS', '403', '2006', 'ADVANCED NURSING SCIENCE SEMINAR', 'Seminar course in which students collaborate in groups to plan, teach, and evaluate their peers on topics that complement Nursing 404 and 405. Prerequisites: NURS 332, 334, Registration or credit in NURS 404 and 405.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1007', '863250', 'NURS', '404', '2006', 'MENTAL HEALTH NURSING', 'Application of nursing process in classroom and clinical settings related to mental health and illness in individuals and groups. A progressive score in both the classroom and clinical component is needed for progression. A failure in either component will require that both components (theory and clinical) be repeated. Prerequisites: NURS 332, 334.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1008', '490869', 'NURS', '405', '2006', 'ADVANCED ADULT HEALTH NURSING', 'Application of nursing process in classroom and clinical settings related to advanced adult health nursing in individuals and families. A progressive score in both the classroom and clinical component is needed for progression. A failure in either component will require that both components (theory and clinical) be repeated. Prerequisites: NURS 332, 334.', '7', '7', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1009', '802330', 'NURS', '407', '2006', 'DIMENSIONS OF PROFESSIONAL NURSING III', 'Application of the nursing process to acutely ill adults in acute care settings. Prerequisites: NURS 204, 216, 217, 221, 222, 321, 323, 325, 326, 327.', '7', '7', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1010', '286064', 'NURS', '409', '2006', 'NURSING PHARMACOTHERAPEUTICS III', 'Application of pharmacotherapeutic concepts to the nursing care of acutely ill individuals in acute care settings. Prerequisites: NURS 204, 216, 217, 221, 222, 321, 323, 325, 326, 327.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1011', '937570', 'NURS', '410', '2006', 'PATHOPHYSIOLOGY FOR NURSING II', 'Pathophysiological foundations of nursing care of adults with acute illness. Prerequisites: NURS 204, 216, 217, 221, 222, 321, 323, 325, 326, 327.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1012', '604578', 'NURS', '411', '2006', 'COMMUNITY NURSING I', 'Community health nursing foundations and theory. Public health principles. Overview of community health nursing roles. Prerequisites: NURS 204, 216, 217, 221, 222, 321, 323, 325, 326, 327, 407, 408, 409, 410, 430.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1013', '530654', 'NURS', '412', '2006', 'COMMUNITY NURSING II', 'Accelerated Summer course. Family and aggregate nursing. Communities in crisis. International health issues. Applications in community assessment, planning, implementation and evaluation. Prerequisites: NURS 204, 216, 217, 221, 222, 321, 323, 325, 326, 327, 407, 408, 409, 410, 411, 430.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1014', '692743', 'NURS', '414', '2006', 'NURSING LEADERSHIP', 'Synthesis and application of nursing leadership and management theory to the care of groups in a variety of health care settings. Prerequisites: NURS 204, 216, 217, 221, 222, 321, 323, 325, 326, 327, 407, 408, 409, 410, 411, 430.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1015', '733052', 'NURS', '415', '2006', 'HEALTH ECONOMICS FOR NURSES', 'Contemporary issues of health care economics and their application to the nursing care of individual, families, and groups across the life span. Prerequisites: NURS 204, 216, 217, 221, 222, 321, 323, 325, 326, 327, 407, 408, 409, 410, 411.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1016', '471520', 'NURS', '422', '2006', 'CHILD HEALTH INTERVENTIONS', 'Concepts and applications relative to the health and illness needs of child rearing families. Credit by examination only. Prerequisites: Credit in NURS 200, 204, 216 and 280; Current Registered Nurse Licensure.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1017', '335199', 'NURS', '423', '2006', 'MATERNAL HEALTH INTERVENTIONS', 'Concepts and applications related to health interventions for individuals, families, and groups with a focus on maternal health needs. Credit by examination only. Prerequisites: Credit in NURS 200, 204, 216, and 280 ; Current Registered Nurse Licensure.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1018', '327613', 'NURS', '430', '2006', 'NURSING RESEARCH', 'Basic research methods applied to problems in patient care. Prerequisites: NURS 220, 305 or 307, or 321, or approval of instructor.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1019', '81001', 'NURS', '462', '2006', 'COMMUNITY HEALTH', 'Nursing theory for individuals, families, and groups with a focus on community health. Prerequisites: NURS 403, 404, 405; credit or registration in 463.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1020', '555323', 'NURS', '463', '2006', 'COMMUNITY PRACTICUM', 'Application of nursing theory to individuals, families, and groups in the community. Prerequisites: NURS 403, 404, 405; credit or registration in 462.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1021', '814151', 'NURS', '464', '2006', 'NURSING MANAGEMENT', 'Management theory and principles adapted to nursing. A focus on synthesis of nursing health maintenance, restoration and reorganization. Prerequisites: NURS 403, 404, 405; credit or registration in 465.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1022', '104290', 'NURS', '465', '2006', 'MANAGEMENT PRACTICUM', 'Application of management theory to the delivery of health care in a variety of settings. Prerequisites: NURS 403, 404, 405; credit or registration in 464.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1023', '44898', 'NURS', '472', '2006', 'COMMUNITY CONCEPTS', 'Nursing theory with a focus on community health. Prerequisites: NURS 204, 220; 280; current RN licensure; credit or registration in 473.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1024', '928910', 'NURS', '473', '2006', 'COMMUNITY PRACTICUM RN', 'Application of the nursing theory to individuals, families, and groups in the community. Prerequisites: NURS 204, 220; current RN licensure; credit or registration in 472.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1025', '246738', 'NURS', '474', '2006', 'MANAGEMENT CONCEPTS', 'Management theory and principles adapted to nursing for the Registered Nurse. Prerequisites: NURS 204, 220; 280; current RN licensure; credit or registration in 475.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1026', '294271', 'NURS', '433', '2006', 'NURSING INTERVENTION IN THE CARE OF THE AGED', 'A study of the concepts and principles necessary for nursing intervention in the care of the aged. The focus will be on the seven broad areas in the care of the aged that have nursing implications: Aging and Nursing, The Normal Aging Process, Deviations of the Aging Process: Geropsychiatry; Deviations of the Aging Process: Pathophysiology; The Nursing Process; Social Forces and Aging: Implications for Nursing; Research in Aging.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1027', '104465', 'NURS', '434', '2006', 'LABOR AND DELIVERY NURSING', 'Nursing theoretical and clinical components related to childbearing families during the intrapartal period. Prerequisites: NURS 332.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1028', '434333', 'NURS', '435', '2006', 'HEALTH ASSESSMENT AND HEALTH MAINTENANCE OF THE AG', 'A study to impart concepts of the nursing processes as they relate to health assessment and health maintenance of the aging person. Principles of history-taking and physical examination and assessment skills are emphasized as they pertain to the aging patient. The focus is directed toward the assessment of physiological, environmental, sociological, and psychological needs in the aging patient.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1029', '480870', 'NURS', '437', '2006', 'PROFESSIONAL ISSUES AND TRENDS', 'A synthesis course designed to enhance the student’s knowledge of the demands facing professional nurses in current practice settings based on past and present events. Prerequisite: Completion of NURS 323, 325, 332, 334, 430, 404, 405 or approval of the Director, School of Nursing.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1030', '863783', 'NURS', '441', '2006', 'PERIOPERATIVE NURSING THEORY', 'Advanced course focusing on the study of aseptic technique and care of patients in the operating room/recovery room. Emphasis is placed on perioperative concepts and principles. Prerequisites: NURS 304, 305 or 307, 306, or Registered Nurse licensure.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1031', '597209', 'NURS', '442', '2006', 'PERIOPERATIVE NURSING SCIENCE PRACTICUM', 'Clinical study designed to apply principles of preoperative, operative, and postoperative nursing skills. Supervised clinical experiences are offered in health care settings that provide surgical procedures. Prerequisites: NURS 304, 305 or 307, 306, or Registered Nurse licensure.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1032', '740053', 'NURS', '443', '2006', 'ONCOLOGICAL NURSING THROUGH THE LIFE SPAN', 'Designed to expand the use of the nursing process with oncology patients. Malignant diseases most frequently occurring throughout the life cycle are studied. Prerequisites: NURS 304, 305 or 307, 306, or Registered Nurse licensure.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1033', '463107', 'NURS', '446', '2006', 'ETHNIC NURSING CARE', 'Emphasis is placed on the impact of ethnicity in relation to health behaviors and nursing care. Prerequisite: Approval of the Director, School of Nursing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1034', '572325', 'NURS', '448', '2006', 'WOMEN’S HEALTH ISSUES', 'The study of women’s health issues and concerns from a psycho-bio-social perspective. Prerequisite: none.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1035', '268615', 'OCCT', '106', '2006', 'PATHOLOGY', 'Introduction to physical pathological conditions commonly encountered in the practice of occupational therapy. Prerequisite: Credit or concurrent enrollment in Biology 226, 2.0 overall GPA (uncorrected). Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1036', '259012', 'OCCT', '115', '2006', 'PATHOLOGY', 'Introduction to mental and developmental conditions commonly encountered in the practice of occupational therapy. Prerequisite: Completion or concurrent registration in PSYC 201, 2.0 overall GPA (uncorrected). Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1037', '360199', 'OCCT', '201', '2006', 'INTRODUCTION TO OCCUPATIONAL THERAPY', 'History and development of occupational therapy; introduction to the scope, roles, functions and treatment objectives. Prerequisite: 2.0 overall GPA (uncorrected). POTA students only. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1038', '131866', 'OCCT', '203', '2006', 'DEVELOPMENTAL TASKS AND SKILLS', 'The exploration of life tasks and skills as related to the developmental process from birth through senescence for OTA majors only. (Substitute PSYC 278)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1039', '749881', 'OCCT', '205', '2006', 'EVALUATION', 'Introduction to basic evaluation techniques used in the practice of occupational therapy and the O.T. assistant’s role in the evaluation process. Prerequisite: 2.3 overall GPA (uncorrected), completion of OCCT 201. For Occupational Therapy Assistant majors only. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1040', '162530', 'OCCT', '208', '2006', 'GROUP PROCESS', 'Basic elements of interactive processes, activity teaching and observation writing. Prerequisite: 2.3 OCTA (Sp) only.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1041', '417930', 'OCCT', '222', '2006', 'THEORY AND PRACTICE I', 'Exploration of general treatment concepts and treatment approaches and techniques for the client with physical dysfunction to be performed by the Occupational Therapy Assistant. Prerequisites: O.T. 201; concurrent registration in OCCT 205, 226; 2.3 cumulative GPA (uncorrected). For Occupational Therapy Assistant majors. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1042', '687452', 'OCCT', '223', '2006', 'THEORY AND PRACTICE II', 'Exploration of treatment approaches and techniques for the client with developmental and psychosocial dysfunction to be performed by the Occupational Therapy Assistant. Prerequisites: completion of 201, 205, 222, and 226; completion or concurrent registration in 225 and 227; 2.3 cumulative GPA (uncorrected). For Occupational Therapy Assistant majors. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1043', '767107', 'OCCT', '225', '2006', 'THEORY AND PRACTICE LAB', 'A laboratory course to accompany Occupational Therapy 223 providing enrichment to didactic coursework through directed observation and participation in selected aspects of the Occupational Therapy process. Three hours of laboratory. Prerequisites: Concurrent registration in 223; 2.3 overall GPA (uncorrected). For Occupational Therapy Assistant majors. Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1044', '948583', 'OCCT', '226', '2006', 'APPLIED LABORATORY', 'A laboratory course which explores the application of evaluation and treatment techniques used by the occupational therapy assistant. 3 hours of laboratory. Prerequisite: 2.3 cumulative GPA; concurrent registration in OCCT 205 and OCCT 222; for OTA majors only. F.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1045', '380195', 'OCCT', '228', '2006', 'PEDIATRIC LABORATORY', 'A laboratory course providing an opportunity for manipulation and application of treatment approaches and techniques for the client with developmental dysfunction. Three contact hours of lab per week. Prerequisites: 2.3 cumulative GPA, concurrent registration in OCCT 223. For OTA majors only. (Formerly OCCT 227)', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1046', '500159', 'OCCT', '229', '2006', 'PSYCHOSOCIAL LABORATORY', 'A laboratory course providing an opportunity for manipulation and application of treatment approaches and techniques for the client with psychosocial dysfunction. 3 contact hours of lab per week. Prerequisites: 2.3 cumulative GPA, concurrent registration in OCCT 223. For OTA majors only. (Formerly OCCT 227)', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1047', '420103', 'OCCT', '245', '2006', 'THERAPEUTIC MEDIA', 'Basic techniques and analysis of therapeutic media to be applied in Occupational Therapy treatment. Prerequisite: 2.3 overall GPA in Occupational Therapy Assistant majors. F', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1048', '715393', 'OCCT', '260', '2006', 'FIELD WORK PRACTICUM', 'Eight weeks occupational therapy clinical experience in a University-approved setting with clients who have physical, developmental, or psychiatric disorders. Prerequisite: Approval of Fieldwork Coordinator; 2.3 overall GPA (uncorrected). For Occupational Therapy Assistant majors only. Students receive a grade of &quot;CR&quot; or &quot;F.&quot;', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1049', '827772', 'PHAR', '301', '2006', 'STRESS AND THE IMPAIRED PROFESSIONAL', 'A discussion of factors associated with inappropriate responses to stress leading to impairment of health professionals such as alcoholism, drug dependence, and occupational burnout with emphasis on preventative strategies.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1050', '501103', 'PHAR', '302', '2006', 'MEDICAL ETHICS', 'The class will address ethical dilemmas the health professionals face in their professional careers. The students will gain keener insight into the problems associated with health care delivery systems and will be prepared to face the ethical problem that will surface with the adoption of reform. Open to majors in the Health Sciences only.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1051', '270716', 'PHAR', '310', '2006', 'HISTORY OF PHARMACY', 'An examination of the historical basis of contemporary pharmacy practice.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1052', '641923', 'PHAR', '406', '2006', 'COMMUNITY HEALTH EDUCATION', 'Development of technologies that facilitate changes in health related behavior that affect individuals, organizations, and social or community systems.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1053', '605393', 'PHAR', '300', '2006', 'INTRODUCTION TO PHARMACY', 'An introduction to the pharmacist in society, modes of pharmacy practice, historical perspectives, fundamentals of professional responsibility. Grading will be on a credit, no credit basis. Students receiving a grade of &quot;no credit&quot; must repeat the course. Prerequisite: First Year Pharmacy Standing.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1054', '315614', 'PHAR', '350', '2006', 'PHARMACY ADMINISTRATION', 'Principles of management and functions essential to the distribution of goods and services in the pharmaceutical marketplace. Prerequisites: Economics 201; Accounting 110; First Year Pharmacy Standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1055', '570833', 'PHAR', '385', '2006', 'PATIENT COUNSELING AND COMMUNICATION', 'Principles and techniques of communication applicable to pharmacy practice. Prerequisite: Second Year Pharmacy Standing.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1056', '852131', 'PHAR', '402', '2006', 'PHARMACEUTICS I', 'Fundamentals of measurement and calculation and an introduction to liquid oral dosage forms. Three hours lecture and three hours laboratory per week. Prerequisite: First year pharmacy standing.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1057', '609884', 'PHAR', '403', '2006', 'PHARMACEUTICS II', 'A study of heterogeneous liquid, semisolid dosage forms. Transdermal and novel drug delivery systems will be discussed. Prerequisite: 402', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1058', '675298', 'PHAR', '407', '2006', 'MEDICINAL CHEMISTRY I', 'The biochemical basis of drug action. A molecular biology approach with pharmaceutical orientation will be stressed. Special emphasis will be placed on biochemical catalysis (enzyme structure, kinetics, and regulation), metabolism of major food constituents, nutritional parameters affecting bio-chemical processes, hereditary metabolic diseases, and the basic biochemical processes, hereditary metabolic diseases, and the basic biochemical processes associated with biotechnology. Prerequisite: First Year Pharmacy Standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1059', '286464', 'PHAR', '408', '2006', 'IMMUNOLOGY & BIOTECHNOLOGY', 'A discussion of nonspecific host defense mechanisms and a detailed description of specific immunity. Products that impart artificial active and passive immunity are presented. The concept of biotechnology is discussed together with the currently available products of genetic engineering that relate to immunology. The various immunologic disorders and the immunology of cancer are also described. Prerequisite: 411.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1060', '90754', 'PHAR', '411', '2006', 'PHARMACOLOGY I', 'Pharmacological, anatomical, and physiological basis of drug action. Topics include general principles of therapeutics, pharmacodynamics, pharmacokinetics and toxicology, and the action of drugs in the peripheral nervous system. Prerequisite: First Year Pharmacy Standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1061', '539081', 'PHAR', '412', '2006', 'PHARMACOLOGY II', 'Continuation of Pharmacy 411 with emphasis on inflammatory and allergic reactions and the central nervous system. Prerequisites: 411; Credit or registration in 436.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1062', '883673', 'PHAR', '421', '2006', 'HERBAL REMEDIES', 'A study of herbal preparations and other phytomedicinals widely used by the general public as self- selected OTC products for therapeutic or preventive purposes. Emphasis is placed on the need, safety, and efficacy of herbal preparations, and the role of the pharmacist in product selection. Prerequisite: 409; credit or registration in 414.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1063', '830807', 'PHAR', '424', '2006', 'PHARMACEUTICS IV', 'A study of parenterally administered drugs and specialized delivery systems. Three hours lecture and three hours laboratory per week. Prerequisite: 432.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1064', '2188', 'PHAR', '425', '2006', 'DRUG INFORMATION RETRIEVAL AND EVALUATION', 'Introduction to services and resources used to provide evaluative information and associated professional responsibilities. Prerequisite: First Year Pharmacy Standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1065', '455998', 'PHAR', '426', '2006', 'AGING AND DRUG USE IN THE ELDERLY', 'An examination of normative and pathological aging and their effect on drug use and response in the aged population. Prerequisite: Credit or registration in 414.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1066', '99422', 'PHAR', '427', '2006', 'PHARMACY CARE LAB III', 'Continuation of Pharmacy 322 conducted in sequence and in cooperation with other courses in the first semester of the second professional year. Grading will be on a credit, no credit basis. Students receiving a grade of &quot;no credit&quot; must repeat the course. Three hours laboratory experience per week. Prerequisite: Credit or registration in 385, 410, 413, 432, and 435.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1067', '261200', 'PHAR', '436', '2006', 'PHARMACOLOGY LABORATORY', 'A laboratory course to accompany 412. Three hours laboratory. Prerequisite: 411; credit or registration in 412.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1068', '816197', 'PHAR', '440', '2006', 'PROFESSIONAL SERVICE ACCESSORIES', 'Selection, indications, uses, warnings, maintenance and storage of prescription and non-prescription accessories, durable medical equipment and related items. Prerequisite: Third Year Pharmacy Standing.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1069', '231287', 'PHAR', '445', '2006', 'PROFESSIONAL PRACTICE LABORATORY', 'Principles and techniques of dispensing extemporaneously prepared and prefabricated products, and relevant aspects of contemporary pharmacy practice. Prerequisites: 432; credit or registration in 454.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1070', '11082', 'PHAR', '446', '2006', 'NON-PRESCRIPTION MEDICATIONS AND SELF-CARE', 'Selection and use of non-prescription medications for self- treatment of disease or to treat self-limiting disease states. Prerequisite: Credit or registration in 413.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1071', '978726', 'PHAR', '450', '2006', 'PHARMACY CARE LAB IV', 'A capstone course aimed at further development of problem solving skills relevant to making clinical decisions and solving medication related problems. Prerequisite: Credit or registration in Pharmacy 414, 418, 587.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1072', '649216', 'PHAR', '451', '2006', 'PHARMACEUTICAL CARE MANAGEMENT', 'The behavioral, ethical, organizational, and pharmacoeconomic determinants of medication use management in the delivery of pharmaceutical care. Prerequisite: First Year Pharmacy Standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1073', '698533', 'PHAR', '456', '2006', 'RESEARCH METHODOLOGY', 'This course introduces the student to the design of hypothesis-testing studies used in clinical drug trials. Common statistical analyses are also taught. Although students who master the material should be able to design studies to answer questions using the scientific method, the purpose is to give students the skills to evaluate published studies in the medical literature. Prerequisite: Third Year Pharmacy Standing.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1074', '745834', 'PHAR', '470', '2006', 'HOSPITAL PHARMACY', 'The history of hospitals, organization and administration of patient care facilities, distribution of pharmaceuticals to inpatients and outpatients, abilities required of the pharmacist, and the many roles of the pharmacist as a member of the health team. Prerequisites: Second Year Pharmacy Standing.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1075', '597799', 'PHAR', '480', '2006', 'INTRODUCTORY COMMUNITY PHARMACY EXPERIENCE', 'A one-month introduction to the practice of pharmacy in the community setting. Minimum of 40 hours per week experiential training. Prerequisite: First year pharmacy standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1076', '78728', 'PHAR', '481', '2006', 'INTRODUCTORY INSTITUTIONAL PHARMACY EXPERIENCE', 'A one-month introduction to the practice of pharmacy in the institutional setting. Minimum of 40 hours per week experiential training. Prerequisite: Second year pharmacy standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1077', '245994', 'PHAR', '493', '2006', 'CASE STUDIES IN PHARMACY ADMINISTRATION', 'Case studies in drug marketing, pharmacy law and ethics, professional relations, and managerial accounting. Prerequisite: 451, 454.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1078', '17903', 'PHAR', '494', '2006', 'RETAIL PHARMACY MANAGEMENT', 'Principles and problems related to the planning, organizing, and controlling of the retail pharmacy. Prerequisite: 451.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1079', '794121', 'PHAR', '503', '2006', 'PATIENT ASSESSMENT FOR PHARMACISTS', 'Utilization of physical assessment findings to evaluate drug therapy needs and response. Prerequisite: Third Year Pharmacy Standing.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1080', '73766', 'PHAR', '515', '2006', 'CLINICAL PHARMACOKINETICS', 'The application of pharmacokinetic principles to the safe and therapeutic management of the individual patient. Prerequisites: 432.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1081', '519006', 'PHAR', '557', '2006', 'PHARMACOTHERAPY FORUM', 'Exercises designed to give experience in making and evaluating formal medical presentations. Prerequisite: Credit or registration in 588.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1082', '64838', 'PHAR', '586', '2006', 'DISEASE PATHOGENESIS AND PHARMACOTHERAPY II', 'Pathogenesis, diagnosis and rational therapeutic approaches to treatment of selective disease states in humans. Prerequisite: 410, 413, 585; Credit or registration in 414.', '5', '5', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1083', '715689', 'PHAR', '588', '2006', 'DISEASE PATHOGENESIS AND PHARMACOTHERAPY IV', 'Pathogenesis, diagnosis and rational therapeutic approaches to treatment of selective disease states in humans. Prerequisite: 414, 418, 587.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1084', '124399', 'PHAR', '589', '2006', 'RESEARCH METHODS FOR THE PHARMACIST', 'Introduction to the design and common statistical analysis of clinical drug trials. Prerequisite: 425; Third Year Pharmacy.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1085', '380451', 'PHAR', '591', '2006', 'ADVANCED INSTUTIONAL PHARMACY EXPERIENCE', 'One-month, forty hours per week experiential training in an inpatient hospital or institution based pharmacy practice. Prerequisite: Fourth year pharmacy standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1086', '286523', 'PHAR', '592', '2006', 'ADVANCED COMMUNITY PHARMACY EXPERIENCE', 'One-month, forty hours per week experiential training in a community based pharmacy practice. Prerequisite: Fourth year pharmacy standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1087', '976529', 'PHAR', '604', '2006', 'AMBULATORY CARE EXPERIENCE I', 'A one-month structured experience in the provision of clinical pharmacy services in ambulatory settings. A minimum of 40 hours/week of experiential training. Prerequisite: Fourth year pharmacy standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1088', '990334', 'PHAR', '606', '2006', 'AMBULATORY CARE EXPERIENCE II', 'A one-month continuation of Pharmacy 604 with greater involvement in therapeutic decision making. Minimum of 40 hours per week experiential training. Prerequisite: Credit or registration in 604.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1089', '961820', 'PHAR', '608', '2006', 'ACUTE CARE EXPERIENCE I', 'A one-month structured experience focusing on clinical pharmacy services in the acute care setting. Minimum of 40 hours per week experiential training. Prerequisite: Fourth Year Pharmacy Standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1090', '262994', 'PHAR', '609', '2006', 'ACUTE CARE EXPERIENCE II', 'A one-month continuation of Pharmacy 608 with greater involvement in therapeutic decision making. Minimum of 40 hours per week experiential training. Prerequisite: Credit or registration in 608.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1091', '81089', 'PHAR', '629', '2006', 'NON-PATIENT CARE EXPERIENCE', 'A one-month structured experience focusing on aspects of pharmacy outside of direct patient care. Minimum of 40 hours per week experiential training. Prerequisite: Fourth Year Pharmacy Standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1092', '500901', 'PHAR', '630', '2006', 'PATIENT CARE EXPERIENCE I', 'A one-month structured experience focusing on patient centered pharmacy services in the acute or ambulatory care setting. Minimum of 40 hours per week experiential training. Prerequisite: Fourth Year Pharmacy Standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1093', '146667', 'PHAR', '631', '2006', 'PATIENT CARE EXPERIENCE II', 'A one-month structured experience focusing on patient centered pharmacy services in the acute or ambulatory care setting. Minimum of 40 hours per week experiential training. Prerequisite: Credit or registration in 630.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1094', '911895', 'PHAR', '409', '2006', 'MEDICINAL CHEMISTRY II', 'A study of organic compounds of pharmaceutical and medicinal importance with emphasis on chemical structure, relationship of chemical structure to drug action, metabolism, usage, incompatibilities, and syntheses. Prerequisite: 407.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1095', '503089', 'PHAR', '410', '2006', 'MEDICINAL CHEMISTRY III', 'Continuation of 409. Prerequisite: 409.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1096', '602664', 'PHAR', '413', '2006', 'PHARMACOLOGY III', 'Continuation of 412 with emphasis on the cardiovascular, renal, and respiratory systems. Prerequisites: 407, 412, 436.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1097', '11318', 'PHAR', '414', '2006', 'PHARMACOLOGY IV', 'Continuation of 413 with emphasis on the gastrointestinal system, endocrine system, reproductive system, and chemotherapeutic agents. Prerequisites: 413; credit or registration in 418.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1098', '764288', 'PHAR', '418', '2006', 'CHEMOTHERAPEUTIC AGENTS', 'A study of the chemistry, therapeutic classification, mechanisms of action, toxicity, and spectra of agents currently used in the therapy of infectious and neoplastic diseases. Prerequisites: 408, 412; credit or registration in 409.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1099', '418862', 'PHAR', '419', '2006', 'PHARMACOBIOTECH', 'A study of the application of biotechnology to pharmaceuticals including drugs, diagnostics, and vaccines. Emphasis is placed on the techniques of biotechnology, recombinant proteins and peptides, monoclonal antibodies, and physiochemical properties, and metabolism of genetically engineered biopharmaceuticals. Prerequisite: 408, 409; credit or registration in 414; or approval of Department Head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1100', '242604', 'PHAR', '423', '2006', 'MEDICAL IMMUNOLOGY', 'Cellular and serum immunology. Antibody structure and function. Types of antigen antibody reactions. Immunosuppression, tolerance and enhancement. Systems mediating immunologic injury and inflammation. Immunogenetics. Prerequisites: 408, 412, 436.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1101', '775370', 'PHAR', '432', '2006', 'PHARMACEUTICS III', 'The interrelationship between formulation factors and pharmacokinetic aspects of drug absorption, distribution, metabolism, and excretion. Prerequisite: 403.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1102', '397589', 'PHAR', '454', '2006', 'LAW', 'Local, state and federal laws, rules and regulations relating to the practice of Pharmacy. Prerequisite: Second Year Pharmacy.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1103', '891820', 'PHAR', '467', '2006', 'PESTICIDES', 'Chemicals and drugs which have important application as insecticides, herbicides, fungicides, and rodenticides. Prerequisite: 413.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1104', '473903', 'PHAR', '479', '2006', 'NUCLEAR PHARMACY', 'Handling and storage of radioisotopes and their application to pharmacy. The official radiopharmaceuticals and non-official radiopharmaceuticals of merit employed in therapy will be considered. Prerequisite: Second year pharmacy.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1105', '143424', 'PHAR', '489', '2006', 'INTRODUCTORY TOXICOLOGY', 'Survey of the occurrence, properties and harmful effects of natural and man-made toxic substances in personal, occupational and global environments. Prerequisites: Credit or registration in Zoology 311, 313, and Chemistry 304.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1106', '489620', 'PHAR', '500', '2006', 'MOLECULAR STRUCTURE AND FUNCTION OF PROTEINS', 'This course is designed to introduce graduate students to basic concepts and techniques in the study of protein structure and function. Course coverage will include structural determinants, relationship of structure to function, thermodynamics of protein interactions, protein/ligand interactions, kinetics, transition state theory, enzyme mechanisms and protein purification techniques.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1107', '552631', 'PHAR', '502', '2006', 'PRODUCT DEVELOPMENT', 'Theoretical and practical aspects involved in the formulation of a pharmaceutical; emphasis on research techniques employed in developing an acceptable medicinal product. Two hours lecture and six hours laboratory. Prerequisites: 431, 432.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1108', '389417', 'PHAR', '513', '2006', 'PHARMACOKINETICS', 'A quantitative treatment of the dynamics of drug absorption, distribution, metabolism, and excretion, including the development of mathematical models for these processes. Application of analog and digital computation will be discussed. Prerequisites: Mathematics 301 or 401, and/or Consent of Department Head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1109', '507522', 'PHAR', '514', '2006', 'ADVANCED BIOPHARMACEUTICS', 'An advanced treatment of factors affecting drug availability from dosage forms and the influence of the route of administration and the dosage regimen on drug availability. Prerequisite: 513 or Consent of Department Head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1110', '346753', 'PHAR', '522', '2006', 'ADVANCED PHARMACOLOGY LABORATORY', 'Laboratory techniques used to evaluate drug action. Nine hours laboratory. Prerequisite: Credit or registration in 521.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1111', '463183', 'PHAR', '523', '2006', 'FORENSIC TOXICOLOGY', 'Quantitative and qualitative toxicological analyses. Six hours laboratory. Prerequisite: 489.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1112', '26528', 'PHAR', '525', '2006', 'INDUSTRIAL TOXICOLOGY', 'Chemical hazards in the workplace; regulatory and medico-legal programs, product safety, worker health protection, chemical-related health risk assessment. Prerequisite: 489.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1113', '411590', 'PHAR', '527', '2006', 'NEUROPHARMACOLOGY', 'Advanced discussion of the literature and methodology related to the action of drugs in neurological systems. Prerequisites: 413, 414, or Consent of Department Head.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1114', '178872', 'PHAR', '529', '2006', 'IMMUNOPHARMACOLOGY', 'A study of the interactions between pharmacologic agents; the immune systems and their biological function. Prerequisites: 414, 423.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1115', '150927', 'PHAR', '531', '2006', 'SYNTHETIC MEDICINALS', 'A study of the methodologies and strategies used in the synthesis of compounds of pharmacological significance. Prerequisites: Chemistry 531, 532 or Consent of Department Head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1116', '792040', 'PHAR', '532', '2006', 'CHEMISTRY OF HETEROCYCLIC COMPOUNDS', 'Chemical and physical properties, nomenclature, and synthesis of heterocycles commonly found in compounds of biological and pharmacological importance. Prerequisites: Chemistry 531, 532, or Consent of Department Head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1117', '465395', 'PHAR', '540', '2006', 'BIOSTATISTICS', 'The theoretical and applied techniques of data collection, analysis, and interpretation in pharmaceutical research. Prerequisite: Psychology 439.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1118', '127456', 'PHAR', '541', '2006', 'ADVANCED MARKETING OF DRUG PRODUCTS', 'Distributive systems and underlying economic principles, functions, policies, and practice as related to marketing drug products.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1119', '782375', 'PHAR', '542', '2006', 'ADVANCED RETAIL PHARMACY MANAGEMENT', 'Advanced problems related to planning, organizing, and controlling the retail pharmacy.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1120', '427215', 'PHAR', '543', '2006', 'BEHAVIORAL SCIENCES IN PHARMACY ADMINISTRATION', 'Psychology, sociology, and communications as related to pharmacy management.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1121', '390449', 'PHAR', '544', '2006', 'NEW DRUG DEVELOPMENT', 'Professional, administrative and legal requirements for discovery, production and distribution of a new drug.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1122', '863464', 'PHAR', '545', '2006', 'RESEARCH METHODOLOGY', 'Description and application of the scientific method to social and economic research problems peculiar to the health fields.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1123', '928116', 'PHAR', '546', '2006', 'RESEARCH DESIGN', 'Design and analysis of research problems in the health care field.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1124', '537115', 'PHAR', '547', '2006', 'HEALTH CARE ADMINISTRATION', 'A description and analysis of the current health care system in the United States, using economic and administrative concepts and principles. Prerequisite: Consent of Department Head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1125', '775359', 'PHAR', '548', '2006', 'HOSPITAL ADMINISTRATION', 'The hospital as a complex health care organization, its nature and functions, personnel and staff behavior, and how it is viewed by its client, the patient. Prerequisite: Consent of Department Head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1126', '431205', 'PHAR', '549', '2006', 'HEALTH CARE LAW', 'A study of legal issues pertaining to health care topics such as modes and institutions for providing medical care, liability of selected providers and review of applicable laws, rules, and regulations. Prerequisite: Consent of Department Head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1127', '139780', 'PHAR', '550', '2006', 'BIOCHEMICAL TOXICOLOGY', 'A detailed coverage of biochemical mechanisms of toxicity. Prerequisite: Credit or registration in Toxicology 412 or equivalent.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1128', '786676', 'PHAR', '555', '2006', 'FERMENTATION', 'Production of medicinal products by microorganisms. Two hours lecture and three hours laboratory.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1129', '195494', 'PHAR', '566', '2006', 'ADVANCED MEDICINAL ANALYSIS', 'Advanced analytical procedures; use of new techniques and equipment. Prerequisites: Credit or registration in 568 and Consent of Department Head.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1130', '558641', 'PHAR', '568', '2006', 'ADVANCED MEDICINAL ANALYSIS LABORATORY', 'A laboratory course to accompany 566. Six hours laboratory. Prerequisite: Credit or registration in 566.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1131', '29281', 'PHAR', '569', '2006', 'CONCEPTS IN DRUG DESIGN', 'A study of traditional and contemporary approaches to drug design and development. Basic concepts important to receptor-ligand interactions will be introduced. Literature examples will be used to illustrate contemporary methods, such as molecular modeling, QSAR, and combinatorial strategies. Prerequisite: 409 or Consent of Instructor.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1132', '970863', 'PHAR', '577', '2006', 'PHARMACEUTICAL PREFORMULATION', 'Introduce students to the necessary physicochemical parameters, determination of kinetic rate profiles, characterization of physical properties, and compatibility with common excipients of new drug substances.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1133', '956229', 'PHAR', '578', '2006', 'SOLID-STATE CHEMISTRY OF DRUGS', 'This fundamental course on pharmaceutical solids provides a comprehensive, cutting-edge overview of ideas, issues, language, and methodologies of pharmaceutical solid-state research.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1134', '921101', 'PHAR', '579', '2006', 'DOSAGE FORM DESIGN', 'Theoretical and practical aspects involved in the design of dosage forms including the development of drug molecules and industrial scale manufacturing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1135', '444767', 'PHAR', '580', '2006', 'TOXICOLOGY RESEARCH', 'Student participation in ongoing laboratory research to illustrate experimental design and performance. Prerequisite: Open to students enrolled in the non- thesis option Master of Science program in toxicology (pharmaceutical sciences) only.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1136', '99654', 'PHAR', '581', '2006', 'ADVANCED TOXICOLOGY', 'Cellular mechanisms of adverse chemical effects in biological systems. Areas of emphasis include principles of pathology, genotoxicity, carcinogenicity, teratogenicity and toxic responses of major organ systems. Prerequisites: 415, 489.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1137', '410721', 'PHAR', '582', '2006', 'ADVANCED TOXICOLOGY LABORATORY', 'Common laboratory techniques used to evaluate chemical toxicity. One hour lecture, six hours laboratory. Prerequisite: Credit or registration in 581.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1138', '997398', 'PHAR', '583', '2006', 'SURFACE AND INTERFACIAL PHENOMENA', 'This course deals with the fundamentals of surface and interfacial phenomena encountered by pharmaceutical scientists in the development of dosage forms.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1139', '489070', 'PHAR', '584', '2006', 'CHEMICAL KINETICS AND STABILITY OF PHARMACEUTICALS', 'This course deals with the fundamentals aspects of chemical kinetics applied to the stability of pharmaceutical raw materials and final dosage forms.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1140', '918243', 'PHIL', '201', '2006', 'INTRODUCTION', 'The nature of philosophical inquiry and themes in the history of philosophy, including questions which deal with such topics as knowledge and truth, the nature of reality, metaphysics, faith and unbelief, meaning and value, the individual and society, and human nature.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1141', '344151', 'PHIL', '203', '2006', 'LOGIC', 'The nature and limits of logic, informal critical reasoning, types of arguments, good reasoning and its relationship to truth, the traditional fallacies, and an introduction to formal logic with statement calculus.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1142', '952253', 'PHIL', '300', '2006', 'DIRECTED STUDY', 'Selected studies in Philosophy and Religion. Content varies. A. A Study of Religion. (Same as Religion 300A.); B. Ethics in America. C. The Examined Life.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1143', '944770', 'PHIL', '400', '2006', 'INTERSECTIONS OF SCIENCE AND RELIGION', 'This course will cover the issues that have arisen historically and also in the contemporary world in the dialogue of science and religion. Major focus should be given to how these issues and positions have affected our understanding of human nature. Topics that should be covered include: Darwin and Evolutionary Theory, Creationism, Design, the Origin of the Cosmos, the Big Bang, Quantum Theory and Chance, The Free Will, Miracles and Prayer, and Theories of Human Nature in Asian Religions and Christianity.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1144', '755740', 'PHIL', '401', '2006', 'ANCIENT AND MEDIEVAL', 'Philosophical thought from the Pre-Socratic to the sixteenth century, including Plato and Aristotle, the Epicureans and the Stoics, the Neo-Platonists, and Aquinas; the rise and fall of medieval scholasticism. (Formerly 301.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1145', '131126', 'PHIL', '402', '2006', 'MODERN', 'Philosophical thought from the seventeenth century to the present, including rationalism, empiricism, idealism, pragmatism, logical positivism, and existentialism. (Formerly 302.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1147', '547781', 'PHIL', '404', '2006', 'CONTROVERSIAL ISSUES IN BIOETHICS', 'The ethical significance of and ethical dilemmas that arise as a result of new cutting-edge biotechnologies. Ethical topics include: reproductive options, abortion, stem-cell research, human cloning, genetic discrimination and confidentiality, genetic engineering, regulation of human research, and euthanasia.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1148', '596520', 'PHIL', '405', '2006', 'PHILOSOPHY OF RELIGION', 'A study of attempts to bring rational justification and clarification to religious beliefs and practices, focusing primarily on the concepts of Christian theology. Topics will include the existence of nature of God, faith and reason, death and immortality, mysticism, the problem of evil, religious experience, and the impact of existentialism on contemporary religious thought.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1149', '223153', 'PHIL', '407', '2006', 'THEORIES OF HUMAN NATURE', 'This course will cover the theories of human nature that have arisen historically and also in the contemporary world. Major figures to be covered include Plato, Aristotle, Aquinas, Hobbes, Locke, Hume, Kant, Darwin, Marx, Nietzche, Freud, Sartre, Maslow, and Contemporary Psychological theories. This course will answer questions like what is the human being?, what are human beings capable of?, what does it mean to live a fulfilled human life?, what is the purpose of human life?, is the human being basically aggressive or kind?, and how do human beings differ from animals?', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1150', '330157', 'PHIL', '415', '2006', 'THEORY OF KNOWLEDGE', 'A comparative and critical survey of the major theories which deal with the nature and extent of human knowledge and related notions,such as belief, justification, perception, and memory. Classical and contemporary philosophical positions will be covered representing a wide range of theories and their application to other disciplines of human inquiry, such as biology and psychology.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1151', '23736', 'PHYS', '201', '2006', 'DESCRIPTIVE ASTRONOMY', 'The astronomical universe from a non-mathematical viewpoint. Prerequisite: Credit or registration in PHYS 202.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1152', '613601', 'PHYS', '203', '2006', 'GENERAL', 'Principles and applications of mechanics, heat and sound. Prerequisite: MATH 112 or 113.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1153', '193621', 'PHYS', '204', '2006', 'GENERAL', 'Principles of electricity, magnetism, and light. Prerequisite: PHYS 203.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1154', '951851', 'PHYS', '207', '2006', 'MECHANICS, SOUND, AND HEAT', 'Principles and applications of mechanics, sound, and heat. For students who will pursue science and engineering. Prerequisite: Credit or registration in MATH 131. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1155', '150717', 'PHYS', '208', '2006', 'ELECTRICITY, MAGNETISM, AND LIGHT', 'Principles and applications of electricity, magnetism, and light. For students who will pursue science and engineering. Prerequisite: PHYS 207. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1156', '968980', 'PHYS', '210', '2006', 'ELECTRICITY, MAGNETISM, AND LIGHT LABORATORY', 'Laboratory experiments in electricity, magnetism, and light to accompany PHYS 204 or 208. Prerequisite: Credit or registration in 204 or 208. (Formerly PHYS 206 and 212.)', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1157', '383056', 'PSYC', '201', '2006', 'INTRODUCTION', 'Principles of psychology as a science, characteristics of human behavior and cognitions and a survey of major topic areas including motivation, learning, emotions, and social and biological factors. F, Sp, Su', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1158', '290496', 'PSYC', '203', '2006', 'CHILD PSYCHOLOGY', 'Physical, cognitive, psychological, and social development of children up to adolescence. Focus on normal development with attention to deviations from normality. Principles and applications with implications for families and professionals. Prerequisite: PSYC 201. F, Sp, Su', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1159', '755657', 'PSYC', '205', '2006', 'ADOLESCENT PSYCHOLOGY', 'Physical, cognitive, psychological, and social development of the adolescent. Focus on normal development with attention to deviations from normality. Prerequisite: PSYC 201. F, Sp, Su', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1160', '578549', 'PSYC', '278', '2006', 'DEVELOPMENTAL PSYCHOLOGY', 'Human life-span developmental psychology emphasizing an integrative conceptualization of the totality of ontogenetic, behavioral and physiological changes. Prerequisite: PSYC 201. F, Sp, Su', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1161', '849137', 'PSYC', '301', '2006', 'EDUCATIONAL PSYCHOLOGY', 'Application of psychological methods and facts as applied to the teaching of individuals of varying development levels and cultural backgrounds. Secondary and K-12 teacher education majors will complete twenty hours of observation at selected school sites. Prerequisite: PSYC 201. F, Sp, Su', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1162', '784937', 'PSYC', '303', '2006', 'PHYSIOLOGICAL', 'Physiological correlates of behavior with special emphasis on the physiology and anatomy of the nervous system as a basis for relating behavior to its physiological components. Prerequisites: PSYC 201; BIOL 226 or equivalent. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1163', '549413', 'PSYC', '315', '2006', 'EXPERIMENTAL', 'Principles of learning and related principles of motivation and memory, with emphasis on scientific method and experimental findings. Prerequisite: PSYC 201. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1164', '805366', 'PSYC', '316', '2006', 'EXPERIMENTAL', 'Human sensory processes and perception, with emphasis on scientific method and experimental findings Prerequisite: PSYC 201. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1165', '706039', 'PSYC', '317', '2006', 'LEARNING LABORATORY', 'A laboratory to accompany PSYC 315. Three hours laboratory.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1166', '994179', 'PSYC', '318', '2006', 'PERCEPTION LABORATORY', 'A laboratory to accompany PSYC 316. Three hours laboratory.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1167', '905019', 'PSYC', '319', '2006', 'COMPARATIVE PSYCHOLOGY', 'The concepts of species- typical behavior and ontogenetic development as illustrated in organisms at various phyletic levels and the application of these ideas to the study of human behavior. Prerequisites: PSYC 201. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1168', '116760', 'PSYC', '325', '2006', 'HUMAN LEARNING AND COGNITION', 'Principles of knowledge acquisition and usage in humans. Emphasis on the structure, processes, and models of memory, attention, information processing, and the neural basis of cognition. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1169', '991577', 'PSYC', '401', '2006', 'ABNORMAL', 'Nature of mental and emotional disorders with their underlying determinants. Prerequisite: PSYC 201. F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14086', '394090', 'PSYC', '402', '2006', 'Social Psychology', 'Psychological factors and influences in social behavior; the individual in social situations; the influence on social environment in behavior and development. (Same as SOCL 402.) Prerequisites: PSYC 201, SOCL 101. F', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('1171', '35003', 'PSYC', '403', '2006', 'EXCEPTIONAL CHILDREN AND YOUTH', 'Legal definitions and clinical classifications of children and youth who vary from normal (mentally, physically, or emotionally). Emphasis on etiology, identification, and educational service provision in multicultural settings. Prerequisite: PSYC 201, 203. (Formerly The Exceptional Child.) Su', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1172', '335728', 'PSYC', '405', '2006', 'MENTAL RETARDATION', 'Recognition, diagnosis, and etiology of mental retardation. Emphasis on psychological theories, procedures, and application. Prerequisites: PSYC 201, 203. (Formerly The Mentally Retarded Child.) F, Su', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1173', '346344', 'PSYC', '406', '2006', 'PERSONALITY', 'Development, dynamics, and processes in the structure of personality. Prerequisite: PSYC 201. Students with prior credit in PSYC 427 are not eligible to receive credit in PSYC 406. F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1174', '979773', 'PSYC', '407', '2006', 'HISTORY AND SYSTEMS', 'The evolution of modern psychology from its historical origins in philosophy and the natural sciences to contemporary systems and theory. Prerequisites: PSYC 201 and six hours of psychology above 300. F, Sp, Su', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1175', '91469', 'PSYC', '410', '2006', 'HEALTH PSYCHOLOGY', 'Behavioral factors in physical illness and maintenance of health; including an examination of research issues, behavioral techniques in treatment, intervention and preventative medicine strategies. Prerequisites: PSYC 201, BIOL 226. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1176', '477469', 'PSYC', '411', '2006', 'ADVANCED TOPICS', 'A study of theoretical issues in experimental and applied areas of psychology. Prerequisite: 12 hours of psychology. F, Sp, Su', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1177', '75471', 'PSYC', '415', '2006', 'INDUSTRIAL', 'Application of psychological principles and techniques to problems of industry, with emphasis on employee selection and placement, merit rating, accident reduction, training, measurements, and improvement of morale. Prerequisite: PSYC 201.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1178', '639249', 'PSYC', '425', '2006', 'PSYCHOLOGY OF ADULTHOOD AND AGING', 'Study of physical, intellectual, personality, social and emotional development during the middle and older years of the life span. Su', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1179', '73989', 'PSYC', '427', '2006', 'THEORIES OF PERSONALITY', 'Survey of the dominant personality theories. Prerequisite: nine hours of psychology. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1180', '298623', 'PSYC', '439', '2006', 'STATISTICS', 'Statistical techniques needed in the study, interpretation, and preparation of educational and psychological research. F, Sp ,Su', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1181', '969405', 'PSYC', '441', '2006', 'PSYCHOLOGY OF CHILD ABUSE', 'Identification, treatment, and prevention of child abuse and neglect. F, Sp, Su', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1182', '97725', 'PSYC', '450', '2006', 'EVOLUTIONARY PSYCHOLOGY', 'Evolutionary origins of behavior are integrated into a framework of ontogeny and physiology, delineating a modern adaptationist approach to psychology. Prerequisites: PSYC 201, BIOL 226 or equivalent.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1183', '912224', 'RADT', '101', '2006', 'INTRODUCTION', 'An introduction to radiologic technology including historical aspects, orientation to the program and curriculum objectives. F, Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1184', '163027', 'RADT', '204', '2006', 'CLINICAL RADIOGRAPHY ORIENTATION', 'Analysis of the health care delivery system including professional development, responsibilities, and principles of clinical radiography. Prerequisite: RADT 101. Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1185', '49576', 'RADT', '303', '2006', 'RADIOGRAPHIC PROCESSING TECHNIC', 'The fundamentals of processing technics: chemistry, equipment, and methods of automatic processing. Prerequisite: RADT 204. F', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1186', '62941', 'RADT', '309', '2006', 'Methods of Patient Care', 'General patient care, principles of body mechanics, and specific nursing procedures related to radiologic technology. Prerequisite: RADT 204. F', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1187', '132007', 'RADT', '313', '2006', 'RADIOGRAPHIC PROCEDURES', 'The principles and skills involved in routine radiographic positioning to demonstrate the extremities and associated girdles. Prerequisites: RADT 204; BIOL 114, 115, 116, 117, and 214 or 228; registration in RADT 314. F', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1188', '432631', 'RADT', '314', '2006', 'RADIOGRAPHIC PROCEDURES LABORATORY', 'A laboratory course to accompany 313. Prerequisite: Registration in RADT 313. F', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1189', '353437', 'RADT', '315', '2006', 'CLINICAL RADIOGRAPHY', 'Sixteen hours per week of supervised clinical experience in an approved medical center. Prerequisites: Credit or registration in RADT 204, 303, 309, 313, 314. F', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1190', '887663', 'RADT', '320', '2006', 'RADIOGRAPHIC EXPOSURE', 'Radiographic exposure principles: prime factors, factors influencing radiographic quality, beam restricting devices and attentuating devices. Prerequisite: RADT 303. Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1191', '11181', 'RADT', '321', '2006', 'RADIOGRAPHIC EXPOSURE LABORATORY', 'A laboratory course to accompany 320. Prerequisite: Registration in RADT 320. Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1192', '202574', 'RADT', '322', '2006', 'RADIOGRAPHIC PROCEDURES', 'The principles and skills involved in routine radiographic positioning to demonstrate the vertebral column, skull, thoracic cavity, abdominal cavity and glands. Prerequisite: RADT 313, 314. Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1193', '672601', 'RADT', '323', '2006', 'RADIOGRAPHIC PROCEDURES LABORATORY', 'A laboratory course to accompany 322. Prerequisite: Registration in RADT 322. Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1194', '560593', 'RADT', '324', '2006', 'CLINICAL RADIOGRAPHY', 'Sixteen hours per week of supervised clinical experience in an approved medical center. Prerequisites: Credit or registration in RADT 315, 320, 321, 322, 323, 325. Sp', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1195', '7941', 'RADT', '327', '2006', 'CLINICAL RADIOGRAPHY', 'Thirty-five hours per week of supervised clinical experience in an approved medical center. Prerequisite: RADT 324. S', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1196', '378640', 'RADT', '403', '2006', 'RADIOGRAPHIC EXPOSURE', 'Advanced theories of radiographic exposure including problem solving and the practical application of technic principles. Methods of generator and x-ray equipment quality assurance. Prerequisite: RADT 320, 321. F', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1197', '554773', 'RADT', '405', '2006', 'RADIOBIOLOGY', 'Fundamentals of radiation biology with special emphasis on clinical aspects, including dosage, common pathologies, and protective measures. Prerequisite: RADT 325. Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1198', '912960', 'RADT', '409', '2006', 'ADVANCED RADIOGRAPHIC PROCEDURES', 'Advanced principles and skills involved in special views of all body systems. Introduction to cross-sectional anatomy as applied to US, CT, and MRI. Prerequisite: RADT 322, 323. F', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1199', '495400', 'RADT', '410', '2006', 'ADVANCED PROCEDURES LABORATORY', 'A laboratory course to accompany 409. Prerequisite: Registration in RADT 409. F', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1200', '546350', 'RADT', '413', '2006', 'ADVANCED CLINICAL RADIOGRAPHY', 'Twenty-one hours per week of supervised clinical experience in an approved medical center. Prerequisites: Credit or registration in RADT 328, 403, 405, 409, 410, 419. F', '6', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1201', '307051', 'RADT', '424', '2006', 'DIAGNOSTIC IMAGING', 'The fundamentals of various imaging modalities including image intensification, recording media, ultrasound, computerized transaxial tomography, digital radiography and MRI. Prerequisites: Credit or registration in RADT 325 and 403. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1202', '530403', 'RADT', '425', '2006', 'RADIOLOGIC OPERATIONS', 'Principles and problems in administrative functions, supervision of radiology departments, personnel guidance, productivity and budget. Prerequisite: Senior standing. Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1203', '882078', 'RADT', '428', '2006', 'SPECIAL RADIOGRAPHIC PROCEDURES', 'Principles and skills involved in special procedures using contrast media and supplementary radiographic procedures. Procedure practice in a clinical situation with direct and indirect supervision. Prerequisite: RADT 409, 410. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1204', '653394', 'RADT', '432', '2006', 'ADVANCED CLINICAL RADIOGRAPHY', 'Twenty-one hours per week of supervised clinical experience in an approved medical center. Prerequisites: Credit or registration in RADT 413, 420, 424, 428. Sp', '6', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1205', '510176', 'READ', '321', '2006', 'READING IN THE PRIMARY GRADES', 'Materials and methods for beginning reading instruction. Must be admitted to Teacher Education. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1206', '973546', 'READ', '418', '2006', 'TEACHING READING IN THE CONTENT AREAS', 'A study of the methods, materials, and practices which contribute to making an effective application of reading skills in the subject matter areas. A. For Secondary Teachers; B. For Elementary Teachers. Prerequisite: Approval of instructor.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1207', '130863', 'READ', '419', '2006', 'SURVEY OF READING', 'Basic course in teaching reading; introduction to methods and materials; and a survey of contemporary problems. Prerequisite: Approval of instructor.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1208', '585647', 'READ', '420', '2006', 'TEACHING READING TO ADULTS', 'Procedures and materials currently in use for teaching reading to adults.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14107', '850025', 'RELG', '309', '2006', 'Islam', 'Survey of Islamic history from the inception in 7th century Arabia to the present day. Special emphasis on the religious principles and the institutions of the civilizations to which it gave rise. Attention is given to those factors that continue to make Islam an important force in the world today. (Same as HIST 309.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('1210', '468810', 'SCIE', '103', '2006', 'INTEGRATED BIOLOGICAL SCIENCES', 'A course for non- science majors designed to focus on basic concepts and principles of the biological sciences. Emphasis is on the history and methods of biological science, basic chemistry and physiology of living systems, ecological principles and related environmental issues, and biodiversity. Prerequisites: SCIE 101 and 102. For Elementary Education Majors only.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1211', '884269', 'SOCW', '101', '2006', 'INTRODUCTION TO SOCIAL WORK', 'The historical development of the social work profession to the present. The knowledge, values, and skills required for the profession are identified. The role of social workers in various fields of social work practice is described.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1212', '582202', 'SOCW', '102', '2006', 'SOCIAL WELFARE AS AN INSTITUTION', 'The structure and function of public and private agencies that have been developed to help meet human needs. This course will integrate society’s values with program services provided to prevent or alleviate social problems experienced by individuals, groups and communities.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1213', '302576', 'SOCW', '205', '2006', 'HUMAN BEHAVIOR AND SOCIAL ENVIRONMENT I', 'An introduction to basic principles and fundamental concepts necessary for acquiring and organizing knowledge about human behavior and the social environment. Concentration on the stages of individual development from birth to old age is emphasized. Prerequisites: SOCW 101 and 102.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1214', '796494', 'SOCW', '301', '2006', 'INTERVIEWING AND THE HELPING RELATIONSHIP', 'Develops theoretical knowledge and practical skills in interviewing, and recording by studying concepts, principles, and techniques in the classroom and by applying them in role-play interview situations. Students are required to volunteer 30 clock hours in an agency approved by the classroom instructor. Prerequisites: SOCW 101, 102, and 205.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1215', '745228', 'SOCW', '303', '2006', 'SOCIAL POLICY AND SOCIAL WORK', 'Learning to identify and analyze the processes of social policy decision-making in the social, political, and economic arenas. Examines societal values and ideologies which relate to the development of social policies and programs. Students are required to volunteer 30 clock hours in an agency approved by the classroom instructor. (Formerly 203.) Prerequisites: SOCW 101, 102.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1216', '352151', 'SOCW', '305', '2006', 'HUMAN BEHAVIOR AND SOCIAL ENVIRONMENT II', 'An introduction to knowledge, theories, and concepts about human behavior from the perspective of small groups, organizations, and communities. Prerequisite: SOCW 205 and Social Work major; for non-majors, junior level standing and approval of social work program head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1217', '859435', 'SOCW', '315', '2006', 'PRACTICE I', 'The development of skills necessary for generalist social work practice with individuals, families, and small groups. Utilization of a systems approach to problem solving and an introduction to strategy selection are covered. Prerequisites: SOCW 205 and 301.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1218', '877235', 'SOCW', '317', '2006', 'GROUP WORK', 'Examines various theoretical approaches to social work practice with groups, basic skills involved in leadership and group development, and the fundamentals of the dynamics and processes in social group work. Prerequisite: SOCW 301 or approval of program director.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1219', '784782', 'SOCW', '319', '2006', 'CHALLENGES AND SERVICES RELATED TO AGING', 'Examines the challenges people experience in relation to or as a consequence of aging, and the resources available to deal with specific problems.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1220', '212872', 'SOCW', '404', '2006', 'SOCIAL AND EMOTIONAL ASPECTS OF DISABILITIES', 'The course examines physical and developmental disabilities from four basic perspectives using an interdisciplinary ecological approach: (1) physical and medical aspects of disabilities; (2) psychosocial and emotional aspects of disabilities; (3) micro and macro-level intervention approaches and (4) advocacy, policy- making, and legislative efforts by and on behalf of people with disabilities at the local, state, and national levels.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1221', '764899', 'SOCW', '416', '2006', 'PRACTICE II', 'Generalist social work theory and its application at the macro level of practice. This course will focus on the application of social work skills of assessment and problem- solving at the organizational and community level. To be taken concurrently with Social Work 421 and 422.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1222', '795963', 'SOCW', '421', '2006', 'FIELD EDUCATION IN SOCIAL WORK', 'Supervised practice in an approved social work agency for four full days weekly. Fall or Spring enrollment. Application must be made in the first month of the semester prior to enrolling in Field Work. Prerequisites: Completed all 100 and 200 level courses, earned a grade of C or higher in all courses required for a major, and earned a GPA of 2.3 in all courses required for the degree. During enrollment in Field Education, the student will enroll in Social Work 416 and 422. No more than one additional course may be taken during this semester. (For a total of 17 hours.)', '8', '8', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1223', '415445', 'SOCW', '422', '2006', 'FIELD EDUCATION SEMINAR', 'To be taken by all students enrolled in SOCW 421, FIELD EDUCATION IN SOCIAL WORK. The course content is integrated with the field practicum experience. This is a capstone course addressing materials covered in all social work foundation areas from the perspective of a generalist social work practitioner. Emphasis is on value and ethics, diversity, research, social and economic justice, and populations-at-risk. The formal evaluation of social work students is conducted via an exit exam and an assessment of written and oral communication skills.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1224', '437501', 'SOCW', '401', '2006', 'THE CHILD AND THE COMMUNITY', 'Examines the common and particular needs of children in the community, social welfare services and resources developed by communities for the care and training of children.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1225', '356557', 'SOCW', '403', '2006', 'FAMILY VIOLENCE AND SOCIAL WORK PRACTICE', 'Identifies the four major kinds of violence with families; assesses the dynamics and complex issues associated with family violence and their implications for the social worker; looks at current local, state, and national efforts to resolve the problem. Emphasis is on an overview of treatment and prevention programs for victims and perpetrators of domestic violence.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1226', '423385', 'SOCW', '405', '2006', 'DIFFERENTIAL DIAGNOSIS', 'An organized introduction to and synopsis of the criteria for the individual diagnoses in the DSM-IV as it relates to the functionality of human behavior in the context of diverse social systems. Prerequisites: PSYC 201 and 401.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1227', '816141', 'SOCW', '406', '2006', 'CHILD ABUSE AND NEGLECT', 'Defines the abused and neglected child and family and examines the dynamics associated with dysfunctional families. (May not receive credit for 406 and 408.)', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1228', '911329', 'SOCW', '410', '2006', 'HUMAN SERVICES IN A RURAL ENVIRONMENT', 'An examination of human services in a rural environment, the social and cultural factors affecting the delivery system, and the skills and resources needed by the helping professional to be more effective in working with the rural individual, family, group, and community.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14332', '700716', 'SOCW', '411', '2006', 'Social Work Practice With The Elderly', 'An introduction to the social, economic, and psychological dimensions of aging using a person-in-environment approach. (Same as GERO 411.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('1230', '311541', 'SOCL', '101', '2006', 'INTRODUCTION', 'A survey of the basic concepts of sociology and techniques of sociological research; factors and principles influencing the social life of man. (Same as SOCL 102 and 300C.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1231', '457680', 'SOCL', '102', '2006', 'INTRODUCTION: AN INTERNATIONAL PERSPECTIVE', 'A survey of the basic concepts of sociology and techniques of sociological research; factors and principles influencing the social life of man. This course examines these factors from an international perspective. (Same as SOCL 101 and 300C.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1232', '643396', 'SOCL', '203', '2006', 'SOCIAL PROBLEMS', 'Individual, family, and community disorganization. Attention is given to social planning, reconstruction, and problems of minorities.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14041', '235790', 'SOCL', '226', '2006', 'Perspectives On Aging', 'An orientation from practitioners in the community and from an academic interdisciplinary perspective to programs, services, issues, and problems in working with older persons. (Same as GERO 226, formerly SOCL and GERO 426.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('1234', '339759', 'SOCL', '310', '2006', 'SOCIOLOGY IN FILM', 'An examination of sociological concepts and themes in American and foreign popular and educational films. A $5 fee will be assessed to cover partial cost of film rental.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1235', '296790', 'SOCL', '436', '2006', 'SEMINAR IN THE DEVELOPMENT OF SOCIOLOGY', 'The study of social thought that influenced sociological ideas and concepts. Recent trends and influences in modern sociology will be discussed.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14389', '736722', 'SOCL', '404', '2006', 'Medical Sociology', 'An analysis of human adaptation to illness, the sick role, social organization of medical institutions, and the sociology of death.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14092', '120221', 'SOCL', '405', '2006', 'Theories Of Crime And Delinquency', 'An analysis of the extent of crime and delinquency; impact of crime and elinquency on society; physical, social, and psychological causation of crime and delinquency; and current issues in criminology. (Same as CJUS 405.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('1238', '427652', 'SOCL', '406', '2006', 'RELIGION AND SOCIETY', 'A study of structure and functions of religion and its effect on society, culture, and personality, a comparative analysis of religion; and an examination of religion in American based on sociological research. (Formerly Sociology of Religion.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1239', '937766', 'SOCL', '412', '2006', 'MINORITY GROUPS', 'An analysis of the structure, culture, and social processes of ethnic, racial, social and religious groups in America. (May be offered in non-traditional modes.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14098', '942830', 'SOCL', '415', '2006', 'Juvenile Delinquency And Justice', 'The nature, extent, and causes of juvenile delinquency; factors in its treatment and prevention, including community and agency programs; and analysis of the legal system as it applies to juveniles. (Same as CJUS 415.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('1241', '632503', 'SOCL', '419', '2006', 'THE FAMILY', 'The past and present role of the family as the primary social group in our culture; problems facing the modern family in a changing social world. (Same as SOCL 300D.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14053', '277033', 'SOCL', '424', '2006', 'Women And Aging', 'Examines the life experiences of women as they age. Emphasis is on sociological influences on perceptions of aging among women and on aging women’s roles in society. (Same as GERO 424.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14059', '110129', 'SOCL', '425', '2006', 'Seminar In Death And Dying', 'Exploration of the social organization of dying, including a view on cross-cultural perspectives on death, treatment of the dying patient, and the grief process. (Same as GERO 425)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('1245', '618530', 'SOCL', '443', '2006', 'SOCIOLOGY OF SPORTS, RECREATION, AND LEISURE', 'The structure and functions of sports and recreation in fulfilling leisure pursuits in American society.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1246', '253532', 'SOCL', '447', '2006', 'REGIONAL UNITED STATES FIELD STUDIES', 'Extended thematic field study within the United States with guest lectures, tours, and assigned readings. A. North, B. South, C. East, D. West. Individual topics may not be repeated for credit.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('13250', '971043', 'SOCL', '450', '2006', 'Social Research', 'The formulation of a research problem; ethics of research; development of research design; sampling procedures; collecting and processing of research data.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14429', '207459', 'SOCL', '451', '2006', 'Social Statistics', 'Statistical procedures appropriate to the measurement and analysis of social data. Prerequisite: Approval of department head.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('1249', '130768', 'SPAN', '101', '2006', 'ELEMENTARY', 'An introduction to the basic grammatical structures of Spanish with the objectives of developing an elementary ability to speak, understand, read, and write the language.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1250', '72013', 'SPAN', '102', '2006', 'ELEMENTARY', 'A continuation of the study of Spanish on the elementary level.Prerequisite: A &quot;C&quot; or better in SPAN 101 or permission of the department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1251', '690837', 'SPAN', '104', '2006', 'SPANISH FOR PROFESSIONS', 'An introduction to the vocabulary, terminology, and structures for developing basic Spanish skills for professions. Prerequisite: SPAN 102 or equivalent. (May not be used as part of a major or minor in Spanish.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1252', '895666', 'SPAN', '210', '2006', 'COMMERCIAL SPANISH', 'Readings, discussions and translations of business materials in Spanish with continued emphasis on reading, writing, speaking, listening and translation skills. Prerequisite SPAN 202 or 205.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1253', '867975', 'SPAN', '231', '2006', 'GRAMMAR AND COMPOSITION', 'A review and drill of the elements of Spanish grammar and syntax with special emphasis on developing the ability to organize and to write correctly and fluently in Spanish. Prerequisite: SPAN 202 or 205.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1254', '106282', 'SPAN', '301', '2006', 'SURVEY OF SPANISH AMERICAN LITERATURE', 'An introduction to the outstanding writers of Spanish America from pre-Colombian to modern times. Prerequisite: SPAN 202, 205 or permission of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1255', '333167', 'SPAN', '311', '2006', 'PHONETICS', 'An introduction to the Spanish phonetic system with emphasis on pronunciation practice in the language learning facility. Prerequisite: SPAN 102 or the equivalent.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1256', '224533', 'SPAN', '312', '2006', 'CULTURE AND CIVILIZATION OF MEXICO', 'Advanced studies in the history and culture of Mexico with continued emphasis on understanding, speaking, reading, and writing. Prerequisite: SPAN 202 or 205.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1257', '529667', 'SPAN', '313', '2006', 'CULTURE AND CIVILIZATION OF SPAIN', 'Advanced studies in the history and culture of Spain with continued emphasis on understanding, speaking, reading, and writing. Prerequisite: SPAN 202 or 205.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1258', '149308', 'SPAN', '314', '2006', 'CULTURE AND CIVILIZATION OF LATIN AMERICA', 'Advanced studies in the history and culture of Latin America with continued emphasis on understanding, speaking, reading, and writing. Prerequisite: SPAN 202 or 205.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1259', '135862', 'SPAN', '415', '2006', 'CONTEMPORARY SPANISH LITERATURE', 'Extensive reading of authors of the Generation of 1898 and of the Twentieth Century. Prerequisite: SPAN 202 or 205.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1260', '866012', 'SPAN', '474', '2006', 'SECOND LANGUAGE ACQUISITION', 'The application of second language acquisition theory in the classroom. Prerequisite: Spanish/French 473.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1261', '460849', 'SPED', '202', '2006', 'SURVEY OF THE EDUCATION OF CHILDREN WITH SPECIAL N', 'A survey of the cause, evaluation, education and prevention of children with special needs. (10 observation hours required)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1262', '593542', 'SPED', '463', '2006', 'STUDENT TEACHING FOR SPECIAL EDUCATION', 'Directed observation and participation in special education situations. Must be admitted to Teacher Education.', '6', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1263', '509408', 'SPED', '464', '2006', 'STUDENT TEACHING FOR SPECIAL EDUCATION', 'Eighteen weeks directed observation and participation in special education. Must be admitted to Teacher Education.', '9', '9', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1264', '302793', 'SPCH', '100', '2006', 'FOUNDATIONS OF SPEECH COMMUNICATION', 'Experiential learning approach to special problems in communication: articulation, speech anxiety, interviews, presentational skills, and related concerns. This course does not replace or substitute for a required course.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('13229', '933300', 'SPCH', '101', '2006', 'Fundamentals', 'Focus is on the total range of communication behaviors we engage in within the contexts of intrapersonal, interpersonal, small group, organizational, public speaking, and mass communication.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13235', '806197', 'SPCH', '102', '2006', 'Voice And Diction', 'A non-technical approach to developing expressive speech. Emphasis is on phonetics, the mechanics of vocal sound, and oral readings.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('1267', '386848', 'SPCH', '110H', '2006', 'HONORS SPEECH COMMUNICATION', 'Speech taught more as a liberal art than as a technical art. Emphasis on developing effective rhetorical skills, problem-solving, critical thinking, and the place of values in establishing humane goals. Presentations required. Prerequisite: Admission to the Honors Program or permission of the Undergraduate Honors Council. Substitutes for either Speech 101 or 201.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('13253', '246606', 'SPCH', '118', '2006', 'Interpersonal Communication', 'Stresses practical application of communication concepts and skills. Emphasis on self-concept, language, perception, listening, nonverbal communication, and resolution of interpersonal skills development.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13259', '233850', 'SPCH', '201', '2006', 'Public Speaking', 'The process of public communication, including an understanding of audience, to produce, evaluate, and critique public expression in public arenas.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13265', '592679', 'SPCH', '260', '2006', 'Small Group Communication', 'Introduction to small group participation and leadership. Small group variables included for study are group structure, climate, goals, roles, norms, conflict, and rhetorical sensitivity. Special emphasis given to application of concepts within problem-solving discussion groups.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13271', '281264', 'SPCH', '304', '2006', 'Intercultural Communication', 'A study of communication principles between members of different cultures. Distinctions among verbal and non-verbal code systems regarding various aspects of culture will be examined. Prerequisite: Speech 101, 118, or consent of instructor. May not be used as a substitute for a foreign language.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13277', '866353', 'SPCH', '305', '2006', 'Oral Interpretation', 'Oral reading of prose and poetry with emphasis on determining and interpreting the thought and emotional content of selections of literature.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13289', '414715', 'SPCH', '311', '2006', 'Argumentation And Debate', 'Argumentation as a method for critical inquiry. Emphasis on debate as argument exemplar with instruction in analysis, reasoning, and use of evidence. Participation in debates. (Formerly Speech 311-Debate.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13295', '681011', 'SPCH', '325', '2006', 'Special Topics', 'Study of various communication contexts, problems, and issues. A. Rhetoric and Public Address; B. Forensics; C. Interpersonal Communication; D. Organizational Communication; E. Communication Theory; F. Instructional Communication. Prerequisite: SPCH 101, 118, or 201 or consent of Department Head.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13301', '801432', 'SPCH', '330', '2006', 'Persuasion', 'A review of general theoretical approaches to persuasion and influence with focus on principles of persuasive communication and application of persuasive strategies. Prerequisite: SPCH 101 or 201 or consent of instructor. (Formerly Speech 230 - Persuasion.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13307', '33246', 'SPCH', '335', '2006', 'Nonverbal Communication', 'The process of generating and interpreting messages from such nonverbal sources as space, physical appearance, time, facial expressions, and other nonverbal codes explored for effects on interpersonal relationships. Prerequisites: SPCH 101, 118, or 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13313', '934543', 'SPCH', '341', '2006', 'Communication And Gender', 'Exploration of how gender and communication interact, exploring how gender influences communication, how gender is communicated, and how communication influences our understandings of gender and what it means to be gendered beings. Prerequisites: SPCH 101, 118, or 304 or approval of the instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13319', '88663', 'SPCH', '344', '2006', 'Organizational Communication', 'An introduction to the principles and theories of competent organizational communication. You will learn about the research and practice in this field.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13343', '511210', 'SPCH', '450', '2006', 'Communication Theory', 'An analysis and evaluation of predominant communication theories that identify fundamental communicative forces in human action. Prerequisite: SPCH 101, 330, or approval of instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13355', '65311', 'SPCH', '452', '2006', 'Criticism Of Contemporary Communication', 'This course examines various aspects of contemporary communication. Prerequisites: SPCH 101, 201, or approval of the instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13325', '160675', 'SPCH', '405', '2006', 'Advanced Public Communication', 'Preparation and delivery of the principal types of speeches, with some attention to the miscellaneous types of speeches; rhetorical criticism. An effort is made to arrange outside speaking engagements for members of the class.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13331', '202048', 'SPCH', '420', '2006', 'Performance Studies', 'Theory and practice in the art of group oral presentation of literature; selection, analysis, and arrangement of materials for oral interpretation; program planning for public presentation; emphasis on Reader’s Theatre. Prerequisite: SPCH 305 or consent of instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13349', '28651', 'SPCH', '451', '2006', 'American Public Address', 'Provides a study of American public address from colonial times to the present. Prerequisite: SPCH 101, 201, or approval of instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13361', '308329', 'SPCH', '453', '2006', 'Rhetoric Of Social Movements', 'This course examines social movement. Prerequisite: SPCH 101, 201, or approval of instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('1286', '294145', 'THEA', '101', '2006', 'PLAY READING I', 'Reading and discussing 15 plays by contemporary American playwrights. F', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1287', '253183', 'THEA', '102', '2006', 'PLAY READING II', 'Reading and discussing 15 plays by contemporary non-American playwrights. Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1288', '837996', 'THEA', '110', '2006', 'THEATRE FUNDAMENTALS: SKILLS', 'Teaches the basic skills required to function in a production company: Production Organization, Scene Shop Skills, Property Operations, Electrical Operations, Scene Painting, Sound Operations, Costume Shop Operations, Wardrobe Operations, and Make-up Operations. F', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1289', '443453', 'THEA', '111', '2006', 'THEATRE FUNDAMENTALS: AESTHETICS', 'Teaches fundamentals of design theory, stage movement and dramatic theory to include genres and styles. Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1290', '389045', 'THEA', '191', '2006', 'ENJOYING THEATRE', 'Seeks to improve appreciation and understanding of theatre art to include playwriting, acting, directing, designing (scenery, lighting, costumes and make-up) and managing. The course gives emphasis to critical observation and thinking skills. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1291', '704008', 'THEA', '201', '2006', 'PLAY READING III', 'Reading and discussing 15 plays by Modern American playwrights. F', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1292', '904301', 'THEA', '202', '2006', 'PLAY READING IV', 'Reading and discussing 15 plays by Modern European playwrights. Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1293', '982587', 'THEA', '207', '2006', 'MAKE-UP', 'Teaches basic skills of make-up design and application for straight and character roles in theatre, television and film. Selected special applications are taught. Laboratory hours on TheatreWorks.ULM productions and student make-up kit are required. F, Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1294', '213417', 'THEA', '221', '2006', 'BEGINNING ACTING', 'Teaches the basic skills of memorization, relaxation, concentration, physicalization, vocal control and script interpretation through exercises and scene study.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1295', '207095', 'THEA', '301', '2006', 'SCENIC CONSTRUCTION', 'Teaches principles and techniques for the building, rigging and shifting of scenery through study, exercises and application. Laboratory hours on TheatreWorks.ULM productions required.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1296', '915888', 'THEA', '303', '2006', 'LIGHTING', 'Teaches theory, design and practice of lighting for theater, television and film. Laboratory hours on Theatre Works.ULM productions required.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1297', '19614', 'THEA', '309', '2006', 'PLAYWRIGHTING', 'Teaches the writing of short plays through theory, example and practice.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1298', '862006', 'THEA', '360', '2006', 'COSTUME CONSTRUCTION', 'Teaches pattern drawing, costume alteration and sewing for theatre, television and film through application projects. Laboratory hours on TheatreWorks.ULM productions required.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1299', '162495', 'THEA', '487', '2006', 'SENIOR RECITAL', 'Provides a culminating project for performance students to demonstrate their proficiency. Prerequisite: Permission of the Director of Theater.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1300', '253464', 'THEA', '401', '2006', 'COSTUME DESIGN', 'Teaches application of art fundamentals and historical research to the creation of costumes for theatre, television and film through drawing and rendering projects. Laboratory hours on TheatreWorks.ULM productions required. Prerequisite: THEA 360 or permission of instructor.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1301', '454686', 'THEA', '409', '2006', 'DRAMATIC LITERATURE, THEORY, AND CRITICISM', 'A study of the developments in Western dramatic theory, literature, and criticism from the ancient Greeks to the present.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1302', '335202', 'THEA', '419', '2006', 'CHILDREN’S THEATRE', 'Teaches the selection, direction of plays for child audiences.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1303', '119817', 'THEA', '423', '2006', 'CREATIVE DRAMATICS', 'Teaches the use of imaginative and creative activities for instruction in college, secondary and elementary school programs.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1304', '869400', 'THEA', '425', '2006', 'DIRECTING', 'Teaches directorial skills in leadership, script selection and analysis, conduct of auditions, rehearsals and performance evaluation through workshop and application. Prerequisites: THEA 111, 221 and permission of instructor.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1305', '16214', 'THEA', '427', '2006', 'THEATRE LEADERSHIP AND MANAGEMENT', 'House and box office management, public relations and program development for arts production.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1306', '921248', 'THEA', '431', '2006', 'SUMMER THEATRE PRODUCTION', 'Provides practical application of theatre skills in a summer theatre environment. Student’s participation is determined through audition and application process. Prerequisite: Permission of instructor.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1307', '902645', 'TOXI', '101', '2006', 'TOXICOLOGY AND THE ENVIRONMENT', 'This class will provide an introduction to the field of toxicology and illustrate the relationships between chemicals, man and the environment. Historical and contemporary &quot;case studies&quot; of contamination episodes will be used to highlight the various factors involved in evaluating and controlling chemical hazards. F', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1308', '950756', 'TOXI', '201', '2006', 'ENVIRONMENTAL TOXICOLOGY', 'This course will encompass both the fate and effects of chemicals in the environment, with emphasis in aquatic and terrestrial ecosystems. Potential sources, routes of exposure, and physical, chemical, and biological factors important with the movement of contaminants within various components of the ecosystems will be examined. In addition, emerging environmental regulations, issues, and case studies will be discussed. Prerequisites: BIOL 120, 121; CHEM 107, 109. Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14174', '240972', 'TOXI', '326', '2006', 'Clinical Chemistry And Toxicology', 'Principles of basic clinical chemistry and toxicology procedures applied to enzymes; endocrine function; electrolytes and blood gases; renal function; gastric, pancreatic and intestine function; biochemistry of pregnancy; therapeutic drug monitoring and analysis of toxic substances. Prerequisite: CLSC 322, 323, or approval of the Program Director. (Same as CLSC 326.) Toxicology majors only.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('1310', '413856', 'TOXI', '401', '2006', 'GENERAL LABORATORY TECHNIQUES', 'This class is designed to provide the student with basic skills required to work in a toxicology laboratory. Prerequisite: Consent of instructor. Toxicology majors only.', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1311', '16068', 'TOXI', '412', '2006', 'GENERAL TOXICOLOGY', 'Continuation of Toxicology 411. Physiological basis of chemical toxicity in organ systems. Prerequisites: TOXL 411; BIOL 311, 312; grade of &quot;C&quot; or better in Toxicology 101 and 201; credit or registration in 414. Toxicology majors only. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1312', '170586', 'TOXI', '416', '2006', 'ANALYTICAL TOXICOLOGY LABORATORY', 'Laboratory exercises to accompany 415. Six hours laboratory. Prerequisite: credit or registration in 415. Toxicology majors only.', '2', '2', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14177', '615903', 'TOXI', '424', '2006', 'Clinical Toxicology', 'Information on the clinical management of poisonings including the assessment of toxic potential, stabilization of vital function, and specific antidotal measures. Prerequisite: Credit or registration in TOXI 412, 414. F (Same as PHARM 524).', '2', '2', '2', '0', '', '0');
INSERT INTO `courses` VALUES ('1314', '977044', 'TOXI', '443', '2006', 'INDUSTRIAL HYGIENE LABORATORY', 'A laboratory dedicated to the practical application of information disseminated in Toxicology 444. Three hours laboratory. Prerequisite: credit or registration in 442. Toxicology majors only. Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1315', '199237', 'TOXI', '444', '2006', 'INDUSTRIAL HYGIENE', 'Introduction to industrial hygiene: general principles, regulatory standards, workplace hazards, exposure control, problem solving strategies. Prerequisites: TOXI 101, 201. Toxicology majors only. Sp', '3', '3', '0', '0', '', '0');
