INSERT INTO `courses` VALUES ('1751', '374506', 'MUSC', '375', '2006', 'SIXTEENTH CENTURY COUNTERPOINT', 'Composition of two, three, and four-voice motets and a technical study of related choral tradition, with the liturgical literature of the Renaissance and Gregorian chant as a frame of reference. More complex contrapuntal devices may be elected by students with adequate background. Class performance, analysis, and hearing of representative works are correlated with written work. Prerequisite: MUSC 204.', '2', '2', '0', '0', '', '0');
