INSERT INTO `courses` VALUES ('1762', '803074', 'MUSC', '461', '2006', 'KEYBOARD HARMONY & CREATIVE MUSICIANSHIP FOR PIANO', '1 credit, 2 credit maximum. A functional approach to keyboard skills for piano instructors. A combination of lab and lecture to include harmonization, chord voicing, transposition, modulation, improvisation, sight reading, and score reading. Development of proficiency in these skills, as well as an understanding of how they can be taught at different levels.', '1', '1', '2', '0', '', '0');
INSERT INTO `courses` VALUES ('1763', '179228', 'MUSC', '473', '2006', 'BAND ARRANGING', 'Scoring for woodwind, brass and percussion instruments and for full band. Transcribing from piano, organ and orchestral literature. Scoring for the marching band. Prerequisite: MUSC 204 and 205.', '3', '3', '0', '0', '', '0');
