INSERT INTO `courses` VALUES ('14128', '481715', 'MATH', '420', '2006', 'Introduction To Biostatistics I', 'An introduction to the discipline of statistics and its application to problems of biology, including human biology, medicine, and public health. Topics include Bayesian inferences, estimation techniques, hypothesis testing, goodness of fit, and analysis of variance. Prerequisite: MATH 114 or MATH 131 or permission of the department head and junior standing or above. (Same as Computer Science 420).', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('1766', '290538', 'MATH', '403', '2006', 'MATHEMATICAL STATISTICS', 'Probability, discrete and continous random variables, expected values and moments, special continous distributions, sampling, point estimation, multivariate normal, interval estimation, tests of hypothesis, regression and linear hypotheseis, experimental design models, sequential tests, and nonparametric methods.  Prerequisite: MATH 303.', '3', '3', '0', '0', '', '0');
