INSERT INTO `courses` VALUES ('1775', '927582', 'MGMT', '413', '2006', 'SUPPLY CHAIN MANAGEMENT', 'Management and integration of the supply chain over the entire channel of distribution. Proactive procurement and control of materials and services in a global environment. Topics: inventory control, quality control, production scheduling, materials handling, storage, contracting, make or buy analysis, value analysis, outsourcing, transportation, and salvage. Prerequisite: MGMT 301 or MRKT 301 or consent of instructor. F', '3', '3', '0', '0', '', '0');
