INSERT INTO `courses` VALUES ('1785', '989945', 'MRKT', '411', '2006', 'INTEGRATED MARKETING CAMPAIGNS', 'The study of integrated marketing communication campaign strategies used by businesses and non-profit organizations. Students develop advertisements, public relations releases, sales promotion tools, and direct marketing pieces to coincide with their campaign plan. Prerequisites: MRKT 301 and MRKT 407. F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1786', '89074', 'MRKT', '406', '2006', 'BUSINESS-TO-BUSINESS MARKETING', 'Strategies and tactics used in marketing complex technologies and services to industrial firms. Emphasis is given to value and vendor analysis as key elements in business-to-business marketing. Prerequisite: MRKT 301. F', '3', '3', '0', '0', '', '0');
