INSERT INTO `courses` VALUES ('1873', '14727', 'ASCI', '407', '2006', 'MEAT PROCESSING AND PRESERVATION', 'The factors involved in dressing percentage and output values of carcasses, cutting, curing, and handling of meat products. One hour lecture and four hours laboratory. Prerequisite: ASCI 101.', '3', '3', '0', '0', '', '0');
