INSERT INTO `courses` VALUES ('1875', '89049', 'ATMS', '103', '2006', 'BASIC METEOROLOGY LABORATORY I', 'A laboratory course designed to expose students to basic uses of meteorological data from the surface, upper air, radars, and satellites. Topics include surface and upper-air map analysis, fronts and cyclones, and severe weather.', '1', '1', '0', '0', '', '0');
