INSERT INTO `courses` VALUES ('1888', '68006', 'AVIA', '110', '2006', 'Primary Ground Instruction', 'Aircraft and principles of flight, aircraft systems and engines, weight and balance, and performance.  Airports, communications, air traffic control, meteorology, navigation, radio aids to navigation, aeronautical chartes, Aeronautical Information Manual, Federal Aviation Regulations, flight publications, and flight safety.', '3', '3', '3', '0', '', '0');
