INSERT INTO `courses` VALUES ('1890', '211952', 'AVIA', '301', '2006', 'AERODYNAMICS', 'Analysis of the physics of flight including the application of basic aerodynamics to the wing and airfoil and the analysis of lift and drag components relative to the wing platform and airplane performance. The application of aerodynamic effects of turbo jet engines involving the principles of propulsion. Prerequisite: private pilot certificate or approval of department head.', '3', '3', '0', '0', '', '0');
