INSERT INTO `courses` VALUES ('1898', '644546', 'BIOL', '114', '2006', 'Fundamentals of Anatomy and Physiology I', 'Introduction to anatomy and physiology, including cells, tissues, organs, and the integumentary, skeletal, muscular, and nervous systems. Non-majors only. Cannot be taken for credit if credit has been awarded for 311 or 312. (Formerly Zoology 111.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('1899', '963675', 'BIOL', '116', '2006', 'Fundamentals of Anatomy and Physiology Laboratory', 'A laboratory course to accompany 114. Two hours laboratory. Prerequisite: Credit or registration in 114. Non-majors only. (Formerly Zoology 113.)', '1', '1', '1', '0', '', '0');
INSERT INTO `courses` VALUES ('1900', '951639', 'BIOL', '240', '2006', 'HUMAN ANATOMY', 'Gross structure and physiology of the body with emphasis on the skeletal, muscular, nervous, and circulatory systems. Prerequisites: 120 and 121, or 226; cumulative GPA of 2.5 or higher with a minimum of &quot;C&quot; in 120 or 226; credit or registration in 241. (Formerly Zoology 240.) F', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1901', '929698', 'BIOL', '241', '2006', 'HUMAN ANATOMY LABORATORY', 'A laboratory course to accompany 240. Three hours of laboratory. Prerequisites: Cumulative GPA of 2.5 or higher with a minimum of a &quot;C&quot; in 120 or 226; credit or registration in 240. (Formerly Zoology 241.) F', '1', '1', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('1903', '518731', 'BIOL', '411', '2006', 'GENETICS AND SOCIETY', 'A survey of current topics in genetic research and recent advances in biotechnology, including implications of these discoveries. Prerequisite: Biology 120 and either 305 or permission of the instructor.', '3', '3', '0', '0', '', '0');
