INSERT INTO `courses` VALUES ('2046', '635458', 'ENTR', '435', '2006', 'FAMILY AND CLOSELY HELD BUSINESSES', 'Practical considerations associated with family businesses. Focus on unique opportunities and problems of managing family business through the business life cycle, such as founder stage, training children, bringing children into the business, and managing for succession. Prerequisite: Senior standing or consent of instructor. (Formerly MGMT 333.) F', '3', '3', '0', '0', '', '0');
