INSERT INTO `courses` VALUES ('2048', '651538', 'EQSC', '420', '2006', 'THERAPEUTIC EQUITATION', 'Individualized instruction on basic horsemanship as it relates to therapeutic horseback riding. Specific areas include safety, equipment, riding, anatomy/conformation, techniques of instruction, and familiarization of rider capabilities, limitations, and disabilities.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2049', '381929', 'ESLG', '092', '2006', 'INTERMEDIATE STRUCTURE', 'The second of two intermediate English structure courses for non-native speakers. Prerequisite: Minimum of &quot;C&quot; in ESL-082 or placement based on competency tests given by the Department of Foreign Languages. Students will receive a grade of A, B, C, or NC.', '5', '5', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2050', '242235', 'ESLG', '108', '2006', 'ADVANCED READING, WRITING, AND STRUCTURE', 'An advanced course in the study of the English language for nonnative speakers. This course deals with English structure, reading, and writing. Students will receive a grade of A,B,C, or NC. Prerequisite: Graduate standing and a minimum of &quot;C&quot; in ESLG 091 and 092 or placement based on English competency tests given by the Department of Foreign Languages.', '9', '9', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2051', '962331', 'FCSC', '336', '2006', 'HISTORY OF HOUSING AND INTERIOR DESIGN', 'Emphasis on designing with historical preservation in mind; survey of housing and furniture styles, historic wallpapers and textiles. Prerequisite: Junior standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2052', '710072', 'FCSC', '421', '2006', 'STUDENT TEACHING', 'Modern methods of teaching family and consumer sciences in the secondary schools; integrating philosophy of family and consumer sciences, principles of learning, student experiences, needs, and interests; cooperative planning and evaluation; professional ethics. Prerequisite: FCSC 313. Must be admitted to Teacher Education.', '12', '12', '0', '0', '', '0');
