INSERT INTO `courses` VALUES ('2054', '892978', 'FCSC', '439', '2006', 'Mentoring Children', 'Hands-on experience in working with children and families, research, intervention strategies, communication skills, planning activities and implementing mentoring programs. Open to majors and non-majors.', '3', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2055', '374124', 'FINA', '405', '2006', 'CASE PROBLEMS IN FINANCIAL MANAGEMENT', 'Case studies of financial topics; emphasis on sources and uses of funds, cost of funds, return on investments; analysis of financial statements. Prerequisite: 316. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2056', '382381', 'FINA', '406', '2006', 'FUTURES AND OPTIONS CONTRACTS', 'A seminar introducing the principles of valuation of futures and options instruments, contract design, hedging and speculative use. Emphasis on financial futures contracts and common stock put and call options. Prerequisites: 315 and QMDS 210. Sp', '3', '3', '0', '0', '', '0');
