INSERT INTO `courses` VALUES ('2079', '483105', 'GEOS', '491', '2006', 'RESEARCH', '1-6 credits; maximum, 6 credits. Research problems that can be completed in one semester. Three hours laboratory per week for each credit hour. A written proposal with a list of references must be submitted to and be approved by the supervising professor and department head. Results of work will be presented in a seminar. Prerequisites: Junior or senior standing in geology.', '1', '6', '6', '0', '', '0');
INSERT INTO `courses` VALUES ('14038', '447080', 'GERO', '226', '2006', 'Perspectives On Aging', 'An orientation from practitioners in the community and from an academic interdisciplinary perspective to programs, services, issues, and problems in working with older persons. (Same as SOCL 226, formerly SOCL and GERO 426.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('2081', '935926', 'GERO', '430', '2006', 'ELDER ABUSE', 'An examination of the definition, causes, and theories associated with the problem of elder abuse. Relevant research regarding the historical perspectives, recognition of abuse, assessment techniques, and interventions will be presented.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2082', '239760', 'GERO', '475', '2006', 'TEACHING CHILDREN ABOUT AGING', 'An evaluation of pre-school, elementary and secondary school literature, class projects, video tapes and feature films that focus on various issues related to aging. The focus is to identify methods and mechanisms that enhance children’s images of older persons and growing older. (Formerly SOCL 495A.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('13085', '618202', 'GOVM', '205', '2006', 'Introduction To Paralegal Studies', 'Students are introduced to the structure and workings of the law firm, especially the important aspects of law office practices, which include investigation and research techniques.', '3', '3', '3', '0', '', '0');
