INSERT INTO `courses` VALUES ('2100', '142911', 'HIST', '201', '2006', 'UNITED STATES HISTORY', 'Survey of the political, economic, social, and cultural foundations of American life with emphasis upon the rise of the United States as a world power.  201: To 1877.  202: Since 1877.', '3', '3', '0', '0', '', '0');
