INSERT INTO `courses` VALUES ('2103', '986968', 'HIST', '330', '2006', 'ENGLISH HISTORY', 'A survey of the English and British national experience from the Saxon period to the present. The development of the English political system will be reviewed, as will the rise and decline of England as a major power. The major emphasis of this class will concentrate on the period 1485 to 1914. England’s relationship with both Scotland and Ireland will also be considered in this period. Prerequisites HIST 111, 112.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2104', '985634', 'HIST', '491B', '2006', 'Directed Study: European History', '1-3 credits each. Directed study and/or research in selected areas of history. A. American History; B. European History; C. Latin American History. Prerequisite: Advanced standing and approval of department head.', '1', '3', '3', '0', '', '0');
