INSERT INTO `courses` VALUES ('2108', '757504', 'HIST', '477', '2006', 'THE MIDDLE AGES', 'The social, cultural, religious, political, and economic history of Europe from the end of the Roman Empire to the Renaissance. Particular attention is devoted to the Church, feudalism, manorialism, political institutions, and the rise of towns and capitalism. Prerequisite: HIST 111. (Formerly 327.)', '3', '3', '0', '0', '', '0');
