INSERT INTO `courses` VALUES ('2110', '300307', 'HONR', '101H', '2006', 'HONORS COLLOQUIUM', 'A scheduled gathering to discuss topics relevant to a semester theme.  May be repeated for a total of 7 credits.  Grades of CR (credit) or NC (no credit) will be assigned.  Prerequisites: Admission to the Honors Program or permission of the Honors Council.', '1', '1', '7', '0', '', '0');
