INSERT INTO `courses` VALUES ('2113', '745743', 'HORT', '472', '2006', 'PLANTSCAPES', 'The principles, techniques, and concepts used in interior and exterior plantscapes. Three hours lecture and two hours laboratory. Prerequisite: AGRI 101 or 103. (Formerly HORT 470, 471).', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2114', '361737', 'HORT', '401', '2006', 'PLANTS AS TEACHING TOOLS', 'The basic concepts concerned in handling plants for the preschool and elementary school child. Selection and development of materials to enhance learning situations and develop an appreciation of the role of plants in life. Not to be taken for major credit or used to satisfy science requirements.', '3', '3', '0', '0', '', '0');
