INSERT INTO `courses` VALUES ('2200', '79472', 'QMDS', '210', '2006', 'STATISTICS AND QUALITY CONTROL', 'Hypothesis testing; simple and multiple regression and correlation; forecasting; quality control techniques, chi square and analysis of variance; introduction to non-parametric statistics. Emphasizes statistical analysis using software packages. Prerequisite: QMDS MATH 116 and CSCI 170. F, Sp, Su', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2201', '805350', 'QMDS', '411', '2006', 'OPERATIONS RESEARCH', 'Operations research techniques including linear programming, dynamic programming, game theory, queuing theory, Monte Carlo method and simulation with emphasis on applications to manufacturing, marketing, and financial management problems. Prerequisite: QMDS 210.', '3', '3', '0', '0', '', '0');
