INSERT INTO `courses` VALUES ('2217', '51699', 'READ', '101', '2006', 'READING ENHANCEMENT', 'Development of enhanced reading skills in vocabulary, comprehension, and speed. May be repeated for a maximum of 6 credits.', '3', '3', '6', '0', '', '0');
INSERT INTO `courses` VALUES ('2218', '767706', 'READ', '322', '2006', 'READING IN THE INTERMEDIATE GRADES', 'Materials and methods for developing and strengthening reading skills in children beyond the primary grades. Must be admitted to Teacher Education. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2219', '939784', 'READ', '418A', '2006', 'Teaching Reading in the Content Areas: Secondary', 'A study of the methods, materials, and practices which contribute to making an effective application of reading skills in the subject matter areas. A. For Secondary Teachers; B. For Elementary Teachers. Prerequisite: Approval of instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('2220', '168616', 'READ', '418B', '2006', 'Teaching Reading in the Content Areas: Elementary', 'A study of the methods, materials, and practices which contribute to making an effective application of reading skills in the subject matter areas. A. For Secondary Teachers; B. For Elementary Teachers. Prerequisite: Approval of instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('2221', '181708', 'READ', '481', '2006', 'CONTINUING STUDIES IN READING', 'Various topics in reading. May be repeated. Credit may not be applied toward a degree. Grade of CR (Credit) or NC (No credit) will be awarded.', '1', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('2222', '596790', 'SCIE', '101', '2006', 'INTEGRATED PHYSICAL SCIENCE I', 'Basic concepts and principles of physics, in particular, force and motion, energy, light, heat, electricity, and magnetism.  Personal applications of science, process skills, problem-solving, and discovery/inquiry learning are also emphasized.  Integrated lecture/lab format.  For Elementary Education Majors only.  F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2223', '87341', 'SCIE', '102', '2006', 'INTEGRATED PHYSICAL SCIENCE II', 'Fundamental concepts of chemistry with an emphasis on the interdisciplinary nature of the concepts introduced.  Integrated lecture/lab format.  For Elementary Education Majors only. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2224', '474370', 'SCIE', '104', '2006', 'INTEGRATED GEOSCIENCES', 'The goal of this course is to present an integrated approach to essential concepts in introductory geology (physical and historical geology), astronomy (from an earth science perspective), and weather and to emphasize the personal application of science, the process skills, problem-solving, and discovery/inquiry learning. Prerequisites: SCIE 101 and 102. For Elementary Education Majors only.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2225', '489764', 'SOCL', '416', '2006', 'DIRECTED STUDY', 'Guided reading and/or research in a special area of sociology. Prerequisite: Twelve hours of sociology, a grade point of 3.00 in sociology courses and prior approval of supervising instructor.', '1', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('14035', '458685', 'SOCL', '435', '2006', 'Anthropology Of Sex Roles', 'An analysis of different values that define appropriate masculine and feminine behavior (sex roles) in a variety of cultures. Explores the impact of social institutions on teaching and enforcing sex roles in traditional and modern societies. (Same as ANTG 435)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('2227', '969947', 'SOCW', '318', '2006', 'COMMUNITY INTERVENTION', 'A study and evaluation of theories and strategies of community intervention; a problem- solving methodology to teach techniques of working with planning action groups around problem identification, and intervention activities; methods of community organization.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2228', '251160', 'SOCW', '435', '2006', 'DIRECTED STUDY', 'Guided readings in selected areas directed by instructor on an individual basis. May not be taken for more than three credits. Prerequisite: Junior standing and prior approval by supervising professor.', '1', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('2229', '453660', 'SOCW', '495', '2006', 'DIRECTED RESEARCH IN SOCIAL WORK', 'The application of basic research methods in studying specialized problems. Prerequisites: SOCL 450 and approval of supervising professor.', '1', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2230', '15839', 'SOCW', '408', '2006', 'Child Abuse Recognition and Treatment', 'Defines the abused and neglected child; examines the dynamics associated with dysfunctional families and multi-level treatment approaches for the abused child and abusive parents. (Formerly 406 and 407; may receive credit for only one of the following: 406, 408, or PSYC 441.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('2231', '330631', 'SOCW', '417', '2006', 'SOCIAL WORK ADMINISTRATION', 'Study of organization and administrative practices utilized in public agencies for expediting services offered to the public. Emphasis on planning, programming, budgeting, public relations, interpretation of social policy, decision making, and legislation. Prerequisite: Approval of program director. (Formerly Organization and Administration in Social Welfare Agencies.)', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('2232', '259011', 'SPAN', '103', '2006', 'STUDY ABROAD', 'Application of language skills in a foreign language environment. Students will be involved in a total immersion living situation and will receive a minimum of fifteen clock hours per week in formal language instruction. May be repeated with permission of department head.', '3', '6', '6', '0', '', '0');
