INSERT INTO `courses` VALUES ('2248', '790696', 'SPAN', '407', '2006', 'CONTEMPORARY LATIN AMERICAN LITERATURE', 'Maximum 6 cr. An analysis and discussion of outstanding works from the Spanish American literature of the twentieth century. Prerequisite: SPAN 202 or 205 or permission of department head.', '3', '3', '6', '0', '', '0');
