INSERT INTO `courses` VALUES ('14165', '680336', 'KINS', '491D', '2006', 'Directed Study: Selected Topics', 'Directed study and/or research of A. Health; B. Kinesiology; C. Athletic Injuries; D. Selected Topics. A maximum of six credit hours may be taken. Prerequisite: Approval of department head.', '1', '3', '6', '0', '', '0');
INSERT INTO `courses` VALUES ('14371', '831275', 'ANTG', '111', '2006', 'Human Origins And Evolution', 'The study of the origin of and biogenetic heritage of humans; a survey of modern human racia variation; an introduction to archeological and prehistoric techniques of investigation regarding the changing physical form of humans. (Same as GEOG 111.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14189', '105166', 'ANTG', '120', '2006', 'Prehistoric Archeology Of The Bible', 'An introduction to prehistoric archeology of the Middle East and nearby areas that figure prominently in the Judeo-Christian Old Testament. Temporal scope includes the Paleolithic, Mesolithic, Neolithic, and the Chalcolithic Periods.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14201', '662549', 'ANTG', '201', '2006', 'Introduction To Archeology', 'Survey of the history and development of archeology as a science, its place in the sciences, and an overview of the important material remains of past human life and activities in selected areas of the world. (Same as GEOG 201.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14208', '635877', 'ANTG', '308', '2006', 'Old World Archeology', 'A survey of the major archeological sequences of prehistoric Eurasia, Africa, and Oceania. (Same as GEOG 308.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14220', '17424', 'ANTG', '410', '2006', 'Archeology Of Louisiana Indians', 'An introduction to the archeology of Louisiana’s Indian tribes from Paleo-Indian times (10,000 B.C.) through the historic period. Prerequisite: Junior or senior standing or permission of the instructor. (Same as GEOG 410.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14232', '855342', 'ANTG', '414', '2006', 'The Indians Of North America', 'Survey of the distribution and principal regional lifeways of North American Indian groups prior to disruption, dispersal, and reservationizing. (Same as GEOG 414.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14238', '907668', 'ANTG', '421', '2006', 'The Archeology Of Mexico', 'An in-depth view of the development of pre-Hispanic Mexican and Meso-American cultures and civilizations from Paleo-Indian times to the Spanish Conquest; an overview of the major archeological sites pertinent to these cultures. (Same as GEOG 421.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14244', '786679', 'ANTG', '423', '2006', 'Archeological Field Techniques', 'A field course introducing the basic techniques of archeological mapping, excavation, cataloging, specimen preparation and preservation. A study of soils, physical stratigraphy, synecology, and techniques of artifact laboratory analysis. May be repeated once for a maximum of 12 hours credit. (Same as GEOG 423.)', '6', '6', '12', '0', '', '0');
INSERT INTO `courses` VALUES ('14356', '352485', 'CJUS', '233', '2006', 'Forensics', 'An overview of techniques applied in identification of human skeletal remains for the medicolegal professions, including assessment of age, sex, ancestry, trauma, and disease; occupational stress markers; procedures for search/recovery of human remains; and estimations of time since death (same as ANTS and SOCL 233).', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14323', '506832', 'HONR', '301H', '2006', 'Honors Social Science', 'Discussion and investigation of topics in anthropology, econimics, geography, political science, psychology, or sociology. Course content varies and may link two or more of these disciplines. Prerequisites: Admisson to the Honors Program or permission of the University Honors Council. (May be repeated for up to six hours of credit.)', '3', '3', '6', '0', '', '0');
INSERT INTO `courses` VALUES ('14365', '949488', 'ANTG', '411', '2006', 'Archeology Of North America', 'The archeological background of North America from Mexico to the Arctic Circle; progressive states of development of prehistoric North American cultures. (Same as GEOG 411)', '3', '3', '3', '0', '', '0');
