INSERT INTO `courses` VALUES ('12620', '659482', 'PHAR', '634', '2006', 'ADVANCED COMMUNITY PHARMACY PRACTICE EXPERIENCE', 'Application of knowledge skills and attitudes necessary to provide pharmaceutical care in the community pharmacy setting. Experiences include patient triage, disease state management, patient education, follow-up and monitoring as well as medication distribution activities. Fourth Year Pharmacy Standing.', '6', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('12621', '483999', 'PHAR', '635', '2006', 'ADV INSTITUTIONAL PHARMACY PRACTICE EXPERIENCE', 'Application of the knowledge, skills, and attitudes necessary to provide pharmaceutical care and manage the pharmacy practice in the institutional setting. Fourth Year Pharmacy Standing.', '6', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('12622', '706191', 'PHAR', '636', '2006', 'ADV AMBULATORY CARE PHARMACY PRACTICE EXPERIENCE', 'Application of the knowledge, skills, and attitudes necessary to provide pharmaceutical care in ambulatory care setting. Activities provide exposure to a variety of disease states, and experiences include patient triage, disease state management, and patient education, follow-up, and monitoring. Fourth Year Pharmacy Standing.', '6', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('12623', '693995', 'PHAR', '637', '2006', 'ADV ADULT MEDICINE PHARMACY PRACTICE EXPERIENCE', 'Application of the knowledge, skills, and attitudes necessary to provide pharmaceutical care in acute care settings. Activities provide exposure to a variety of disease states, allowing students to gain experience gathering and evaluating patient data, making therapeutic decisions and monitoring drug therapy. Fourth Year Pharmacy Standing.', '6', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('12624', '258329', 'PHAR', '638', '2006', 'ADV SPECIALTY PHARMACY PRACTICE EXPERIENCE', 'Application of the knowledge, skills, and attitudes necessary to provide pharmaceutical care in specialty setting. Activities provide a focused exposure to specific areas of pharmacy practice, and experiences are focused on providing pharmacy care in these areas of practice. Fourth Year Pharmacy Standing.', '6', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('12625', '531402', 'PHAR', '639', '2006', 'ADVANCED PHARMACY PRACTICE ELECTIVE EXPERIENCE I', 'Application of the knowledge, skills, and attitudes necessary to provide pharmaceutical care in various settings. Activities provide a focused exposure to specific areas of pharmacy practice and experiences are focused on providing pharmacy care in these areas of practice. Fourth Year Pharmacy Standing.', '6', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('12626', '779609', 'PHAR', '640', '2006', 'ADVANCED PHARMACY PRACTICE ELECTIVE II', 'Application of the knowledge, skills, and attitudes necessary to provide pharmaceutical care in various settings. Activities provide a focused exposure to specific areas of pharmacy practice and experiences are focused on providing pharmacy care in these areas of practice. Fourth Year Pharmacy Standing.', '6', '6', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('12629', '706081', 'PHAR', '504', '2006', 'VETERINARY PHARMACOTHERAPY', 'Pathogenesis, diagnosis, and rational therapeutic approaches to the treatment of selective disease states in companion animals. Prerequisite: 3rd year pharmacy standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('12630', '459410', 'PHAR', '516', '2006', 'STERILE PRODUCTS', 'The theory and application of procedures used in the preparation of sterile products for intravenous and other sterile uses. Prerequisites: 2nd or 3rd year pharmacy standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('12631', '956126', 'PHAR', '559', '2006', 'PHARMACY-BASED IMMUNIZATION DELIVERY', 'An innovative and active training program that provides the skills necessary to become a primary source for vaccine advocacy, education, and administration. Prerequisite: 3rd year pharmacy standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('12632', '512631', 'PHAR', '560', '2006', 'DEVELOPING AMBULATORY CARE SERVICES', 'Teaches the aspects of starting services and managing patient specific pharmacotherapy regimens in an ambulatory care setting. Prerequisite: 3rd year standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('12644', '975385', 'PHAR', '561', '2006', 'Women&#39;s Health and the Pharmacist', 'Practical information necessary to provide optimal pharmaceutical care to women in a wide variety of practice settings. Includes both social and therapeutic issues. Emphasis placed on drugs in pregnancy/lactation. Prerequisite: 3rd year pharmacy standing.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('12645', '103512', 'PHAR', '562', '2006', 'BASIC STRATEGIES FOR SCHOLARLY COMMUNICATION', 'The student will be responsible for producing professional level education material. The student will produce an article that will be submitted to a pharmacy journal along with a presentation to facutly over their research. Prerequisite: 3rd year pharmacy standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('12646', '79324', 'PHAR', '563', '2006', 'SELECTED PSYCHIATRIC & SUBSTANCE ABUSE DISORDERS', 'This course will expand the pharmacy student&#39;s exposure to selected topics in behavioral health including pharmacotherapy of selected psychiatric disorders. Prerequisite: 3rd year pharmacy standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('12647', '431559', 'PHAR', '564', '2006', 'PHARMACOGENETICS', 'This course will provide students with basic concepts in pharmacogenetics and the role of inheritance on inter-individual variation in drug response. In addition, the class will provide students with an appreciation of how pharmacogenetic and pharmacogenomic fields impact pharmaceutical care. Prerequisite: Pharmacy 411, 412, and 432.', '3', '3', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('12648', '796862', 'PHAR', '590B', '2006', 'Methods and Principles of Toxicology', '4 credits each. Intended to provide the graduate student with hands-on laboratory experience in methods commonly used in A. Neurotoxicology, B. Aquatic Toxicology, C. Immunotoxicology, D. Hepatotoxicology. Prerequisite for A, B, and D: 550 and Consent of Instructor. Prerequisite for C: 408 or 423.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('12649', '354390', 'PHAR', '590C', '2006', 'Methods and Principles of Toxicology', '4 credits each. Intended to provide the graduate student with hands-on laboratory experience in methods commonly used in A. Neurotoxicology, B. Aquatic Toxicology, C. Immunotoxicology, D. Hepatotoxicology. Prerequisite for A, B, and D: 550 and Consent of Instructor. Prerequisite for C: 408 or 423.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('12650', '794051', 'PHAR', '590D', '2006', 'Methods and Principles of Toxicology', '4 credits each. Intended to provide the graduate student with hands-on laboratory experience in methods commonly used in A. Neurotoxicology, B. Aquatic Toxicology, C. Immunotoxicology, D. Hepatotoxicology. Prerequisite for A, B, and D: 550 and Consent of Instructor. Prerequisite for C: 408 or 423.', '4', '4', '0', '0', '', '0');
INSERT INTO `courses` VALUES ('13915', '248306', 'MGMT', '330', '2006', 'Enterprise Management', 'Students will study the application of management concepts to new and growing ventures. Analysis of organizational design, planning, implementation, and control of enterprises; social and ethical considerations in managing enterprises; and production, decision making, and behavioral factors in new and growing venture management. Prerequisite: Junior/senior standing or consent of instructor. F, Sp', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14383', '922568', 'GOVM', '403', '2006', 'Ethics', 'A study of the theoretical foundations of morality with reference to approaches to ethical thought, character, and action. Concepts include good and evil, right and wrong, obligation and rights, responsibility and freedom. The application of these concepts to the working out of particular ethical problems. (Same as PHIL 403.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13048', '287250', 'DANC', '122', '2006', 'Aerobic Dance', 'Rhythmic activities that improve cardiorespiratory endurance, flexibility, body composition, muscular strength, and endurance.', '1', '1', '2', '0', '', '0');
INSERT INTO `courses` VALUES ('13064', '205408', 'POLS', '101', '2006', 'Introduction To Political Science', 'Development of the state; nature and problems of government in relation to the individual; emphasis upon the development of various forms of government.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13070', '764708', 'POLS', '200', '2006', 'Directed Study', 'Selected studies in government. Content varies. A. American Government (Same as POLS 201.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13076', '109376', 'POLS', '201', '2006', 'American National', 'Consideration of the nature of democracy, civil rights, federalism, the political process, the legislative, executive, and judicial branches, and current problems.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13082', '351909', 'POLS', '202', '2006', 'State And Local', 'An intensive study of state and local government, structural components, political process, and basic problems. Special emphasis on Louisiana government.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14335', '922568', 'POLS', '403', '2006', 'Ethics', 'A study of the theoretical foundations of morality with reference to approaches to ethical thought, character, and action. Concepts include good and evil, right and wrong, obligation and rights, responsibility and freedom. The application of these concepts to the working out of particular ethical problems. (Same as PHIL 403.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13106', '118190', 'POLS', '406', '2006', 'Political Parties, Campaigns, And Elections', 'Political parties, Campaigns and Elections focuses on three closely related topics and phenomena: parties, campaigns and elections in the American context. We will be seeking to understand their parallel, and in some sense, symbiotic development and relationships. We will study the history of party politics in the United States, focusing on change and development in relationship to the dynamic American Republic. Prerequisite: POLS 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13112', '787621', 'POLS', '407', '2006', 'Western Political Theory', 'A study of political theory beginning with Plato and the ancient Greeks and continuing through the Middle Ages, Reformation, and Enlightenment to Marx, Mills and the modern age. The central questions of political thought are explored e.g., what is the good?; what is justice?; how is political authority acquired?; where does sovereignty reside?; and how do we incur obligations? Prerequisite: POLS 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13118', '533147', 'POLS', '413', '2006', 'Constitutional Law: Governmental Powers', 'A study of the U.S. Constitution through analysis of decisions of the Supreme Court. The cases demonstrate the nature of judicial function, the nature of the federal system, and the scope and character of the process of the national government. Prerequisite: POLS 201 or approval of department head and permission of the instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13124', '553789', 'POLS', '414', '2006', 'Constitutional Law: Civil Rights', 'A study of the U.S. Constitution through analysis of decisions of the Supreme Court. The cases demonstrate the scope and character of the limitations on governmental powers concerning civil rights such as speech, press, religions, and rights against discrimination. Prerequisite: POLS 201 or approval of department head and permission of the instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13130', '720669', 'POLS', '417', '2006', 'Public Administration And Administrative Law', 'Structure and processes of public administration, practices and processes of public administration, practices in personnel, organization and development of administrative law. Prerequisite:  POLS 201. (Formerly 309.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14183', '547805', 'GOVM', '418', '2006', 'Constitutional Law And Criminal Justice', 'Study of constitutional law and its applicability in the criminal justice system. Prerequisite: 201. (Formerly GOVM/CJUS 421.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13136', '547805', 'POLS', '418', '2006', 'Constitutional Law And Criminal Justice', 'Study of constitutional law and its applicability in the criminal justice system. Prerequisite: POLS 201. (Same as CJUS 418) (Formerly GOVM/CJUS 421.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13142', '693779', 'POLS', '420', '2006', 'Louisiana Government And Politics', 'Development and organization of political processes in Louisiana; contemporary problems of Louisiana state and local governments. (Formerly 302.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13148', '280343', 'POLS', '421', '2006', 'American Political Theory', 'This course focuses on the origin and development of American political thought. Topics include Locke and liberalism, Puritanism and republicanism, Paine and the revolutionary impulse, Founders and Anti-Federalists, Jefferson’s agricultural republic, Tocqueville’s social America, Jacksonian democracy, Lincoln and Calhoun, populists, progressives and pragmatists, New Dealers and social engineers. Prerequisite: POLS 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13154', '531276', 'POLS', '423', '2006', 'American Urban Politics', 'An analysis of the structure and functions of contemporary city governments with considerable attention devoted to basic problems. Special emphasis on local government in Louisiana. (Formerly 307.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13160', '961005', 'POLS', '429', '2006', 'Women And Politics', 'An examination of the role that women have played in the development of the U.S. political system, including historical analysis of the barriers to women’s participation and the impact of politically active women on contemporary issues.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13166', '93926', 'POLS', '433', '2006', 'The Judical Process', 'Structure, functions, and operations of the judiciary. Prerequisite: 201. (Formerly 312.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13172', '68286', 'POLS', '440', '2006', 'Comparative', 'The structure, theory, organization, and methods of control in the governments of the major European powers; recent political trends. Prerequisite: 201. (Formerly 305.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13178', '83591', 'POLS', '441', '2006', 'Comparative Govt And Politics In Latin America', 'A comparative survey of governmental institutions and political processes which characterized the Latin American republics from the colonial period to the present. (Formerly 306.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13184', '923717', 'POLS', '450', '2006', 'American Foreign Policy', 'Major foreign policies, objectives, and commitments of the United States; their political, economic, and military implications. Policy decisions currently confronting the United States in various areas of the world. (Formerly 317.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13190', '795337', 'POLS', '453', '2006', 'International Politics', 'The nation-state system, power politics, processes affecting international relations; sources of tension in international politics and possibilities of their peaceful resolution. (Formerly 315.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13196', '315402', 'POLS', '454', '2006', 'Congress And The Presidency', 'A course focused on two of the three major institutional components of American politics. Main topics include the history of the Congress and Presidency (i.e., the two institution’s structural, behavioral and institutional adaptation to a changing America), the internal and external dynamics of public policy production (i.e., how demands arising in the electorate and elsewhere are conveyed to and/or produced within the Congress and the translation of these demands into specific laws and policies), and, the complex interaction between the Congress and the Presidency as different and separate institutions sharing power. Prerequisite: POLS 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13202', '321995', 'POLS', '457', '2006', 'Contemporary Political Thought', 'Political thought of the twentieth century; liberalism, modern totalitarianism, conservatism, Freudianism, existentialism, and democracy.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13208', '725649', 'POLS', '461', '2006', 'International Political Economy', 'A course focused on the intersection of politics and economies in relations among actors in the international environment. Main topics include: international trade strategies; the political strategies of transnational firms; assessment of international organization; the role of the Uited States in the contemporary global economy. Prerequisite: 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13214', '695003', 'POLS', '463', '2006', 'Comparative Politics Of Race And Ethnicity', 'International comparative analysis of how political processes both shape and are shaped by racial and ethnic differences, both within and among nation-states. Prerequisite: 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13220', '201149', 'POLS', '465', '2006', 'Global Development', 'Cross-cultural exploration of the processes of economic and political development in different regions and countries of the world, including discussion of globalization and its character and effects in geographical and political settings. Prerequisite: 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13226', '163450', 'POLS', '491', '2006', 'Directed Study', 'Directed study and/or research in selected areas of government. A. National, State, and Local; B. Public Administration and Law; C. Political Theory; D. International Relations; E. Comparative; F. Political Processes. Prerequisite: Advanced standing, approval of department head, and permission of the instructor.', '1', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13232', '933300', 'CMST', '101', '2006', 'Fundamentals', 'Focus is on the total range of communication behaviors we engage in within the contexts of intrapersonal, interpersonal, small group, organizational, public speaking, and mass communication.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13238', '806197', 'CMST', '102', '2006', 'Voice And Diction', 'A non-technical approach to developing expressive speech. Emphasis is on phonetics, the mechanics of vocal sound, and oral readings.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13244', '732563', 'CMST', '109', '2006', 'Forensic Speaking Laboratory', 'Supervised participation in the intercollegiate debate program. May be repeated for a maximum of three hours credit.', '1', '1', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13247', '594276', 'POLS', '470', '2006', 'Social Research', 'The formulation of a research problem; ethics of research; development of research design; sampling procedures; collecting and processing of research data. (Same as SOCL 450.) Prerequisite: POLS 101.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13256', '246606', 'CMST', '118', '2006', 'Interpersonal Communication', 'Stresses practical application of communication concepts and skills. Emphasis on self-concept, language, perception, listening, nonverbal communication, and resolution of interpersonal skills development.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13262', '233850', 'CMST', '201', '2006', 'Public Speaking', 'The process of public communication, including an understanding of audience, to produce, evaluate, and critique public expression in public arenas.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13274', '281264', 'CMST', '304', '2006', 'Intercultural Communication', 'A study of communication principles between members of different cultures. Distinctions among verbal and non-verbal code systems regarding various aspects of culture will be examined. Prerequisite: CMST 101, 118, or consent of instructor. May not be used as a substitute for a foreign language.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13280', '866353', 'CMST', '305', '2006', 'Oral Interpretation', 'Oral reading of prose and poetry with emphasis on determining and interpreting the thought and emotional content of selections of literature.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13286', '295461', 'CMST', '310', '2006', 'Communication And Conflict', 'A study of the theory and practice of communication strategies and styles in conflict situations. The course will look at conflict in interpersonal and/or profession settings. Prerequisites: CMST 101, 118, 201 or consent of the instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13292', '414715', 'CMST', '311', '2006', 'Argumentation And Debate', 'Argumentation as a method for critical inquiry. Emphasis on debate as argument exemplar with instruction in analysis, reasoning, and use of evidence. Participation in debates. (Formerly Speech 311-Debate.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13298', '681011', 'CMST', '325', '2006', 'Special Topics', 'Study of various communication contexts, problems, and issues. A. Rhetoric and Public Address; B. Forensics; C. Interpersonal Communication; D. Organizational Communication; E. Communication Theory; F. Instructional Communication. Prerequisite: CMST 101, 118, or 201 or consent of Department Head.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13304', '801432', 'CMST', '330', '2006', 'Persuasion', 'A review of general theoretical approaches to persuasion and influence with focus on principles of persuasive communication and application of persuasive strategies. Prerequisite: CMST 101 or 201 or consent of instructor. (Formerly Speech 230 - Persuasion.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13310', '33246', 'CMST', '335', '2006', 'Nonverbal Communication', 'A review of general theoretical approaches to persuasion and influence with focus on principles of persuasive communication and application of persuasive strategies. Prerequisite: CMST 101 or 201 or consent of instructor. (Formerly Speech 230 - Persuasion.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13316', '934543', 'CMST', '341', '2006', 'Communication And Gender', 'Exploration of how gender and communication interact, exploring how gender influences communication, how gender is communicated, and how communication influences our understandings of gender and what it means to be gendered beings. Prerequisites: CMST 101, 118, or 304 or approval of the instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13322', '88663', 'CMST', '344', '2006', 'Organizational Communication', 'An introduction to the principles and theories of competent organizational communication. You will learn about the research and practice in this field.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13328', '160675', 'CMST', '405', '2006', 'Advanced Public Communication', 'Preparation and delivery of the principal types of speeches, with some attention to the miscellaneous types of speeches; rhetorical criticism. An effort is made to arrange outside speaking engagements for members of the class. Prerequisite: CMST 110H or 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13334', '202048', 'CMST', '420', '2006', 'Performance Studies', 'Theory and practice in the art of group oral presentation of literature; selection, analysis, and arrangement of materials for oral interpretation; program planning for public presentation; emphasis on Reader’s Theatre. Prerequisite: SPCH CMST or consent of instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13346', '511210', 'CMST', '450', '2006', 'Communciation Theory', 'An analysis and evaluation of predominant communication theories that identify fundamental communicative forces in human action. Prerequisite: SPCH 101, 330, or approval of instructor. Prerequisites: CMST 101, 330, or approval of instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13352', '28651', 'CMST', '451', '2006', 'American Public Address', 'Provides a study of American public address from colonial times to the present. Prerequisite: CMST 101, 201, or approval of instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13358', '65311', 'CMST', '452', '2006', 'Criticism Of Contemporary Communication', 'This course examines various aspects of contemporary communication. Prerequisites: CMST 101, 201, or approval of the instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13364', '308329', 'CMST', '453', '2006', 'Rhetoric Of Social Movements', 'This course examines social movement. Prerequisite: CMST 101, 201, or approval of instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13370', '877251', 'CMST', '462', '2006', 'Advanced Small Group Communication', 'Studies of group action, interaction, and leadership designed to apply small group theory. Emphasis on the nature of group communication as exemplified in the discussion/conference model. Prerequisite: CMST 260 or 101 or consent of instructor.', '3', '3', '3', '0', '', '0');
