INSERT INTO `courses` VALUES ('13575', '209018', 'H&HP', '201', '2006', 'First Aid And CPR', 'First aid to the victims of accidents, sudden illness, cardiac or respiratory emergencies. First aid and CPR certification granted to those meeting agency requirements. Two hours laboratory. (Formerly First Aid and Personal Safety.)', '1', '1', '1', '0', '', '0');
INSERT INTO `courses` VALUES ('13582', '60759', 'H&HP', '211', '2006', 'Foundations Of Physical Education', 'Basic understanding and familiarization of health, physical education and recreation as a profession; including historical background, principles, trends, curriculum and professional preparation. For Health and Physical Education majors only.', '3', '3', '3', '0', '', '0');
