INSERT INTO `courses` VALUES ('13594', '730398', 'H&HP', '221', '2006', 'Concepts Of Personal Wellness', 'The purpose of this course is to provide learning opportunities for the development of conceptual and functional knowledge of health-related physical fitness and the role it plays in the development and maintenance of human wellness. The health-behavior-gap is examined, and specific emphasis is placed on the individual long-term preventive and rehabilitative benefits derived from lifelong participation in physical activity. Prerequisite: Kinesiology majors only.', '2', '2', '2', '0', '', '0');
INSERT INTO `courses` VALUES ('13600', '394389', 'H&HP', '233', '2006', 'Water Safety Instruction', 'Qualifies individuals to teach and certify American Red Cross Aquatic courses. Prerequisite: 220.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('13606', '440761', 'H&HP', '241', '2006', 'Personal And Community Health', 'An orientation course in health which deals with nutrition, stress, exercise, diseases and their prevention.', '3', '3', '3', '0', '', '0');
