INSERT INTO `courses` VALUES ('14017', '116093', 'AGRI', '435', '2006', 'Principles Of Gis', 'Introduction to techniques for analyzing, displaying, and managing geographic data topics including: structure of geographic data bases, principles of digital cartography, basic spatial statistics, and analysis of spatial relationships using GIS. Prerequisite: Junior, senior, or graduate standing (Same as GEOS 435).', '3', '3', '3', '0', '', '0');
