INSERT INTO `courses` VALUES ('14011', '861302', 'BIOL', '201', '2006', 'Environmental Science', 'Exploration of contemporary issues in environmental science. Man’s interaction with the Earth’s biological and physical resources. Topics include global warming, biodiversity, conservation, pollution, wetlands, sustainable agriculture, and population growth. Prerequisites: An introductory biology course or approval of department head. (Same as GEOS 201). F', '3', '3', '3', '0', '', '0');
