INSERT INTO `courses` VALUES ('14278', '566877', 'AVIA', '415', '2006', 'Aircraft Engines And Systems', 'Advanced aircraft engines and systems of a typical large twin pressurized turboprop, including warning systems, doors, electrical, lighting, fuel, engines, fire protection, hydraulics, flight controls, stall warnings, avionics, TCAS, EGPWS, communications, landing gear, pneumatics, air conditioning, pressurization, oxygen, ice protection, APU, and emergency equipment. Prerequisite: minimum grade of &quot;C&quot; in 110 and 111, completion of all 100-level English and Math courses, or instructor approval. (Formerly 416)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14284', '698562', 'ACCT', '440', '2006', 'Law for Accountants', 'Specifically for accounting majors. Legal concepts underlying business organizations, contracts, debtor-creditor relationships, government regulation of business, the uniform commercial code, property and professional and legal responsibilities. Can be used as a preparation for the CPA examination. Prerequisite: 401. F, Sp (Formerly ACCT 440.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14290', '579218', 'CHEM', '307', '2006', 'Quantitative Analysis', 'Discussion of techniques and theories of gravimetric, volumetric and physicochemical methods of analysis. Three hours lecture. Prerequisite: &quot;C&quot; or better in CHEM 108. F (Formerly 307)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14296', '947032', 'CHEM', '308', '2006', 'Quantitative Analysis Laboratory', 'A laboratory course to accompany CHEM 240. Six hours laboratory. Prerequisite: &quot;C&quot; or better in CHEM 110 and credit or registration in CHEM 240. F (Formerly 308)', '2', '2', '2', '0', '', '0');
INSERT INTO `courses` VALUES ('14302', '216924', 'CHEM', '401', '2006', 'Physical Chemistry I', 'Fundamental interpretations of the physical principles of chemistry with emphasis on equilibrium thermodynamics and chemical kinetics. Three hours lecture and one hour recitation. Prerequisites: &quot;C&quot; or better in CHEM 108; PHYS 208; MATH 132. F (Formerly 401)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14308', '404242', 'CHEM', '336', '2006', 'Biochemistry I', 'Introduction to structure and function of proteins, nucleic acids, lipids and carbohydrates and enzyme kinetics, mechanisms, regulation, and vitamins. Three hours lecture. Prerequisite: &quot;C&quot; or better in CHEM 232. F, Su1 (Formerly 336)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14317', '544549', 'SPLP', '113', '2006', 'Intro/splh Disorders', 'For SPLP majors in the College of Health Sciences, for Health Care Professionals, and for Education majors.  A survey of speech, language and hearing development and associated disorders.  An introduction to the professions of Speech-Language Pathology and Audiology.', '3', '3', '3', '0', '', '0');
