INSERT INTO `courses` VALUES ('14386', '751975', 'PHIL', '403', '2006', 'Ethics', 'A study of the theoretical foundations of morality with reference to approaches to ethical thought, character, and action. Concepts include good and evil, right and wrong, obligation and rights, responsibility and freedom. The application of these concepts to the working out of particular ethical problems. (Same as PHIL 403.)', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14350', '839632', 'ANTS', '233', '2006', 'Forensics', 'An overview of techniques applied in identification of human skeletal remains for the medicolegal professions, including assessment of age, sex, ancestry, trauma, and disease; occupational stress markers; procedures for search/recovery of human remains; and estimations of time since death (same as ANTS and SOCL 233).', '3', '3', '3', '0', '', '0');
