INSERT INTO `courses` VALUES ('14402', '893304', 'SPLP', '222', '2006', 'American Sign Language II', 'This course is designed to expand receptive and expressive American Sign Language (ASL) skills. It is a natural language approach incorporating cultural interactions which are being used to develop appropriate conversational skills in ASL at the survival-plus level. Vocabulary acquisition and the understanding and use of ASL linguistic structure is emphasized. Instruction is in ASL without voice. Prerequisite: SPLP 220.', '3', '3', '3', '0', '', '0');
INSERT INTO `courses` VALUES ('14408', '325076', 'SPLP', '220', '2006', 'Beginning Sign Language', 'An introductory course in the study of American Sign Language (ASL) and Pidgin Sign English (PSE) with an emphasis on the Deaf Community and their culture. An understanding of some of the basic cultural differences of the Deaf and how they relate to the development and existence of sign languages will be coupled with basic skills in both ASL and PSE. Non-majors only.', '3', '3', '3', '0', '', '0');
