INSERT INTO `draft_courses` VALUES ('1', '953665', 'ACCT', '308', '2006', 'ACCOUNTING INFORMATION SYSTEMS', 'Identification of distinct information systems knowledge required by accountants. Survey of effect of computers on accounting systems. Analysis of internal controls and cycles of accounting systems, both manual and computer. Prerequisites: 213, CSCI 170. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('2', '360471', 'ACCT', '311', '2006', 'INTERMEDIATE II', 'Second in a two-course sequence intended to study the theory and application of financial reporting for business entities. Prerequisities: minimum grade of &quot;C&quot; in 310 and junior standing. F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('3', '445491', 'ACCT', '320', '2006', 'COST', 'Basic concepts and procedures involved in accounting for raw materials, direct labor, and manufacturing overhead in job order, process, and standard cost accounting systems. Prerequisites: minimum grades of &quot;C&quot; in 212 and 213; junior standing. F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('4', '318391', 'ACCT', '330', '2006', 'INCOME TAX', 'The Federal Income Tax Law; preparation of returns for individual taxpayers. Prerequisites: minimum grades of &quot;C&quot; in 212 and 213; junior standing. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('5', '253858', 'ACCT', '410', '2006', 'INTERNATIONAL AND ADVANCED ACCOUNTING TOPICS', 'Topics include international GAAP; foreign currency transactions and translations; consolidations; and other advanced accounting issues. Prerequisite: Minimum grade of &quot;C&quot; in 311. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('6', '459134', 'ACCT', '430', '2006', 'THEORY', 'Accounting principles and procedures and the theoretical framework on which they rest; selected specialized topics; emphasis on publications of the AICPA and AAA. Prerequisite: Minimum grade of &quot;C&quot; in 311 or permission of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('7', '798952', 'ACCT', '431', '2006', 'ADVANCED INCOME TAX', 'Income tax applicable to partnerships and corporations; various other taxes; research of tax problems. Prerequisite: Minimum grade of &quot;C&quot; in 330 or permission of department head. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('8', '655622', 'ADED', '402', '2006', 'FOUNDATIONS OF ADULT EDUCATION', 'A comprehensive study of Adult Education with emphasis on historical background and current developments. Adult Education and Literacy Acts, teacher certification, extent and consequences of illiteracy, characteristics of adult learners, special-needs groups, and resources.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('9', '299339', 'ADED', '410', '2006', 'METHODS AND MATERIALS IN ADULT EDUCATION', 'The course will aid teachers in developing skills, techniques and strategies for teaching basic skills to the adult learner. There will also be a focus on organizing instructional materials to promote effective learning. (Formerly Curriculum and Instruction 410.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('10', '319191', 'AGRI', '101', '2006', 'PLANT SCIENCE', 'Principles of agronomic plant production, improvement, maintenance, harvest, storage, marketing and utilization. Three hours lecture and two hours laboratory.', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('11', '896760', 'AGRI', '103', '2006', 'PLANT SCIENCE', 'Principles of horticulture plant production, improvement, maintenance, harvest, storage, marketing and utilization. Three hours lecture and two hours laboratory.', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('12', '796970', 'AGRI', '110', '2006', 'AGRICULTURAL CAREERS', 'An introduction to academic and employment opportunities in agriculture careers.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13', '16716', 'AGRI', '115', '2006', 'AGRICULTURAL SAFETY', 'Recognition and applied solutions as related to farm machinery, livestock, chemicals, and AGRICULTURE 139  other related agricultural hazards. Emphasis will be placed on both the ethics and liabilities associated with farm safety.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14', '272722', 'AGRI', '244', '2006', 'AGRIBUSINESS PRACTICUM', 'Design an agribusiness enterprise including a plan detailing practices, procedures, and budget, and completed in a professional manner under faculty supervision. May be used as elective credit only. Prerequisite: Approval of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('15', '965297', 'AGRI', '301', '2006', 'AGRICULTURAL STATISTICS', 'Planning, conducting, and interpreting agricultural experiments, to include central tendency measures and dispersion, hypothesis, hypothesis testing, and statistical inferences as applied to agricultural data. Prerequisite: MATH 110 or 111, 116.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('16', '810259', 'AGRI', '310', '2006', 'AGRICULTURAL LECTURES', 'Lectures to be presented by professional agriculturists. Students will present oral and written interpretations of current topics relating to agriculture.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('17', '425181', 'AGRI', '311', '2006', 'COMPUTER APPLICATIONS IN THE AGRICULTURAL SCIENCES', 'Provide the student with a working knowledge of IBM compatible microcomputer systems and common software usages of agriculturalist. Topics covered: DOS, word processing, spreadsheet analysis, introduction to SAS, and management software. Prerequisite: CSCI 170.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('18', '150673', 'AGRI', '344', '2006', 'AGRIBUSINESS PRACTICUM', 'Design, plan, and execute an approved agribusiness enterprise. May be used for elective credit only. May be repeated. Prerequisites: AGRI 244 and approval of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('19', '800397', 'AGRI', '401', '2006', 'ANIMAL AND PLANT IMPROVEMENT', 'Application of the scientific methods, principles, and techniques of mating and selection systems to improve economically important traits in animal and plant populations.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('20', '213697', 'AGRI', '405', '2006', 'IRRIGATION PRACTICUM', 'Principles and techniques in irrigation system design.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('21', '616004', 'AGRI', '429', '2006', 'INTEGRATED PEST MANAGEMENT', 'Principles of insect pest management, identification, and control/management systems. Three hours lecture and two hours laboratory. Prerequisite: AGRI 101 or 103. (Formerly AGRI 425, 426).', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('22', '429091', 'AGRI', '430', '2006', 'WEED SCIENCE AND ECOLOGY', 'Principles of weed science, weed identification, spread, reproduction, and control/management systems. Three hours lecture and two hours laboratory. Prerequisite: AGRI 101 or 103. (Formerly AGRI 427, 428).', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('23', '210279', 'AGRI', '406', '2006', 'PLANT PATHOLOGY', 'A study of signs, symptoms, and syndromes as they relate to the causal agents and control of plant diseases. Three hours lecture and two hours laboratory. Prerequisites: BIOL 120, 121, MICR 201. Field trip fee required. (Formerly AGRI 403, 404).', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13409', '652273', 'AGEC', '203', '2006', 'Agricultural Marketing', 'The principles and methods of marketing agricultural products and the institutions performing the various functions in the marketing of various commodities. Current trends and developments in the agricultural marketing process. (Formerly AGEC 203)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13415', '552735', 'AGEC', '305', '2006', 'Farm Business Records', 'Fundamental principles for assimilation, organization, and analyses of farm records as applied to decision-making processes. Utilization of computers in farm records and accounting will be stressed. Prerequisite: AGEC 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13421', '412267', 'AGEC', '307', '2006', 'Advanced Agricultural Marketing', 'Fundamental concepts of forward pricing. Introduction to forward contracting, the futures market, and options on agricultural futures. Emphasis is placed on forward pricing as a tool to reduce price risk. Prerequisite: AGEC 203. (Formerly AGEC 207)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13427', '651376', 'AGEC', '310', '2006', 'Agricultural Finance', 'Principles of agricultural finance emphasizing costs and returns from capital and credit, various types and sources of credit, development, characteristics and role of agricultural lending institutions. Prerequisite: AGEC 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13433', '773108', 'AGEC', '311', '2006', 'Agricultural Price Analysis And Forecasting', 'Basic treatment of agricultural price analysis tools. Emphasis is placed on computer software and problem recognition. Course lectures concentrate on local commodities.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13439', '161241', 'AGEC', '421', '2006', 'Environmental Economics', 'Principles and methods for critically analyzing environmental issues from an economic and policy perspective. Review of current environmental issues with emphasis on agriculture related topics. Prerequisite: AGEC 201 or ECON 202.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13445', '605041', 'AGEC', '422', '2006', 'Community And Rural Development', 'Theory and practical application of economic principles to the problems faced in community and rural development. Rerequisite: AGEC 201 or ECON 202.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13451', '133580', 'AGEC', '423', '2006', 'Farm And Agribusiness Management', 'Managerial techniques applied to farm and agribusiness organizations, which include input-output relationships, enterprise combinations, and budget analysis. Emphasis will be placed upon typical decision- making problems encountered by farm and agribusiness managers. Prerequisite: AGEC 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('34', '406227', 'AGRO', '201', '2006', 'SOILS', 'Principles of soil science and the properties of soils as related to plant growth. Three hours lecture and two hours laboratory. Prerequisites: MATH 111, CHEM 101 or 107.', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('35', '923432', 'AGRO', '301', '2006', 'RESOURCE CONSERVATION AND MANAGEMENT', 'Designing and laying out conservation practices necessary to  reduce erosion. Emphasis will be placed upon surveying, mapping, drainage, terracing, water impoundment and safe water movement. Three hours lecture and two hours laboratory. Prerequisite: MATH 110 or 111. (Formerly AGMC 301).', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('36', '387438', 'AGRO', '308', '2006', 'ADVANCE FARM CROPS', 'Varieties, species, genetics, breeding, cultural practices and uses of fiber and grain crops. Three hours lecture and two hours laboratory. Prerequisite: AGRI 101 or 103. (Formerly AGRO 304, 306).', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('37', '865361', 'AGRO', '309', '2006', 'FORAGE CROPS AND PASTURE MANAGEMENT', 'Adaptation, production, establishment, utilization and management of forage crops and pastures. Three hours lecture and two hours laboratory. Prerequisite: AGRI 101 or 103. (Formerly AGRO 305, 307).', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('38', '722384', 'AGRO', '404', '2006', 'SOIL AND WATER RESOURCES', 'The nature, distribution, use, and conservation of important soil groups; the efficient use, control, and storage of water in humid and arid regions; water pollution and its control.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('39', '43061', 'AGRO', '413', '2006', 'SOIL FERTILITY AND CHEMICAL PROPERTIES', 'The chemical and mineralogical properties of soils from the theoretical and technical viewpoints. Three hours lecture and two hours laboratory. Prerequisites: AGRO 201; CHEM 101, 105, or 107. (Formerly AGRO 405, 406).', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('40', '164700', 'AFAS', '101', '2006', 'THE AIR FORCE TODAY I', 'This course focuses on the basic characteristics of the air doctrine; U.S. Air Force mission and organization; functions of the U.S. strategic offensive and defensive, general purpose and support forces; officership; followership, military customs and courtesies, and an assessment of communicative skills.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('41', '41575', 'AFAS', '102', '2006', 'THE AIR FORCE TODAY II', 'A focus on U.S. Air Force installations and professions; introduction to flight; geographical awareness; U. S. defense policy; military balance; terrorism; and a study of the other branches of the Armed Services.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('43', '961669', 'ASCI', '101', '2006', 'FARM LIVESTOCK', 'The importance of general animal husbandry, its relation to good farming practices and contribution to agriculture and commerce. Three hours lecture and two hours laboratory.', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('44', '58291', 'ASCI', '201', '2006', 'BREEDS OF LIVESTOCK', 'The origin, history, and characteristics of leading breeds of livestock. Prerequisite: ASCI 101.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('45', '212542', 'ASCI', '205', '2006', 'PRACTICAL WORK WITH LIVESTOCK', 'Dehorning, castration, methods of restraint, fitting for show, etc. Three hours laboratory. Prerequisite: ASCI 101.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('46', '926967', 'ASCI', '307', '2006', 'ANIMAL PHYSIOLOGY', 'An introduction to the study of veterinary science, dealing with the fundamental physiological processes of farm animals. Prerequisites: ASCI 101, BIOL 120, 121, CHEM 101, 103 or 107, 109.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('47', '868549', 'ASCI', '309', '2006', 'INTRODUCTION TO VETERINARY SCIENCE', 'An introduction to the study of veterinary science, dealing with both infectious and noninfectious diseases, parasites, parasitic diseases, and the establishment of immunity through the use of biological products in farm animals. Prerequisites: ASCI 101, BIOL 120, 121.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('48', '637723', 'ASCI', '323', '2006', 'LIVESTOCK FEEDING', 'Chemical composition of feeds, requirements of animals, utilization of nutrients, formulating and balancing rations. Prerequisites: ASCI 101, CHEM 101, 103 or 107, 109, MATH 110 or 111.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('49', '77640', 'ASCI', '325', '2006', 'GENETICS', 'Fundamental concepts of heredity at the molecular, cellular, organismic, and population levels. Nature of the genetic material and its involvement in the determination of structure and function. Consideration given to both classical and modern genetics. Prerequisites: BIOL 120, 121; CHEM 101, 103.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('50', '668947', 'ASCI', '341', '2006', 'LIVESTOCK MANAGEMENT', 'Systems of beef and sheep management including breeding, feeding, disease and parasite control, records, marketing and various management problems with their solutions. Three hours lecture and two hours laboratory. Prerequisites: ASCI 101, 323, MATH 110 or 111.', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('51', '851419', 'ASCI', '342', '2006', 'LIVESTOCK MANAGEMENT II', 'Systems of swine and poultry management including breeding, feeding, disease and parasite control, records, marketing and various management problems with their solutions. Three hours lecture and two hours laboratory. Prerequisites: ASCI 101, 323, MATH 110 or 111.', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('52', '693644', 'ASCI', '401', '2006', 'NUTRITION', 'Nutritional roles of carbohydrates, proteins, lipids, minerals, vitamins and water. Digestion, absorption, and use of nutrients and their metabolites. Prerequisites: ASCI 101, 323, BIOL 120, 121, CHEM 101, 103 or 107, 109.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('53', '98039', 'ASCI', '405', '2006', 'PRACTICAL LIVESTOCK HANDLING', 'Handling of livestock for sale, show, and general management procedures. Three hours laboratory. Prerequisite: ASCI 101.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('54', '61699', 'ANTS', '111', '2006', 'HUMAN ORIGINS AND EVOLUTION', 'The study of the origin of and biogenetic heritage of humans; a survey of modern human racia variation; an introduction to archeological and prehistoric techniques of investigation regarding the changing physical form of humans. (Same as GEOG 111.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('55', '502749', 'ANTS', '120', '2006', 'PREHISTORIC ARCHEOLOGY OF THE BIBLE', 'An introduction to prehistoric archeology of the Middle East and nearby areas that figure prominently in the Judeo-Christian Old Testament. Temporal scope includes the Paleolithic, Mesolithic, Neolithic, and the Chalcolithic Periods.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('56', '543958', 'ANTS', '201', '2006', 'INTRODUCTION TO ARCHEOLOGY', 'Survey of the history and development of archeology as a science, its place in the sciences, and an overview of the important material remains of past human life and activities in selected areas of the world. (Same as GEOG 201.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('57', '713971', 'ANTS', '207', '2006', 'CULTURAL ANTHROPOLOGY', 'A comparative study of the life and organization of human groups throughout the world, with an emphasis on traditional societies. (Formerly ANTS and SOCL 307, SOCL 300E, and GEOS 400B).', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('58', '55484', 'ANTS', '410', '2006', 'THE ARCHEOLOGY OF LOUISIANA INDIANS', 'An introduction to the archeology of Louisiana’s Indian tribes from Paleo-Indian times (10,000 B.C.) through the historic period. Prerequisite: Junior or senior standing or permission of the instructor. (Same as GEOG 410.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('59', '956225', 'ANTS', '414', '2006', 'THE INDIANS OF NORTH AMERICA', 'Survey of the distribution and principal regional lifeways of North American Indian groups prior to disruption, dispersal, and reservationizing. (Same as GEOG 414.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('60', '365348', 'ANTS', '421', '2006', 'THE ARCHEOLOGY OF MEXICO', 'An in-depth view of the development of pre-Hispanic Mexican and Meso-American cultures and civilizations from Paleo-Indian times to the Spanish Conquest; an overview of the major archeological sites pertinent to these cultures. (Same as GEOG 421.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14020', '828592', 'ANTS', '422', '2006', 'Latin America: Peoples And Institutions', 'Description of indigenous social organizations prior to conquest. Survey of community ethnographic studies of African, Amerind, Asiatic and European cultures in contemporary rural and urban Latin America. Prerequisite: Sociology 307 or approval of department head. (Same as SOCL 422.)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('63', '970388', 'ARAB', '201', '2006', 'INTERMEDIATE ARABIC', 'The continuation on the intermediate level of modern standard Arabic.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('64', '962172', 'ART', '101', '2006', 'BASIC DESIGN', 'Introduction to art structure and general analysis of the principles and elements of design. One hour lecture and five hours studio.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('65', '523693', 'ART', '102', '2006', 'THREE DIMENSIONAL DESIGN', 'Application of basic principles and elements of design to three dimensional problems. One hour lecture and five hours studio. Prerequisite: 101. Additional course fee $50.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('66', '893820', 'ART', '103', '2006', 'DRAWING', 'Beginning drawing in a variety of media. One hour lecture and five hours studio.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('67', '349610', 'ART', '104', '2006', 'FIGURE DRAWING', 'Drawing made from models. Six hours studio. Prerequisite: 103. Additional Course Fee $50.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('68', '389054', 'ART', '200', '2006', 'ANALYTICAL PERSPECTIVE', 'The principles of geometry in the development of spatial logic and imagery in one-point, two- point, and isometric perspective will be used. Techniques in rendering support procedures in architecture, illustration, and industrial and environmental design. For art majors and non-art majors. Six hours studio. (Formerly Art 403, Analytical Perspective).', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('69', '616205', 'ART', '203', '2006', 'HANDBUILDING CERAMICS', 'Lectures and demonstrations on fundamental methods of forming, glazing, and firing ceramics. One hour lecture and five hours studio. Additional course fee $50.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('70', '392671', 'ART', '204', '2006', 'WHEEL THROWING CERAMICS', 'Lectures and demonstrations on use of the potter’s wheel. One hour lecture and five hours studio. Additional course fee $50.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('71', '553754', 'ART', '205', '2006', 'GRAPHIC DESIGN', 'Introduction to graphic design concepts and media common to advertising and commercial art. Emphasis on communication through the visual idiom. One hour lecture and five hours studio. Prerequisite: 101 and 103 or approval of department head. (Formerly Advertising Design 205.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('72', '657779', 'ART', '206', '2006', 'GRAPHIC DESIGN', 'Typography, its usage as a design element, the layout and design of publications and introduction to commercial production methods. One hour lecture and five hours studio. Prerequisite: 205. (Formerly Advertising Design 206.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('73', '332491', 'ART', '241', '2006', 'GENERAL CRAFTS', 'Exploration of various craft media. Additional course fee $50.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('74', '515424', 'ART', '242', '2006', 'ARTS AND CRAFTS', 'Application of methods and exploration of media for the exceptional child. One hour lecture and ninety minutes studio.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('75', '716070', 'ART', '246', '2006', 'BLACK AND WHITE PHOTOGRAPHY', 'Photographic theory and practical application of black and white photography. One hour lecture and five hours studio.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('76', '545033', 'ART', '256', '2006', 'COLOR PHOTOGRAPHY', 'Color photography research. One hour lecture and five hours studio. Prerequisite: 246 or equivalent.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('77', '442391', 'ART', '257', '2006', 'LITHOGRAPHY', 'Printing process with Bavarian limestone. One hour lecture and five hours studio. Prerequisite: 101, 104 or approval of department head. Additional course fee $50.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('78', '584620', 'ART', '258', '2006', 'BLOCK PRINTING', 'Printing process in wood and linoleum. One hour lecture and 5 hours studio. Prerequisite: 101, 104 or approval of department head. Additional course fee $50.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('79', '182756', 'ART', '259', '2006', 'ETCHING', 'Printing process with zinc plates. One hour lecture and five hours studio. Prerequisite: 101, 104 or approval of department head. Additional course fee $50.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('80', '520031', 'ART', '260', '2006', 'SILKSCREEN', 'Printing process with stencil techniques. One hour lecture and five hours studio. Prerequisite: 101, 104 or approval of department head. Additional course fee $50.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('81', '253567', 'ART', '271', '2006', 'PAINTING', 'Painting in acrylics. Six hours studio. Prerequisites: 101, 104 or approval of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('82', '34176', 'ART', '272', '2006', 'PAINTING', 'Painting in oils. Six hours studio. Prerequisites: 101, 104 or approval of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('83', '213675', 'ART', '281', '2006', 'SCULPTURE', 'Sculpture problems in a variety of media. One hour lecture and five hours studio. Prerequisite: 102, 104 or approval of department head. Additional course fee $50.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('84', '351606', 'ART', '301', '2006', 'CALLIGRAPHY', 'A comprehensive study of hand lettering. Variations in styles are complemented with a study of motifs executed in pen and ink. For art majors and non-art majors.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('85', '95875', 'ART', '356', '2006', 'ADVANCED COLOR PHOTOGRAPHY', 'Theory and practice of advanced color photography for the creative photographer. Prerequisites: Art 246, 256, or permission of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('86', '716423', 'ART', '402', '2006', 'CREATING PAPER/CASTING AND MOLDING', 'Study and processing of basic skills in creating paper from laminations to cast paper. One hour lecture, 5 hours laboratory per week. Prerequisite: 101.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('87', '895563', 'ART', '406', '2006', 'COMPUTER ART', 'Decision making with the graphic computer, creation to production. Computer-aided visual design including image origination as well as input, output, selection, editing, alteration, and manipulation. Prerequisites: Art 101, 103; Computer Science 170, or approval of department head. Six hours studio.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('88', '809845', 'ART', '407', '2006', 'RAKU CERAMICS', 'Lecture and demonstrations on use of Raku ceramics, including kiln construction and firing. One hour lecture and five hours studio. Additional course fee $50.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('89', '771907', 'ART', '408', '2006', 'ART APPLICATIONS IN TEACHING', 'Mixed media; drawing multi-image processes, enlargement techniques, and three- dimensional poster material are utilized in visual aid techniques.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('90', '851789', 'ART', '409', '2006', 'TECHNIQUES IN THE FINE ARTS', 'A study, analysis, and participation in the productions of various media in the fine arts such as drawing, painting, printing, and sculpture. Additional course fee $50.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('91', '175193', 'ART', '411', '2006', 'ART EDUCATION FOR THE ELEMENTARY SCHOOL', 'This course is designed to present art education students with concepts, methods, and experiences used in creating art activities with children at the elementary level. For juniors, seniors and graduates only. Additional course fee $50.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('92', '600500', 'ART', '412', '2006', 'ART EDUCATION', 'A lecture/discussion/experience course designed to present art education students with theories, strategies, and experiences in designing and teaching art activities. For juniors, seniors, and graduates only. Additional course fee $50.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('93', '969255', 'ART', '418', '2006', 'INCLUSIVE ART INSTRUCTION', 'Objectives, methods, and materials used in teaching gifted, talented, and other diverse learners. For juniors, seniors and graduates only. Additional course fee $50.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('94', '145582', 'ART', '425', '2006', 'RENAISSANCE', 'The visual arts in Italy from the early Renaissance through Mannerism. Prerequisite: 201, 202. (Formerly 325.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('95', '562672', 'ART', '426', '2006', 'NINETEENTH CENTURY', 'Nineteenth Century European and American Rococo through Post-Impressionism periods. Prerequisites: 201, 202, or approval of the department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('96', '492949', 'ART', '429', '2006', 'CONTEMPORARY', 'Twentieth century European and American Art. A study of the major achievements in painting, sculpture, and architecture since 1900. Prerequisite: 201, 202. (Formerly 329.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('97', '39403', 'ART', '431', '2006', 'WATERCOLOR', 'Painting in watercolor. Prerequisites: 101, 103, or approval of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('98', '912282', 'ART', '440', '2006', 'METALCRAFT AND JEWELRYMAKING', 'The designing and fabrication of jewelry and small metal products. One hour lecture and five hours studio. Prerequisite: 102 or approval of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('99', '882003', 'ATMS', '101', '2006', 'INTRODUCTION TO WEATHER AND CLIMATE I', 'An introduction to the science of meteorology with an emphasis on the physical processes working within weather systems. Wind, pressure, precipitation, and radiation processes. Introduction to weather maps and forecasting.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('100', '655607', 'ATMS', '102', '2006', 'INTRODUCTION TO WEATHER AND CLIMATE II', 'Application of physical processes in global circulation of the atmosphere and oceans. Empiric and genetic climatology from a local, regional and global perspective. Global climate change and air pollution. Policy implications of meteorology and climate research. Prerequisite: 101.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('101', '304953', 'ATMS', '104', '2006', 'BASIC METEOROLOGY LABORATORY II', 'A laboratory course designed to expose students to basics of climatology. Topics include atmospheric elements, the earth’s energy balance, climate regions, and global climate change. Prerequisites: 103.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('102', '435758', 'ATMS', '110', '2006', 'MICROCOMPUTERS IN METEOROLOGY', 'Two hours of laboratory work learning to operate personal computer systems dealing with meteorological data. Discussions will consider data distribution, reception and archival; IBM operating systems; Weathercapture software; DIFAX; and the PC-McIDAS network.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('103', '313387', 'ATMS', '303', '2006', 'ATMOSPHERIC THERMODYNAMICS', 'Introduction to classical and statistical thermodynamics as it applies to dry and moist atmospheric phenomena. Ideal Gas Law, First and Second Laws of Thermodynamics. Thermodynamic potentials and diagrams. Moisture variables. Prerequisites: 205, MATH 131, PHYS 207.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('104', '637443', 'ATMS', '304', '2006', 'INTRODUCTION TO ATMOSPHERIC KINEMATICS AND DYNAMIC', 'Vector operations in meterology, horizontal flow patterns, fundamental forces in the atmosphere, basic laws of conservation of momentum, mass and energy applied to the atmosphere. Prerequisites: 205, MATH 131.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('105', '951181', 'ATMS', '315', '2006', 'PHYSICAL METEOROLOGY', 'Introduction to physical principles underlying atmospheric radiation, cloud physics and precipitation processes. Prerequisite: ATMS 304, PHYS 208.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('106', '29457', 'ATMS', '350', '2006', 'SPECIAL TOPICS IN OPERATIONAL METEROLOGY', 'Provides the students with work experience at a National Weather Service Forecast Office.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('107', '182477', 'ATMS', '407', '2006', 'METEOROLOGICAL INSTRUMENTATION AND DATA', 'A survey of the characteristics of meteorological instrumentation and techniques for machine (computer) processing meteorological data. Introduction to remote sensing techniques employing meteorological satellites and weather radar. Prerequisites: 205 and PHYS 208.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('108', '393573', 'ATMS', '409', '2006', 'SEVERE STORMS', 'An introduction to the dynamic and thermodynamic structure of severe local storms, including thunderstorms, tornadoes, and hail storms and a survey of techniques for forecasting the occurrence and movement of these storms. Prerequisites: 205, 315, and PHYS 207.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('109', '614077', 'ATMS', '411', '2006', 'TROPICAL METEOROLOGY', 'An examination of the structure and dynamics of the tropical atmosphere. Convection in the tropics, tropical cyclones (hurricanes) and an introduction to monsoon circulation. Prerequisites: 205, 306, 315 and PHYS 208.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('110', '365232', 'ATMS', '450', '2006', 'ATMOSPHERIC POLLUTION', 'A study of elements, effects and meteorology of air pollution including the &quot;Greenhouse&quot; effect, acid rain, and ozone depletion problems. Air pollution modeling and prediction is also covered. Prerequisites: Completion of freshman mathematics requirements and CHEM 107.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('111', '913603', 'AVIA', '101', '2006', 'INTRODUCTION', 'Introduction to the history and development of aviation, with an analysis of its impact on national and international affairs. A non-technical survey.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('112', '867643', 'AVIA', '102', '2006', 'INTRODUCTION TO SPACE TRAVEL', 'An introduction to the history and technology of man’s efforts in space including an analysis of the social and technological impact on global affairs of the American/Soviet space race. A non-technical survey.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('113', '399407', 'AVIA', '112', '2006', 'PRIVATE PILOT FLIGHT INSTRUCTION', 'Flight instruction and solo practice required for FAA private pilot certificate. Course completion requires that the certificate be issued. Grades of CR (Credit) or NC (No Credit) will be awarded.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('114', '127279', 'AVIA', '220', '2006', 'INSTRUMENT PILOT GROUND SCHOOL', 'Meteorology, federal regulating on air traffic control procedures, navigation radio communications, automatic direction finding, VHF omnidirectional radio range, LORAN and GPS navigation, radar, chart reading clearance coping. Prerequisite: Private or commercial pilot certification or department head approval.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('115', '219250', 'AVIA', '221', '2006', 'INSTRUMENT GROUND TRAINER LAB', 'Development of skills of scan, interpretation, and control for instrument flight. Prerequisite: 220 or concurrent enrollment.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('116', '495281', 'AVIA', '222', '2006', 'INSTRUMENT PILOT FLIGHT INSTRUCTION', 'Flight instruction required for FAA instrument pilot rating, given in actual or simulated instrument meteorological conditions. Course completion requires that the rating be issued. Grades of CR (Credit) or NC (No Credit) will be awarded. Prerequisite: Private pilot certificate. (Formerly 332)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('117', '843702', 'AVIA', '302', '2006', 'AVIATION WEATHER', 'The interpretation of meteorological phenomena affecting aircraft flight. A study of the basic concepts of aviation meteorology; temperature, pressure, moisture, stability, clouds, air-masses, fronts, thunderstorms, icing, and fog. Analysis and use of weather data for flight planning and safe flying; interpretation of United States Weather Bureau maps, reports and forecasts.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('118', '114813', 'AVIA', '310', '2006', 'COMMERCIAL GROUND INSTRUCTION', 'Advanced principles of flight, advanced navigation, aircraft systems, advanced meteorology. Aeronautical knowledge required for Commercial Pilot Certification. Prerequisite: Private Pilot Certification or approval of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('119', '305127', 'AVIA', '311', '2006', 'COMMERCIAL PILOT FLIGHT INSTRUCTION', 'Flight instruction and practice required for FAA commercial pilot certificate. Course completion requires that the certificate be issued. Grades of CR (Credit) or NC (No Credit) will be awarded. Prerequisite: Private pilot certificate with instrument rating. (Formerly 212-213-214)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('120', '615609', 'AVIA', '312', '2006', 'AGRICULTURAL PILOT FLIGHT INSTRUCTION', 'Flight instruction and practice required for FAA commercial pilot certificate, with emphasis on tailwheel and agricultural-type airplanes, and agricultural aviation operations. Course completion requires that the certificate be issued. Grades of CR (Credit) or NC (No Credit) will be awarded. Prerequisite: Private pilot certificate with instrument rating.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('121', '966602', 'AVIA', '317', '2006', 'FEDERAL AVIATION REGULATIONS', 'Those parts of Title 14, Code of Federal Regulations, Chapter I, that are pertinent to pilots, flight instructors, and aviation administrative professionals.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('122', '480320', 'AVIA', '318', '2006', 'AERIAL APPLICATION', 'Concepts of aerial application of agricultural seed and chemicals; safety, history, regulations, equipment, environmental impact. Prerequisite: Private pilot certificate. (Formerly 217).', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14260', '399563', 'AVIA', '330', '2006', 'Aviation Professionalism', 'Aviation safety and interpersonal relations; aeronautical decision making. Aviation professional credentials. Prerequisite: Junior or Senior in Aviation.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('124', '935858', 'AVIA', '340', '2006', 'MULTIENGINE PILOT GROUND SCHOOL', 'Aeronautical knowledge required for FAA multiengine pilot rating. Prerequisites: 301, 310. (Formerly 321).', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('125', '625901', 'AVIA', '341', '2006', 'MULTIENGINE PILOT FLIGHT INSTRUCTION', 'Flight instruction required for FAA multiengine pilot rating. Course completion requires that the rating be issued. Grades of CR(Credit) or NC(No Credit) will be awarded. Prerequisites: 340 or concurrent enrollment; Commercial pilot certificate with instrument rating. (Formerly 321).', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('126', '778781', 'AVIA', '401', '2006', 'AIR TRANSPORTATION', 'The development and present status of air transportation, federal legislation, characteristics and classification of air carriers; the organization and function of the Federal Aviation Administration.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('127', '428807', 'AVIA', '402', '2006', 'AIR TRAFFIC ADMINISTRATION', 'The National Aerospace System. The role of Air Traffic Control in aviation safety. For Aviation Majors. Prerequisites: 110, 111.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('128', '665304', 'AVIA', '405', '2006', 'AVIATION SAFETY', 'A comprehensive study of aviation safety in both civilian flying and the commercial aviation industry; air transportation safety in the areas of regulations, analysis, accident investigation, and human factors; FAA flight standards and rule making; airline safety, and managing human error.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14263', '512736', 'AVIA', '410', '2006', 'Flight Instructor Ground School', 'Instructional knowledge required for FAA flight instructor certificate. Prerequisite: Commercial pilot certificate with instrument rating. (Formerly 313).', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14269', '310810', 'AVIA', '411', '2006', 'Flight Instructor Flight Instruction', 'Flight instruction required for FAA flight instructor certificate. Course completion requires that the certificate be issued. Grades of CR(Credit) or NC(No Credit) will be awarded. Prerequisite: 410 or concurrent enrollment. (Formerly 314).', '1', '1', '1', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('131', '320911', 'AVIA', '413', '2006', 'INSTRUMENT FLIGHT INSTRUCTOR GROUND SCHOOL', 'Instructional knowledge required for FAA instrument rating on flight instructor certificate. Prerequisite: 411.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('132', '996016', 'AVIA', '414', '2006', 'INSTRUMENT FLIGHT INSTRUCTOR FLIGHT INSTRUCTION', 'Flight instruction for FAA instrument rating on flight instructor certificate. Course completion requires that the rating be issued. Grades of CR (Credit) or NC (No Credit) will be awarded. Prerequisite: 413 or concurrent enrollment.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14275', '566877', 'AVIA', '416', '2006', 'Turbine Engines', 'Fundamentals of theory, design, and operation of aircraft turbine engines.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('134', '634298', 'AVIA', '417', '2006', 'ADVANCED AERIAL APPLICATION', 'Operation of an agricultural aviation service. Economics, flight operations and techniques. Prerequisite: Junior standing in Aviation.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('135', '633459', 'AVIA', '420', '2006', 'AIRLINE TRANSPORT PILOT GROUND SCHOOL', 'Aeronautical knowledge required for FAA airline transport pilot certificate. Prerequisite: Unlimited commercial pilot certificate.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('136', '697749', 'AVIA', '430', '2006', 'AVIATION LAW', 'National and international regulatory and civil law as it relates to aviation and the aviation professional.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('137', '663755', 'AVIA', '470', '2006', 'AIRPORT ADMINISTRATION', 'Aeronautical and regulatory knowledge required of public airport managers.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('138', '815936', 'AVIA', '471', '2006', 'AIRPORT PLANNING AND OPERATIONS', 'Elements of planning and developing an airport.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('139', '91322', 'AVIA', '472', '2006', 'AIR TRANSPORTATION LOGISTICS', 'Transportation economics and logistics, including systems of goods movement, principles of transportation, logistics, costs and services, rate making, management and coordination.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('140', '277832', 'AVIA', '403', '2006', 'AEROSPACE EDUCATION', 'Fundamentals of flight. Aeronautical knowledge equivalent to that required by the Federal Aviation Administration private pilot written examination. For Aerospace Education teachers, K-12.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('141', '181168', 'AVIA', '404', '2006', 'ADVANCED AEROSPACE EDUCATION', 'Provides Aerospace Education teachers with advanced technological orientation, and foundation in the aerospace career fields.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('142', '4926', 'BIOL', '101', '2006', 'THE LIVING WORLD', 'Basic scientific and biological principles with an emphasis on the structure and processes of organisms. The course also includes a survey of the major types of living organisms. Non-majors only. No prerequisite.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('143', '145476', 'BIOL', '102', '2006', 'THE LIVING WORLD LABORATORY', 'A laboratory course to accompany 101. Two hours laboratory. Prerequisite: Credit or registration in 101, or approval.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('144', '580574', 'BIOL', '103', '2006', 'INTRODUCTION TO SCIENTIFIC RESEARCH', 'A hands-on approach to understanding the scientific method with emphasis on project design, and data collection and analysis. Prerequisite: Admission to ULM Summer Collegiate Program and ULM-HHMI Pre-College Research Program.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('145', '132204', 'BIOL', '110', '2006', 'HUMAN BIOLOGY', 'A comprehensive study of the structure and function of the human body and of the ecological problems that face mankind today. Non-majors only. Prerequisite: 101.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('146', '364725', 'BIOL', '111', '2006', 'HUMAN BIOLOGY LABORATORY', 'A laboratory course to accompany 110. Two hours laboratory. Prerequisite: Credit or registration in 110, or approval.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('147', '75856', 'BIOL', '115', '2006', 'Fundamentals of Anatomy and Physiology II', 'A continuation of 114. Structure and functions of the circulatory, respiratory, digestive, excretory, endocrine, and reproductive systems. Prerequisites: &quot;C&quot; or better in 114, 116; credit or registration in 117. Non-majors only. Cannot be taken for credit if credit has been awarded in 312. (Formerly Zoology 112.)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('148', '975905', 'BIOL', '117', '2006', 'Fundamentals of Anatomy and Physiology Laboratory', 'A laboratory course to accompany 115. Two hours laboratory. Prerequisite: Credit or registration in 115. Non-majors only. (Formerly Zoology 114.)', '1', '1', '1', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('149', '479538', 'BIOL', '120', '2006', 'PRINCIPLES OF BIOLOGY I', 'A course designed for those students majoring in a science-related field. Course content deals with scientific methodology, DNA and the genetic code, cell structure and cell development.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('150', '380982', 'BIOL', '121', '2006', 'PRINCIPLES OF BIOLOGY I LABORATORY', 'A laboratory designed to accompany 120. Prerequisites: credit or registration in 120.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('151', '591515', 'BIOL', '122', '2006', 'PRINCIPLES OF BIOLOGY II', 'A continuation of Biology 120. Course content provides an introduction to evolution, ecology and biological diversity. Prerequisites: credit with a grade of C or better in BIOL 120.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('152', '446140', 'BIOL', '123', '2006', 'PRINCIPLES OF BIOLOGY II LABORATORY', 'A laboratory designed to accompany Biology 122. Prerequisites: credit with a grade of &quot;C&quot; or better in BIOL 120, 121; credit or registration in BIOL 122.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('154', '807624', 'BIOL', '204', '2006', 'PLANT DIVERSITY', 'The characteristics and reproduction of algae, fungi, bryophytes, ferns and fern allies, and seed plants. Vascular plants covered in greater detail. The relationships of these groups of plants to each other and to man is emphasized. Ecology and economic botany is woven throughout the study of diversity and distribution of these plants. Prerequisites: 122, 123. (Formerly Botany 204.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('155', '381998', 'BIOL', '205', '2006', 'PLANT DIVERSITY LABORATORY', 'A laboratory course in plant diversity to accompany 204. Prerequisites: Credit or registration in 204. (Formerly Botany 205.)', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('157', '476176', 'BIOL', '209', '2006', 'ANIMAL DIVERSITY LABORATORY', 'A laboratory course in animal diversity to accompany 208. Prerequisites: Credit or registration in 208. (Formerly Zoology 210.)', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14450', '810805', 'BIOL', '214', '2006', 'Introductory Microbiology', 'The distribution and importance of microorganisms in nature. Methods of studying and controlling microorganisms. Their role in agriculture, industry and medicine. Prerequisites: Sophomore or higher standing and an earned grade of no less than &quot;C&quot; in one of the following: 101, 114, 120 or equivalent. (Formerly MICRO 201.)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14444', '152508', 'BIOL', '215', '2006', 'Introductory Micro Laboratory', 'A laboratory course to accompany 214. Two hours laboratory. Prerequisite: Credit or registration in 214. (Formerly MICRO 203.)', '1', '1', '1', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('160', '277469', 'BIOL', '220', '2006', 'CELL BIOLOGY', 'A survey of the structural and functional relationships operative at the cellular and molecular levels of organization. Prerequisites: 120, 121, CHEM 108.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('161', '121616', 'BIOL', '226', '2006', 'HUMAN ANATOMY AND PHYSIOLOGY', 'Structure and function of the principal organ systems of the human body. Cannot be taken for credit if credit has been awarded for 311 or 312. Non- majors only. (Formerly Zoology 205.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('162', '473419', 'BIOL', '228', '2006', 'PATHOPHYSIOLOGY', 'A detailed study of nervous, endocrine, cardiovascular, and reproductive systems, and their pathophysiology. Prerequisites: Two semesters of anatomy and physiology. (Formerly Zoology 206.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('163', '273485', 'BIOL', '303', '2006', 'PRINCIPLES OF ECOLOGY', 'The basic principles of the relationships between plants and animals and their environment. Prerequisites: 122, 123, or 220; credit or registration in 304. (Formerly 301.) Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('164', '868182', 'BIOL', '304', '2006', 'PRINCIPLES OF ECOLOGY LABORATORY', 'A laboratory course to accompany 303. Three hours laboratory. Field trip fee required. Prerequisite: Credit or registration in 303. (Formerly 302.) Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('165', '650316', 'BIOL', '305', '2006', 'GENETICS', 'Fundamental concepts of heredity at the molecular, cellular, organismic, and population levels. Nature of the genetic material and its involvement in the determination of structure and function. Consideration given to both classical and modern genetics. Prerequisite: 122, 123; recommended, 220. F', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('166', '906944', 'BIOL', '306', '2006', 'GENETICS LABORATORY', 'A laboratory to accompany 305. Lab topics include Mendelian genetics to molecular genetics. Three hours laboratory. Prerequisites: credit or registration in 305.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('167', '565932', 'BIOL', '308', '2006', 'INTRODUCTION TO EVOLUTIONARY BIOLOGY', 'A survey of fundamental concepts of modern evolutionary theory, including natural selection, speciation, and adaptation, followed by an investigation of recent issues and controversies in evolutionary biology. Reading and discussion of primary sources will be emphasized. Prerequisite: 305.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('168', '771471', 'BIOL', '311', '2006', 'HUMAN PHYSIOLOGY I', 'Basic physiological processes with emphasis on cells, tissues and the skeletal, muscular, and nervous systems. Prerequisites: 120, 121; CHEM 107, 109. (Formerly Zoology 311.) F', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('169', '722880', 'BIOL', '312', '2006', 'HUMAN PHYSIOLOGY II', 'A continuation of 311. Emphasis on circulatory, respiratory, digestive, excretory, endocrine, and reproductive systems. Prerequisites: 311. (Formerly Zoology 312.) Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('170', '657254', 'BIOL', '313', '2006', 'HUMAN PHYSIOLOGY LABORATORY', 'A laboratory course to accompany 312. Three hours laboratory. Prerequisite: Credit or registration in 312. (Formerly Zoology 314.) Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('171', '49303', 'BIOL', '316', '2006', 'COMPARATIVE ANATOMY', 'Study of the structure and evolutionary development of organs and organ systems in vertebrates. Laboratory requires student to purchase dissection specimens. Prerequisite: 208. (Formerly Zoology 201, 202.) Sp, even', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('172', '904048', 'BIOL', '320', '2006', 'CONSERVATION BIOLOGY', 'A course dealing with biodiversity and its conservation. Topics include biodiversity, habitat loss, the effects of habitat changes on populations and the design and establishment of reserves. Prerequisites: 201 or 303, 305.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('173', '662179', 'BIOL', '330', '2006', 'INVERTEBRATE ZOOLOGY', 'The morphology and phylogeny of representative invertebrates. Three hours lecture and three hours laboratory. Prerequisite: 208. (Formerly Zoology 307, 308.)', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('174', '194779', 'BIOL', '332', '2006', 'VERTEBRATE ZOOLOGY', 'The morphology, taxonomy, and life history of representative vertebrates. Three hours lecture and three hours laboratory with field trips. Prerequisite: 208. (Formerly Zoology 309, 310.) Sp, even', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('175', '484623', 'BIOL', '404', '2006', 'ECOLOGICAL METHODS', 'Contemporary methods employed in plant and animal ecology. Prerequisites: 303, 304.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('176', '794383', 'BIOL', '405', '2006', 'EVOLUTIONARY POPULATION GENETICS', 'A study of genetic variation in populations and its role in the evolutionary process. Prerequisite: 305. Sp, odd', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('177', '559503', 'BIOL', '408', '2006', 'CELLULAR PHYSIOLOGY', 'Physical and chemical aspects of the living organism including an introduction to molecular biology with emphasis on the cell. Prerequisites: CHEM 303, 304, 305, 306; credit or registration in 409. (Formerly 401.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('178', '560478', 'BIOL', '409', '2006', 'CELLULAR PHYSIOLOGY LAB', 'A laboratory course to accompany 408. Three hours laboratory. Prerequisite: credit or registration in 408. (Formerly 402.)', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('179', '770289', 'BIOL', '412', '2006', 'PLANT PHYSIOLOGY', 'An introduction to the chemical and physical processes occurring in plants. Three hours lecture and three hours laboratory. Prerequisites: 204, CHEM 108, 110; MATH 116. (Formerly Botany 409.)', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('180', '39041', 'BIOL', '414', '2006', 'ANIMAL PHYSIOLOGY', 'An introduction to the processes by which animals function in their environment. Three hours lecture and three hours laboratory. Prerequisites: 208; MATH 116. (Formerly Zoology 412.)', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('181', '941460', 'BIOL', '416', '2006', 'PHYSIOLOGY OF REPRODUCTION', 'Study of the physiological and environmental processes involved in mammalian reproduction. (Formerly Zoology 429.) Sp, odd.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('182', '361804', 'BIOL', '422', '2006', 'HISTOLOGY', 'Microscopic structure of the cell; cellular organizations; tissues; the relationship of structure to function. Three hours lecture and three hours laboratory. Prerequisite: 208. (Formerly Zoology 415, 417.)', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('183', '485181', 'BIOL', '424', '2006', 'EMBRYOLOGY', 'The structure and maturation of germ cells, the fertilization and early development of vertebrates. Three hours lecture and three hours laboratory. Prerequisite: 208. (Formerly Zoology 419, 420.)', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('184', '802763', 'BIOL', '425', '2006', 'NEUROLOGY', 'Study of the anatomy, physiology and pathology of the human nervous system. (Formerly Zoology 425.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('185', '169429', 'BIOL', '426', '2006', 'NEUROLOGY LABORATORY', 'A laboratory course to accompany 425. Three hours laboratory. Prerequisite: Credit or registration in 425. (Formerly Zoology 426.)', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('186', '867179', 'BIOL', '430', '2006', 'DNA ANALYSIS IN MOLECULAR BIOLOGY', 'A laboratory- based course focused on generating and analyzing DNA data. Methods include introductions, protocols and trouble shooting for PCR, RAPD’s, DNA sequencing, alignment and phylogentic analyses. Prerequisites: Biology 305.', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('187', '289967', 'BIOL', '432', '2006', 'AQUATIC PLANTS', 'A study of the plants that grow in aquatic and wetland sites. Emphasis placed on identification, distribution, and field characteristics. Two hours lecture and four hours laboratory including frequent field trips. Prerequisite: 122, 123. (Formerly Botany 403, 404.)', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('188', '755834', 'BIOL', '434', '2006', 'FIELD BOTANY', 'Field characteristics and sight identification of the regional flora is emphasized. Two hours lecture and four hours laboratory including frequent field trips. Prerequisite: 122, 123. (Formerly Botany 411, 412.)', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('189', '677984', 'BIOL', '436', '2006', 'DENDROLOGY', 'Classification, identification, and characteristics of important forest trees of the United States, stressing native species common to Louisiana. Two hours lecture and four hours laboratory including frequent field trips. Prerequisite: 122, 123. (Formerly Botany 413, 414.)', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('190', '442474', 'BIOL', '438', '2006', 'BIOLOGY OF NONSEED PLANTS', 'The characteristics, classification, phylogeny, and life cycles of fungi, algae, mosses, hornworts, liverworts, and ferns are emphasized. Two hours lecture and four hours laboratory including frequent field trips. Prerequisite: 122, 123. (Formerly Botany 415, 416.)', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('191', '33304', 'BIOL', '440', '2006', 'TAXONOMY OF VASCULAR PLANTS', 'Characteristics of plant families are emphasized. This includes classification systems and emphasis on primitive and advanced characteristics. Most of the lab is devoted to identification of plants by using keys from plant identification manuals Two hours lecture and four hours laboratory. Prerequisite: 204. (Formerly Botany 417, 418.)', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('192', '799599', 'BIOL', '442', '2006', 'AGROSTOLOGY', 'The identification, classification and ecology of grass and grass-like plants. Three hours lecture and three hours laboratory with scheduled field trips. Prerequisite: 204. (Formerly Botany 419, 420.)', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('193', '915892', 'BIOL', '444', '2006', 'PLANT ECOLOGY', 'The study of plant populations and communities and their habitat with emphasis on the communities of the south central United States. Three hours lecture and three hours laboratory. Prerequisite: 303, 304. (Formerly Botany 426, 427.)', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('194', '306788', 'BIOL', '446', '2006', 'ECONOMIC BOTANY', 'The study of applied uses of plants as food, fibers, lumber, drugs, and related commodities. Prerequisite: 204. (Formerly Botany 481.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('195', '667782', 'BIOL', '447', '2006', 'ECONOMIC BOTANY LABORATORY', 'A laboratory course to accompany 446. Two hours laboratory. Prerequisite: Credit or registration in 446. (Formerly Botany 482.)', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('196', '23610', 'BIOL', '451', '2006', 'FIELD BIOLOGY', 'Field studies of the flora and fauna native to selected habitats. Field trip fee required. Prerequisites: 204, 205, 208, 209; approval of department head. Sum', '6', '6', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('197', '213733', 'BIOL', '456', '2006', 'INTRODUCTION TO ENTOMOLOGY', 'The anatomy, physiology, and classification of insects. Three hours lecture and three hours laboratory. Prerequisite: 208. (Formerly Zoology 450, 451.)', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('198', '233714', 'BIOL', '460', '2006', 'PATHOGENIC BACTERIOLOGY', 'The morphology, physiology, and immunogenecity of disease-causing bacteria; methods of isolation, identification and control of pathogenic bacteria and diseases. Three hours lecture. Prerequisites: 214, 215. (Formerly MICRO 417, 418.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('199', '795080', 'BIOL', '462', '2006', 'Food And Dairy Microbiology', 'The role of microorganisms in the preservation, spoilage and poisoning in food and dairy products. Also, their role in the manufacture of products of agricultural origin. Prerequisite: 214; credit or registration in 463. (Formerly MICRO 423.)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('200', '936613', 'BIOL', '463', '2006', 'FOOD AND DAIRY MICRO LABORATORY', 'A laboratory course to accompany 462. Three hours laboratory. Prerequisite: Credit or registration in 462. (Formerly MICRO 424.)', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('201', '890967', 'BIOL', '464', '2006', 'WATER MANAGEMENT', 'A survey of water distribution and uses, including water purification and sewage treatment, industrial and agricultural, irrigation scheduling. (Formerly MICRO 421.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('202', '844383', 'BIOL', '466', '2006', 'ADVANCED MICROBIOLOGY', 'Advanced microbiological techniques; a study of the morphological, physiological, and genetic characteristics of important groups of microorganisms; introduction to biotechnology. Two hours lecture and two hours laboratory. Prerequisites: 214, 215; 8 semester hours of chemistry. (Formerly MICRO 415, 416.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('203', '840661', 'BIOL', '468', '2006', 'GENERAL VIROLOGY', 'A study of representative bacterial, animal and plant viruses. Prerequisites: 214; credit or registration in 469. (Formerly MICRO 425.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('204', '553147', 'BIOL', '469', '2006', 'GENERAL VIROLOGY LABORATORY', 'A laboratory to accompany 468. Three hours laboratory. Prerequisite: Credit or registration in 468. (Formerly MICRO 426.)', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('205', '39162', 'BIOL', '470', '2006', 'IMMUNOLOGY - CONCEPTS AND PRINCIPLES', 'Detailed discussion of the cellular and humoral components of the immune system and their interrelationships in the production and control, at both the genetic and cellular levels, of immune responses. Prerequisites: 214, 115 or 312 or consent of instructor. (Formerly MICRO 430.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('206', '325285', 'BIOL', '472', '2006', 'INTRODUCTORY PARASITOLOGY', 'The taxonomy, life history, and economic importance of protozoa, helminths, and arthropods which are parasites of man. Prerequisites: 122 or 214 or permission of the instructor; credit or registration in 473. (Formerly Zoology 401.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('207', '347530', 'BIOL', '473', '2006', 'INTRODUCTORY PARASITOLOGY LABORATORY', 'A laboratory course to accompany 472. Three hours laboratory. Prerequisite: Credit or registration in 472. (Formerly Zoology 402.)', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('208', '598664', 'BIOL', '476', '2006', 'FRESHWATER ECOLOGY', 'The biological, chemical, and physical characteristics of freshwater habitats. Three hours lecture and three hours laboratory. Field trip fee required. Prerequisites: 122, 123. (Formerly Biology 406, 407.)', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('209', '885762', 'BIOL', '478', '2006', 'ICHTHYOLOGY', 'Evolution, systematics and ecology of fishes with an emphasis on North American freshwater fishes. Two hours lecture and six hours laboratory with field trips. Prerequisites: 122, 123. (Formerly Zoology 405, 406.) F, even', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('210', '117820', 'BIOL', '479', '2006', 'FISHERIES BIOLOGY', 'General principles and methods of freshwater fisheries biology and the management of natural fish populations. Three hours of lecture and three hours of laboratory. Laboratory fee required. Prerequisite: Biology 208, 209; Math 114, 116 or equivalent. Sp, odd.', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('211', '637705', 'BIOL', '480', '2006', 'HERPETOLOGY', 'Morphology, taxonomy, and life history of amphibians and reptiles with emphasis on the taxonomy of the amphibians and reptiles of Louisiana. Three hours lecture and three hours laboratory with field trips required. Prerequisite: 122, 123. (Formerly Zoology 427, 428.) Sp, odd', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('212', '827223', 'BIOL', '482', '2006', 'MAMMALOGY', 'The morphology, taxonomy, and life history of mammals. Three hours lecture and three hours laboratory with field trips. Prerequisite: 122, 123. (Formerly Zoology 423, 424.) F, odd.', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('213', '479623', 'BIOL', '484', '2006', 'ORNITHOLOGY', 'Morphology, taxonomy, behavior, and natural history of birds. Two hours lecture and three hours laboratory with field trips required. Prerequisite: 122, 123. (Formerly Zoology 431, 432.) Sp, even.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('214', '122887', 'BIOL', '486', '2006', 'GAME BIRDS', 'Recognition, life histories, predators, economics and management practices of North American game birds. Three hours lecture and three hours laboratory with field trips. Prerequisite: Introductory course in Biology and permission of the instructor. (Formerly Zoology 433, 434.) Sp, odd', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('215', '629986', 'BIOL', '488', '2006', 'GAME MAMMALS AND FUR BEARERS', 'Recognition, life histories, predators, economics and management practices of game mammals and fur bearers with special emphasis on regional fauna. Three hours lecture and three hours laboratory with field trips. Prerequisite: Introductory course in Biology and permission of the instructor. (Formerly Zoology 473, 474.) F, even', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('216', '649052', 'BIOL', '490', '2006', 'ESTUARINE ZOOLOGY', 'An introduction to basic estuarine ecology with a focus on the ecology and systematics of major aquatic invertebrates and fishes native to estuaries of the northern Gulf of Mexico. Three hours lecture and three hours laboratory. Field trip fee required. Prerequisite: 122, 123. (Formerly Zoology 435.) Sp, odd', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('217', '990065', 'BIOL', '492', '2006', 'ANIMAL ECOLOGY', 'A study of animal populations, communities and their interactions. Two hours lecture and two hours laboratory. Prerequisites: 303, 304 or equivalent. (Formerly Zoology 440.) F, even', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('218', '919952', 'BIOL', '497', '2006', 'UNDERGRADUATE RESEARCH PARTICIPATION', 'An honors course in which the student performs independent research. Prerequisite: Approval of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('219', '404886', 'BIOL', '498', '2006', 'UNDERGRADUATE RESEARCH PARTICIPATION', 'A continuation of 497.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('220', '668049', 'BUSN', '101', '2006', 'BUSINESS AND SOCIETY', 'A systems approach to the nature of business and its role in society, emphasizing business functions, ethics and values, and the interfaces of business with its environment. Not open to juniors and seniors in the College of Business Administration. F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('221', '362427', 'BUSN', '305', '2006', 'BUSINESS COMMUNICATION', 'Investigation of business communication problems; objectives, principles, methods, channels, media, and forms of business communication; organizing, analyzing, writing correspondence and reports, and reporting orally. Prerequisites: ENGL 101, 102 with grade of &quot;C&quot; or higher and Junior standing. (Formerly OINS 305) F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('222', '438189', 'BLAW', '401', '2006', 'LEGAL ENVIRONMENT OF BUSINESS', 'Analysis of the legal environment of business. Regulatory activity, policy considerations and legal trends are integrated into a detailed treatment of specific legal subjects; contracts, torts, arbitration, products liability and criminal law in business management. Prerequisite: Junior standing. F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('223', '467649', 'BLAW', '402', '2006', 'BUSINESS LAW', 'Study of laws involved in operating businesses, emphasizing forms of organization. Topics include negotiable instruments, credit transactions, security devices, agency, lease and franchise. Prerequisite: Junior standing. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('224', '278320', 'CHEM', '100', '2006', 'THE WORLD OF CHEMISTRY', 'Chemical principles, facts and theories, revealed through practical applications, computer graphic illustrations and experiments. Chemistry’s historical foundations, recent developments, and future directions. Prerequisites: Eligibility for MATH 111 or a more advanced mathematics course. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('225', '744977', 'CHEM', '101', '2006', 'INTRODUCTORY CHEMISTRY I', 'The fundamentals of inorganic chemistry. Prerequisite: Eligibility for MATH 111 or a more advanced mathematics course. Three hours lecture. For non-majors. F, Sp, Su1', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('226', '135431', 'CHEM', '102', '2006', 'INTRODUCTORY CHEMISTRY II', 'The fundamentals of organic and biochemistry. Prerequisite: &quot;C&quot; or better in CHEM 101. Three hours lecture. For non-majors. F, Sp, Su2', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('227', '301929', 'CHEM', '103', '2006', 'INTRODUCTORY CHEMISTRY LABORATORY I', 'A laboratory course to accompany CHEM 101. Three hours of laboratory. Prerequisite: Credit or registration in CHEM 101. For non-majors. F, Sp, Su1', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('228', '958710', 'CHEM', '104', '2006', 'INTRODUCTORY CHEMISTRY LABORATORY II', 'A laboratory course to accompany CHEM 102. Three hours of laboratory. Prerequisite: &quot;C&quot; or better in CHEM 103 and registration or credit in CHEM 102. For non-majors. Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('229', '369145', 'CHEM', '105', '2006', 'CHEMISTRY IN SOCIETY', 'Fundamental concepts of chemistry with an emphasis on its impact on society, industry and the environment. Three hours of lecture per week. Prerequisites: &quot;C&quot; or better in CHEM 101. For non- majors. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('230', '97009', 'CHEM', '106', '2006', 'CHEMISTRY IN SOCIETY LABORATORY', 'A laboratory course to accompany CHEM 105. Three hours of laboratory per week. Prerequisites: &quot;C&quot; or better in CHEM 103 and registration or credit in CHEM 105. Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('231', '895323', 'CHEM', '107', '2006', 'GENERAL CHEMISTRY I', 'A course in modern chemistry theories and principles; quantitative approach and problem solving; descriptive chemistry of selected elements and compounds. Three hours of lecture and one hour of recitation. Prerequisite: &quot;C&quot; or better in MATH 111 or 113, or equivalent, or a score of 20 or better on the Mathematics component of the ACT. F, Sp, Su1', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('232', '260112', 'CHEM', '108', '2006', 'GENERAL CHEMISTRY II', 'A continuation of CHEM 107; continuation of theory with emphasis on chemical equilibria; a quantitative approach to solution chemistry; descriptive chemistry of selected elements and compounds from the main groups and the first transition series. Three hours of lecture and one hour of recitation. Prerequisite: &quot;C&quot; or better in CHEM 107. F, Sp, Su2', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('233', '941392', 'CHEM', '109', '2006', 'GENERAL CHEMISTRY LABORATORY I', 'A laboratory course to accompany 107. Three hours laboratory. Prerequisite: Credit or registration in CHEM 107. F, Sp, Su1', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('234', '735985', 'CHEM', '110', '2006', 'GENERAL CHEMISTRY LABORATORY II', 'A laboratory course to accompany CHEM 108 including qualitative analysis. Prerequisite: &quot;C&quot; or better in CHEM 109; credit or registration in CHEM 108. F, Sp, Su2', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('235', '813259', 'CHEM', '220', '2006', 'INTRODUCTION TO PHYSICAL CHEMISTRY', 'Discussion of basic chemical principles, including thermodynamics, kinetics, equilibrium, and quantum mechanics, with applications to biological molecules. Three hours lecture. Prerequisites: &quot;C&quot; or better in CHEM 108. Sp (Formerly 302)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13791', '849359', 'CHEM', '231', '2006', 'Organic Chemistry Laboratory I', 'Laboratory to accompany Organic Chemistry I. Introduction to procedures used in the organic laboratory. Three hours laboratory. Prerequisite: credit or registration in CHEM 2030 and a &#39;C&quot; or better in CHEM 1010. F, Sp, Su1 (Formerly 305)', '1', '1', '1', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13797', '980553', 'CHEM', '232', '2006', 'Organic Chemistry II', 'Spectroscopic techniques and the chemistry of aromatic compounds, carbonyl compounds, and amines will be covered. Three hours laboratory. Prerequisite: &quot;C&quot; or better in CHEM 230. (Formerly 304)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13803', '160789', 'CHEM', '233', '2006', 'Organic Chemistry Laboratory II', 'Laboratory to accompany Organic Chemistry II. Application of techniques from Organic Chemistry I to organic reactions. Three hours laboratory. Prerequisite: &quot;C&quot; or better in CHEM 2031 and credit or registration in CHEM 2032. F, Sp, Su2 (Formerly 306)', '1', '1', '1', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14287', '579218', 'CHEM', '240', '2006', 'Quantitative Analysis', 'Discussion of techniques and theories of gravimetric, volumetric and physicochemical methods of analysis. Three hours lecture. Prerequisite: &quot;C&quot; or better in CHEM 108. F (Formerly 307)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14293', '947032', 'CHEM', '241', '2006', 'Quantitative Analysis Laboratory', 'A laboratory course to accompany CHEM 240. Six hours laboratory. Prerequisite: &quot;C&quot; or better in CHEM 110 and credit or registration in CHEM 240. F (Formerly 308)', '2', '2', '2', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('241', '278608', 'CHEM', '310', '2006', 'DESCRIPTIVE INORGANIC CHEMISTRY', 'Discussion of elemental properties, elemental combinations into ionic and molecular compounds, and periodic trends. Laboratory synthesis and characterization of inorganic compounds. Three hours lecture; three hours laboratory. Prerequisites: &quot;C&quot; or better in CHEM 108. Sp (Formerly 313/314).', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14299', '216924', 'CHEM', '320', '2006', 'Physical Chemistry I', 'Fundamental interpretations of the physical principles of chemistry with emphasis on equilibrium thermodynamics and chemical kinetics. Three hours lecture and one hour recitation. Prerequisites: &quot;C&quot; or better in CHEM 108; PHYS 208; MATH 132. F (Formerly 401)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('243', '944445', 'CHEM', '321', '2006', 'PHYSICAL CHEMISTRY LABORATORY I', 'Modern experimental techniques in physical chemistry. Three hours laboratory. Prerequisites: &quot;C&quot; or better in CHEM 241 and credit or registration in CHEM 320. F.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14080', '758231', 'CHEM', '322', '2006', 'Physical Chemistry II', 'Continuation of Chemistry 320. Theories of atomic and molecular structure, spectroscopy and quantum mechanics are applied to chemical systems. Three hours lecture and one hour recitation. Prerequisite: &quot;C&quot; or better in CHEM 320. Sp (Formerly 402)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('245', '559272', 'CHEM', '323', '2006', 'Physical Chemistry Laboratory II', 'Modern experimental techniques in physical chemistry. Three hours laboratory. Prerequisites: &quot;C&quot; or better in CHEM 241 and credit or registration in CHEM 320. F.', '1', '1', '1', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14305', '404242', 'CHEM', '350', '2006', 'Biochemistry I', 'Introduction to structure and function of proteins, nucleic acids, lipids and carbohydrates and enzyme kinetics, mechanisms, regulation, and vitamins. Three hours lecture. Prerequisite: &quot;C&quot; or better in CHEM 232. F, Su1 (Formerly 336)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13875', '245955', 'CHEM', '351', '2006', 'Biochemistry Laboratory I', 'Laboratory to accompany Biochemistry I. Introduction to procedures used in Biochemistry Laboratory. Three hours laboratory. Prerequisites: Credit or registration in CHEM 350 and &quot;C&quot; or better in CHEM 233. F', '1', '1', '1', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14311', '329875', 'CHEM', '352', '2006', 'Biochemistry II', 'Thermodynamics, metabolism, photosynthesis, protein and nucleic acid biosynthesis. Three hours lecture. Prerequisite: &quot;C&quot; or better in CHEM 350. Sp (Formerly 337)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('249', '324195', 'CHEM', '407', '2006', 'INSTRUMENTAL ANALYSIS', 'A laboratory/lecture analytical course in the operation of analytical instruments, involving the care and maintenance of equipment in addition to the analytical treatment of known samples. Four hours laboratory; three hour lecture. Prerequisite: &quot;C&quot; or better in CHEM 232, 233, 240, 241. Sp', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('250', '812169', 'CHEM', '420', '2006', 'ADVANCED PHYSICAL CHEMISTRY', 'An introduction to group theory including molecular symmetry, molecular orbital theory, electronic spectroscopy, and vibrational spectroscopy. Three hours of lecture per week. Prerequisite: &quot;C&quot; or better in CHEM 322. F, even', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('251', '997924', 'CHEM', '430', '2006', 'ADVANCED ORGANIC CHEMISTRY', 'A mechanistic study of prevailing organic reactions via structural and electronic effects. Three hours of lecture per week. Prerequisite: &quot;C&quot; or better in CHEM 232 and CHEM 322. Sp, odd', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('252', '686621', 'CLSC', '100', '2006', 'MEDICAL APPLICATION OF SCIENCE FOR HEALTH', 'This course is designed to facilitate academic enrichment in health care science and improve basic awareness of health care careers. This course is open to regular University students and to students who make application to and are accepted into the M*A*S*H program sponsored by the North Louisiana Area Health Education Center. F, Sp, Sum', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('253', '250359', 'CLSC', '101', '2006', 'Principles of Clinical Laboratory Science', 'Orientation to the profession of clinical laboratory science, the hospital internship, and the many phases of laboratory science encountered in the clinical laboratory. F, Sp', '2', '2', '2', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('254', '465573', 'CLSC', '103', '2006', 'PRINCIPLES OF RENAL FUNCTION AND URINALYSIS', 'The morphology, anatomy and physiology of renal system, and principles of urinalysis. One hour lecture. Prerequisite: Approval of the Program Director. Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('255', '964940', 'CLSC', '104', '2006', 'URINALYSIS LABORATORY', 'A laboratory course to accompany 103, involving qualitative and quantitative studies, both chemical and microscopic, of urine. Three hours laboratory. Prerequisite: Approval of the Program Director. Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('256', '995335', 'CLSC', '112', '2006', 'PHLEBOTOMY', 'Study of phlebotomy, professionalism, patient relations, basic medical laboratory terminology, types of specimens, specimen collection procedures, complications of venipuncture, adaptations for special limits and special types of patients, special collection techniques, and actual collection of laboratory specimens. (Formerly CLSC 110, 111)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('257', '601003', 'CLSC', '320', '2006', 'HEMATOLOGY', 'A study of hematopoiesis, classification, mechanisms and clinical characteristics of the anemias, leukemias, lymphomas, and other related diseases; the mechanism and specific disorders of hemostasis and a study of the principles of various hematological testing procedures and interpretations and correlation of test results with disease states. Three hours lecture. Prerequisite: Approval of the Program Director. F, Sp, Sum I', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('258', '266869', 'CLSC', '321', '2006', 'HEMATOLOGY LABORATORY', 'A laboratory course to accompany 320 to include procedures for a complete blood count, sedimentation rate, L.E. prep, osmotic fragility, sickle cell testing, coagulation testing, various miscellaneous tests and quality control procedures. Three hours laboratory. Prerequisite: Approval of the Program Director. F, Sp, Sum I', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('259', '954046', 'CLSC', '322', '2006', 'IMMUNOLOGY', 'A course covering the principles of basic immunology and the application of clinical immunology in the medical laboratory to include: immunoglobulin structures; antigen- antibody reactions, classification, mechanisms, and clinical characteristics of immune disease; transplantation and tumor immunology; and a study of various serological testing procedures and interpretation and correlation of test results with disease states. Three hours lecture. Prerequisite: Approval of Program Director. Sp, Sum I', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('260', '970147', 'CLSC', '323', '2006', 'SEROLOGY LABORATORY', 'A laboratory course to accompany 322 to include representative tests of all types of serological testing performed in the clinical laboratory. Three hours laboratory. Prerequisite: Approval of the Program Director. Sp, Sum I', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('261', '363878', 'CLSC', '324', '2006', 'CLINICAL CHEMISTRY AND TOXICOLOGY', 'Study of physiologic limits and composition of various body fluids and their methods of analysis. Three hours lecture. Prerequisite: Approval of the Program Director. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('262', '849370', 'CLSC', '325', '2006', 'CLINICAL CHEMISTRY AND TOXICOLOGY LABORATORY', 'A laboratory course to accompany 324 including qualitative and quantitative analysis of physiologic components and composition of the various components of blood and other body fluids and methods of analysis for toxic substances. Three hours laboratory. Prerequisite: Approval of the Program Director. F, Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14171', '230260', 'CLSC', '326', '2006', 'Clinical Chemistry And Toxicology', 'Principles of basic clinical chemistry and toxicology procedures applied to enzymes; endocrine function; electrolytes and blood gases; renal function; gastric, pancreatic and intestine function; biochemistry of pregnancy; therapeutic drug monitoring and analysis of toxic substances. Prerequisite: Approval of the Program Director. (Same as Toxicology 326.) Sp, F', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('264', '305270', 'CLSC', '327', '2006', 'IMMUNOHEMATOLOGY AND BLOOD BANKING', 'A course in the genetic theory of the human blood groups and the administrative and technical operation of blood banks. Three hours lecture. Prerequisite: Approval of the Program Director. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('265', '585356', 'CLSC', '328', '2006', 'TECHNICAL PROCEDURES IN BLOOD BANKING', 'A laboratory course to accompany 327, giving practical experience in blood donor selection, blood processing, patient crossmatching, plasmapheresis, and other blood component fractionation procedures. Three hours laboratory. Prerequisite: Approval of the Program Director. F, Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('266', '34791', 'CLSC', '430', '2006', 'MEDICAL MICROBIOLOGY', 'The morphology, physiology, and immunogenicity of disease-causing bacteria, methods of isoloation, identification, susceptibility testing, and control of pathogenic bacteria and diseases. Three hours lecture and three hours laboratory. Prerequisities: BIOL 214, 215. (Formerly CLSC 338I)', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14423', '268561', 'CODI', '151', '2006', 'General Foundation In Audiology', 'The nature of deafness and hard of hearing problems of children.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14417', '256146', 'CODI', '152', '2006', 'Speech And Language Acquisition', 'The development of speech in children in the family and the effects of culture on language.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14411', '325076', 'CODI', '220', '2006', 'Beginning Sign Language', 'An introductory course in the study of American Sign Language (ASL) and Pidgin Sign English (PSE) with an emphasis on the Deaf Community and their culture. An understanding of some of the basic cultural differences of the Deaf and how they relate to the development and existence of sign languages will be coupled with basic skills in both ASL and PSE. Non-majors only.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14405', '893304', 'CODI', '222', '2006', 'American Sign Language II', 'This course is designed to expand receptive and expressive American Sign Language (ASL) skills. It is a natural language approach incorporating cultural interactions which are being used to develop appropriate conversational skills in ASL at the survival-plus level. Vocabulary acquisition and the understanding and use of ASL linguistic structure is emphasized. Instruction is in ASL without voice. Prerequisite: CODI 220.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14479', '980357', 'CODI', '240', '2006', 'Anatomy And Physiology Of Speech Processes', 'The anatomy and physiology of the neurological, respiratory, phonatory, articulatory, and hearing mechanisms.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14485', '28223', 'CODI', '242', '2006', 'Speech, Language, And Hearing Acoustics', 'The production, transmission, and perception of acoustic signals in Speech Communication; acoustic phonetics and psychoacoustics.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14497', '369636', 'CODI', '350', '2006', 'Voice Disorders', 'An identification of problems in phonation and resonation of the voice and rehabilitation procedures.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14564', '150245', 'CODI', '470', '2006', 'Clinical Assisting', 'Students assist with clients being served at the ULM Speech and Hearing Clinic or other approved sites. Prerequisite: See specific requirements for enrollment under Progression of Majors in Communicative Disorders.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14503', '966823', 'CODI', '417', '2006', 'Phonetics', 'Correct sound formation and pronunciation; learning of transcription rules for American phonetics and other sound classification systems. Enrollment in 418 is required.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14509', '813788', 'CODI', '418', '2006', 'Phonetics Laboratory', 'A laboratory course to accompany 417. Two hours laboratory. Prerequisite: Credit or registration in 417, or approval of instructor.', '1', '1', '1', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14515', '75925', 'CODI', '426', '2006', 'Diagnosis And Appraisal Of Communication Disorders', 'Instruments, procedures, and report writing skills utilized in diagnosing and reporting speech, language, and hearing disorders. Enrollment in 427 is required. Prerequisite: 113 and consent of instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14527', '423207', 'CODI', '428', '2006', 'Articulation Disorders', 'Types, causes, symptoms, and therapy for articulatory defects in speech. Prerequisite: 417, 418, or consent of instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14533', '502220', 'CODI', '429', '2006', 'Voice And Fluency Disorders', 'Types, causes, and symptoms and therapy for voice and fluency disorders.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14539', '449577', 'CODI', '431', '2006', 'Stuttering', 'Review of modern theories and therapies of stuttering, nature, causes, diagnosis, and treatment of stuttering.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14545', '873700', 'CODI', '433', '2006', 'Language Pathology', 'Language acquisition, language disorders of reception and expression, and habilitation methodologies in learning. Prerequisite: 152 or consent of instructor.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14552', '979033', 'CODI', '436', '2006', 'Manual Communication', 'An introductory course in manual communication of the hearing impaired. Emphasis on drills and exercises to help students acquire a sign vocabulary fluency.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14558', '690670', 'CODI', '445', '2006', 'Clinical Counseling', 'Issues related to counseling culturally diverse persons with communication disorders and their families.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14576', '112114', 'CODI', '480', '2006', 'Audiology', 'Principles and Techniques of auditory testing and the interpretation of hearing tests. Prerequisite: 151.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14582', '583510', 'CODI', '485', '2006', 'Aural Rehabilitation', 'Professional services and procedures facilitating adequate receptive and expressive communication in persons with hearing impairments.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('287', '706294', 'CINS', '101', '2006', 'MICROCOMPUTER OPERATIONS', 'An introduction to information systems. Topics include hardware, operating systems, and application programs with hands-on experience. Not open to College of Business juniors and seniors. F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('288', '816149', 'CINS', '212', '2006', 'WORD PROCESSING', 'Introduction to word processing concepts and fundamentals of word processing operations; hands- on experience with microcomputer word processors. Laboratory work required. Prerequisites: 35 wpm keyboard competency, 205. F, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('289', '14782', 'CINS', '215', '2006', 'SPREADSHEET APPLICATIONS', 'Intensive coverage of business spreadsheet models; including spreadsheet design, file- building techniques, graphics, and spreadsheet automation with macros. Prerequisite: CSCI 170 or permission of instructor. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('290', '660340', 'CINS', '220', '2006', 'INTRODUCTION TO BUSINESS PROGRAMMING', 'Fundamentals of problem solving, programming logic, and algorithm specification using various modeling tools; coding of algorithms in a programming language. Prerequisites: CSCI 170, MATH 116 and completion of all developmental requirements, or permission of instructor. (Formerly CINS 201) F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('291', '786297', 'CINS', '302', '2006', 'INTERMEDIATE BUSINESS PROGRAMMING', 'Computer programming methods for business systems. Emphasizes contemporary programming environments and application development using a modern programming language. Prerequisite: 220.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('292', '378660', 'CINS', '306', '2006', 'DATA BASE APPLICATION DEVELOPMENT', 'Concepts related to modeling organizational data; emphasis on designing and implementing the Relational database model using both the Structured Query Language (SQL) and a graphical query interface. Prerequisite: CINS 220. (Formerly CINS 401) F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('293', '509710', 'CINS', '340', '2006', 'NETWORKS AND DATA COMMUNICATIONS', 'Concepts of data communications and networks and their impact on the business enterprise; issues pertaining to design, development, and implementation; hands-on experience with a network. Prerequisite: 205. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('294', '16557', 'CINS', '341', '2006', 'ADVANCED NETWORKING', 'Planning, building, and administering local area networks. Emphasis placed on installation, management, and maintenance of client/server networks. Coverage of TCP/IP, remote access, and other popular services. Prerequisite: CINS 340. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('295', '683929', 'CINS', '344', '2006', 'INFORMATION SECURITY MANAGEMENT', 'Principles of information security and assurance, with emphasis on the managerial component. A lifecycle approach is used to identify, assess, and develop responses to security threats to organizational assets. Prerequisite: 340 or permission of instructor.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('296', '95067', 'CINS', '350', '2006', 'MANAGEMENT INFORMATION SYSTEMS', 'Principles of the design, development, and use of information systems for the management of organizations; planning, controlling, staffing, hardware, software, and operational considerations of personnel, workgroup, and organizational information systems. Prerequisite: CSCI 170. F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('297', '60076', 'CINS', '455', '2006', 'END USER SUPPORT', 'Provision of technical support in selection of hardware and software; training in use of hardware and software; and assistance in use of computer information systems in solving business problems. Prerequisites: 306, 340, 350 and BUSN 412, or permission of instructor.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('298', '969752', 'CSCI', '170', '2006', 'COMPUTER LITERACY', 'A study of computer concepts with an emphasis on the personal computer and its practical use, the impact of computers on society, and the knowledge required to be computer literate in today’s digital world.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('299', '741692', 'CSCI', '180', '2006', 'FOUNDATIONS OF COMPUTER SCIENCE', 'An introduction to the foundations of computer science and problem solving using a modern, high-level programming language. Not applicable toward a degree in Computer Science.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('300', '264383', 'CSCI', '200', '2006', 'INTRODUCTION TO COMPUTER PROGRAMMING', 'An introduction to algorithms and programming, with an emphasis on the basic design, implementation, and testing of solutions to numerical and non-numerical problems. Prerequisite: CSCI 180 or credit or registration in a mathematics core curriculum course.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('301', '834794', 'CSCI', '253', '2006', 'COMPUTER ORGANIZATION AND ASSEMBLY LANGUAGE PROGRA', 'Introduction to computer organization, machine language, and assembly language programming. Prerequisite: Grade of &quot;C&quot; or better in CSCI 203.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('302', '335351', 'CSCI', '264', '2006', 'INTRODUCTION TO COMPUTER ORGANIZATION', 'Basic logic design, coding, number representation and arithmetic binary representation, and computer architecture. Prerequisite: CSCI 226, 253, and credit or registration in CSCI 265.', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('303', '747298', 'CSCI', '265', '2006', 'COMPUTER ORGANIZATION LABORATORY', 'Laboratory class to accompany 264. Prerequisite: Credit or registration in 264.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('304', '916758', 'CSCI', '273', '2006', 'FILE AND DATA STRUCTURES', 'Dynamic memory management. Secondary storage concerns. File processing environment, sequential and random access. File organization: sequential, random, indexed, multikey. Hashing and indexing techniques. Awareness of database basics. Prerequisite: CSCI 203 with a grade of &quot;C&quot; or better and CSCI 226.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('305', '703265', 'CSCI', '305', '2006', 'ANALYSIS OF ALGORITHMS', 'The design and analysis of computer algorithms and data structures. Classes of algorithms studied include sorting, searching, graph, parallel and NP- complete. An in-depth study into both efficiency and design. Correctness and formal verification of algorithms. Prerequisites: Grade of &quot;C&quot; or better in 273.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('306', '988445', 'CSCI', '310', '2006', 'ORGANIZATION OF PROGRAMMING LANGUAGES', 'An introduction to programming language specification and analysis. Additional topics include programming language paradigms, runtime environments, binding strategies, compilers, and interpreters. Prerequisite: CSCI 305.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('307', '338532', 'CSCI', '313', '2006', 'COMPUTER THEORY', 'The various types of abstract languages and their corresponding acceptors. The Chomsky hierarchy. Determinism and nondeterminism in language acceptors; problem decidability. The relationship of abstract languages and automata to computers. Prerequisites: CSCI 305 and 310.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('308', '475422', 'CSCI', '320', '2006', 'OBJECT-ORIENTED DESIGN AND PROGRAMMING', 'Elements of the object model including abstraction, encapsulation, modularity, class hierarchies and inheritance, virtual functions, generic classes, and operator overloading. Methods for identifying classes and objects, notation for object-oriented design, the design process, and case studies. Prerequisite: CSCI 305.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('309', '636875', 'CSCI', '326', '2006', 'ADVANCED DISCRETE STRUCTURES', 'A study of advanced topics from discrete mathematics, with special emphasis to applications in computer science. Topics include: efficiency of algorithms, recurrence relations, number theory, graph theory, combinatorics, and Boolean algebra. Prerequisite: CSCI 226.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('310', '90181', 'CSCI', '340', '2006', 'INTERNET PROGRAMMING', 'The art and science of programming for WWW Internet applications from a client-side perspective. Basic and advanced HTML. CGI scripting and an introduction to Java applets. Some experience with Internet and UNIX usage is desirable. Prerequisite: CSCI 273.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('311', '571773', 'CSCI', '350', '2006', 'RESEARCH METHODS FOR COMPUTER SCIENCE', 'An introduction to methods used in computer science research. Topics include techniques and conventions in research methods, evaluation approaches, and presentation of results. Prerequisite: CSCI 226.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('312', '97476', 'CSCI', '367', '2006', 'ADVANCED SOFTWARE APPLICATIONS', 'A study of advanced applications and their use in producing professional level presentations. Students will utilize problems from their major field of study, occupation, and/or other areas of interest. The word processor, spreadsheet, and other tools will be used to integrate the study into a desktop publication. Not applicable toward a degree in computer science. Prerequisites: CSCI 170.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14125', '459356', 'CSCI', '373', '2006', 'Numerical Methods', 'Numerical algorithms fundamental to scientific work including discussion of error. Prerequisite: CSCI 203 and MATH 202 and 132. (Dual listed as MATH 373.)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('314', '354745', 'CSCI', '377', '2006', 'INTERNET FOR THE USER', 'Introduction to Internet including methods of use such as telnet, FTP e-mail, WWW, gopher, and Netscape. Not applicable toward a degree in computer science. Prerequisites: CSCI 170 or approval of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('315', '808433', 'CSCI', '398', '2006', 'COMPUTER PROJECTS', 'Involvement in practical computer projects. Prerequisite: Permission of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14131', '361956', 'CSCI', '420', '2006', 'Introduction To Biostatistics I', 'An introduction to the discipline of statistics and its application to problems of biology, including human biology, medicine, and public health. Topics include Bayesian inferences, estimation techniques, hypothesis testing, goodness of fit, and analysis of variance. Prerequisite: Math 114 or Math 131 or permission of department head. (Same as Math 420).', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14137', '249434', 'CSCI', '421', '2006', 'Introduction To Biostatistics II', 'A continuation of 420. Topics include linear and multiple regression, logistics regression, longitudinal data analysis, nonparametric methods and principal component techniques. Prerequisites: Math 420 or CSCI 420 and junior standing or above. (Same as Math 421).', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('318', '514727', 'CSCI', '445', '2006', 'RESEARCH DATA BASE MANAGEMENT', 'An introductory study into data base management activities and practices using the Statistical Analysis System as related to the statistical and report writing needs of researchers. Topics such as data base display techniques, statistical significance tests, statistical modeling, graphics, and report writing will be discussed. Emphasis is on methods and computing techniques using the Statistical Analysis System. Not open to computer science majors. Prerequisites: CSCI 170; three (3) semester-hour statistics course, or concurrent enrollment in a statistics course, or approval of department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('319', '871048', 'CSCI', '460', '2006', 'PRINCIPLES OF SOFTWARE ENGINEERING', 'A formal approach to state-of-the-art techniques in software design and development. This course includes the classic model of the software life cycle, prototyping, resource allocation in large scale software projects, software cost estimating, and project management techniques. Prerequisites: CSCI 320.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('320', '117440', 'CSCI', '462', '2006', 'DATA COMMUNICATIONS', 'Technical aspects of data communications. ISO Open Systems Interconnect Model. TCP/IP protocol suite. Internetworking and routing. Prerequisite: CSCI 305.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('321', '175068', 'CSCI', '463', '2006', 'THEORY OF PROGRAMMING LANGUAGES', 'A formal treatment of programming language design and translation concepts. Programming language syntax and semantics. Finite and pushdown automata, scanners, symbol tables, parsers, machine-independent code generation. Prerequisite: CSCI 313.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('322', '657345', 'CSCI', '464', '2006', 'GRAPHICS', 'Fundamental concepts of graphics including enhancement, shading, two and three dimensional transformations, animations and hidden-surface problem. Prerequisites: CSCI 203, MATH 202 and Junior standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('323', '496100', 'CSCI', '412', '2006', 'ARCHITECTURE', 'Computer hardware architectures including microcomputers, minicomputers, mainframes and supercomputers; memory organization including cache and virtual memory; pipelining; RISC architecture; parallel processing architectures; comparison of representative architectures. Prerequisites: CSCI 253 and 305.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('324', '684778', 'CSCI', '440', '2006', 'INTERNET SYSTEMS MANAGEMENT', 'This course teaches the art and science of providing an Internet presence. It will cover several types of Internet services, concentrating on the World Wide Web. The course will cover support issues related to providing more advanced WWW services, including user support, security, operations, staffing, and purchasing. Prerequisite: CSCI 340.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('325', '673902', 'CSCI', '442', '2006', 'SIMULATION AND MODELING', 'General principles of stochastic simulation. Model development, analysis, and validation. Comparison of alternative models. Use of special purpose simulation languages. Prerequisites: CSCI 305 and MATH 303.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('326', '180029', 'CSCI', '455', '2006', 'THEORY OF DATA BASE MANAGEMENT SYSTEMS', 'Logical and physical data and file organization; hierarchical, network, and relational data models; data normalization; query facilities; current literature in the database area. Prerequisite: Grade of C or better in CSCI 273.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('327', '779844', 'CSCI', '475', '2006', 'KNOWLEDGE DISCOVERY IN DATABASES', 'An introduction to the field of Knowledge Discovery in Databases, KDD. Topics to be included are: data selection, cleaning, coding, pattern recognition, the discovery of hidden knowledge, learning rules, and data mining algorithms for large databases. Prerequisites: CSCI 455 and MATH 303.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('328', '733977', 'CSCI', '481', '2006', 'EXPERT SYSTEMS', 'A course in Applied Artificial Intelligence for non-programmers who are interested in the use and development of expert systems within their own fields of study. Not applicable toward a degree in computer science. Prerequisite: Senior or graduate standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('329', '149782', 'CONS', '101', '2006', 'CONSTRUCTION MATERIALS', 'Materials of construction, their properties, manufacture, characteristics and use. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('330', '521536', 'CONS', '102', '2006', 'BASIC GRAPHICS', 'Introductory course in architectural drafting. Use and care of instruments and equipment, line weights, lettering, simple plans, sections and details. Three hours of studio. F', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('331', '998360', 'CONS', '110', '2006', 'INTRODUCTION TO CONSTRUCTION', 'Introduction to the history and development of Construction, with an analysis of its impact on the national and international economy. A non-technical survey, especially designed for non-construction majors. F', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('332', '984575', 'CONS', '112', '2006', 'ARCHITECTURAL DRAFTING', 'Introduction to plan reading with instructions given in basic architectural graphics; details of working drawings and shop drawings related to building construction. F, Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('333', '856887', 'CONS', '113', '2006', 'ARCHITECTURAL DRAFTING STUDIO', 'Studio course to accompany 112. Six hours studio. F, Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('334', '745658', 'CONS', '203', '2006', 'CONSTRUCTION TECHNIQUES', 'A study of architectural design fundamentals through analysis of architectural and engineering plans, specifications, and documents used in construction. Prerequisites: 101, 112, 113. F, Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('335', '901334', 'CONS', '204', '2006', 'CONSTRUCTION TECHNIQUES STUDIO', 'Studio course to accompany 203. Three hours studio. F, Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('336', '560152', 'CONS', '206', '2006', 'STATICS AND STRENGTH OF MATERIAL', 'Resolution of forces, equilibrium, application of statics for simple structures, centroids, moments of inertia; materials in tension, compression, bending; shear and moment diagrams; design of simple structures. Prerequisite: MATH 111, 112. F, Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('337', '734104', 'CONS', '207', '2006', 'STATICS AND STRENGTH OF MATERIAL LABORATORY', 'Laboratory course to accompany 206. Three hours laboratory. F, Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('338', '239867', 'CONS', '208', '2006', 'CONSTRUCTION DOCUMENTS', 'A study of drawings, specifications, and documents used in the construction process. Prerequisites: 101, 112, 113. Sp, F', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('339', '35574', 'CONS', '209', '2006', 'CONSTRUCTION DOCUMENTS STUDIO', 'Studio course to accompany 208. Three hours studio. Sp, F', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('340', '370980', 'CONS', '211', '2006', 'CONSTRUCTION SURVEYING', 'Theory and principles of surveying applied to construction. Prerequisite: MATH 111, 112. Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('341', '330048', 'CONS', '212', '2006', 'CONSTRUCTION SURVEYING LABORATORY', 'Laboratory course to accompany 211. Six hours laboratory. Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('342', '607347', 'CONS', '214', '2006', 'INTERIOR SYSTEMS', 'A study of interior environmental systems including construction techniques, material handling, storage and transporation. Materials and systems influence of interior design as they pertain to acoustics, sound transmission, fire hazards, lighting, and energy.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('343', '468455', 'CONS', '220', '2006', 'CONSTRUCTION INSPECTION', 'Introduction to principles and practices of inspecting construction projects-in-progress with emphasis on the nature of construction problems encountered on the job site.', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('344', '169801', 'CONS', '221', '2006', 'LOCAL AND STATE BUILDING CODES', 'Under direct supervision, the student trainee develops effective public contacts with builders and contractors in conducting routine work within a building department office.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('345', '962091', 'CONS', '222', '2006', 'PLAN AND SPECIFICATION REVIEWS', 'Under direct supervision, the student trainee examines plans and specifications of buildings for conformity with zoning regulations and building laws.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('346', '276888', 'CONS', '223', '2006', 'RESIDENTIAL INSPECTIONS', 'Inspection of fences, patios, carports, residential blockwalls, etc.; conducting occupancy surveys in various structures such as apartments and hotels.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('347', '224701', 'CONS', '224', '2006', 'COMMERCIAL AND INDUSTRIAL INSPECTIONS', 'Inspection of buildings under construction, alteration or repair for compliance with building laws.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('348', '64750', 'CONS', '225', '2006', 'DAMAGED BUILDING INSPECTIONS', 'Inspection of damaged buildings and reporting needs for repair or demolition.', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('349', '791615', 'CONS', '250', '2006', 'STRUCTURAL SYSTEMS', 'A survey of structural systems including the design of temporary structures. Prerequisites: 206, 207.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('350', '95749', 'CONS', '260', '2006', 'MECHANICAL AND ELECTRICAL SYSTEMS', 'A survey of systems for water supply and sanitation, heating and air conditioning, and electric service and installations.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('351', '182190', 'CONS', '304', '2006', 'CONSTRUCTION TECHNIQUES LABORATORY', 'Laboratory course to accompany 303. Three hours laboratory. F', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('352', '966682', 'CONS', '305', '2006', 'STEEL STRUCTURES', 'Theory and principles of design and fabrication of structural steel in conformance with current codes and practices. Prerequisites: 206, 207. F', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('353', '753094', 'CONS', '306', '2006', 'STEEL STRUCTURES LABORATORY', 'Laboratory course to accompany 305.Three hours laboratory. F', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('354', '678289', 'CONS', '308', '2006', 'CONCRETE STRUCTURES AND FORM DESIGN', 'Theory and principles of design and building of concrete structures and forms in conformance with current codes and practices. Prerequisites: 206, 207. F', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('355', '651461', 'CONS', '309', '2006', 'CONCRETE STRUCTURES AND FORM DESIGN LABORATORY', 'Laboratory course to accompany 308. Three hours laboratory. F', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('356', '426996', 'CONS', '318', '2006', 'ESTIMATES', 'Principles and theories of estimating, classification of work and quantity survey techniques applied to various types of construction projects. Prerequisites: 203, 204, 208, 209. F, Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('357', '858318', 'CONS', '319', '2006', 'ESTIMATES LABORATORY', 'Laboratory course to accompany 318. Three hours laboratory. F, Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('358', '431305', 'CONS', '322', '2006', 'PIPELINE CONSTRUCTION', 'An introduction to pipeline construction including basic design considerations, standards and regulations, estimating and bidding, acquisition of Right-of-Way, construction equipment and procedures, safety, and pipeline maintenance. Prerequisite: Approval of director.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('359', '160973', 'CONS', '324', '2006', 'ENVIRONMENTAL CONSTRUCTION', 'An introduction to pollutants, hazardous and toxic substances encountered on the job site. Emphasis on biological hazards, health safety standards in regard to handling and disposal of products and liability management. Prerequisite: Junior standing. F', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('360', '8100', 'CONS', '328', '2006', 'CAPITAL PROJECTS', 'Theory of control and management by owner corporations of cost, time, and value of construction for capital ventures. Techniques for control of projects, with emphasis on general principles and relationships. Prerequisites: Approval of director. (Formerly 311.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('361', '952841', 'CONS', '350', '2006', 'MECHANICAL SYSTEMS OF BUILDINGS', 'Principles and practices of sanitation, water supply, heating, cooling, ventilation, smoke management, and fire suppression systems. Emphasis on general principles, relationships and language needed by the General Contractor. Prerequisite: 203, 204, 208, 209. F', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('362', '159333', 'CONS', '351', '2006', 'MECHANICAL SYSTEMS OF BUILDINGS LABORATORY', 'Laboratory course to accompany 350. Two hours laboratory. F', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('363', '992675', 'CONS', '407', '2006', 'PROJECT CONTROL SYSTEMS', 'Analysis and application of scheduling techniques. The use of critical path method (CPM) and program evaluation and review (PERT). Prerequisites: 303, 304. F, Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('364', '809729', 'CONS', '408', '2006', 'ESTIMATES', 'Analysis and determination of cost of construction operations including applicable indirect and overhead cost, and the preparation of bid proposals for construction costs. Prerequisites: 318, 319. Sp, F', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('365', '904991', 'CONS', '409', '2006', 'ESTIMATES LABORATORY', 'Laboratory course to accompany 408. Three hours laboratory. Sp, F', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('366', '894009', 'CONS', '410', '2006', 'ANALYTIC AND CONCEPTUAL ESTIMATING', 'Separation of conceptual planning into its constituent elements and essential features. Feasibility of a construction project by analysis of income, construction investment, operating cost and risk. Prerequisite: Approval of director.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('367', '369882', 'CONS', '412', '2006', 'ADVANCED PROJECT CONTROL SEMINAR', 'Continuation of 407/415 with emphasis on job control of resources, cost control and reporting. Selected current construction projects used with research in the field and group analysis and discussion in regular class meetings. Five hours a week are required for field and classroom. Prerequisites: 407, 415.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('368', '639095', 'CONS', '414', '2006', 'CONSTRUCTION MANAGEMENT', 'Management principles as applied to the construction contractor. Prerequisite: Junior standing. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('369', '133877', 'CONS', '415', '2006', 'PROJECT CONTROL SYSTEMS LABORATORY', 'A studio to accompany 407. Three hours studio per week. Prerequisite: Credit or registration in 407. F, Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('370', '405455', 'CONS', '417', '2006', 'FOUNDATION PROBLEMS', 'Foundation and soil problems as related to construction. Prerequisites: 303, 304. Sp, F', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('371', '10075', 'CONS', '418', '2006', 'FOUNDATION PROBLEMS LABORATORY', 'Laboratory course to accompany 417. Three hours laboratory. Sp, F', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('372', '463924', 'CONS', '419', '2006', 'CONSTRUCTION DOCUMENTS AND REGULATORY AGENCIES', 'Current building codes and significant aspects of construction specifications. Prerequisite: Senior standing or approval of director.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('373', '12803', 'CONS', '428', '2006', 'VALUE MANAGEMENT', 'Utilization of the constructor’s special knowledge to cut the cost of a construction project to the lowest practicable level without compromising its function or sacrificing quality and aesthetics. Prerequisite: Approval of director and junior standing. (Formerly 411.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('374', '478529', 'CONS', '430', '2006', 'CONSTRUCTION SAFETY', 'A course designed to provide constructors a system of recognition, avoidance, and prevention of unsafe working conditions and employments covered by The Occupational Safety and Health Act of 1970. Prerequisite: Junior standing.(Formerly 420.) Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('375', '633724', 'CONS', '455', '2006', 'ELECTRIC SERVICE AND INSTALLATIONS', 'Basic theory and practical application of electricity as applied to the construction field; basic design of illumination and electrical wiring based on the National Electric Code: introduction to cost estimates of installations. Prerequisites: 203, 204, 208, 209 and Junior standing. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('376', '974893', 'COUN', '433', '2006', 'INTERPERSONAL COMMUNICATION', 'Paired, triad, and small group involvement to develop awareness of various communicative skills utilized in the interpersonal relationship and increase effectiveness in interpersonal communication.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('377', '755417', 'COUN', '434', '2006', 'CAREER DEVELOPMENT IN THE ELEMENTARY SCHOOLS', 'Methods and systems of introducing the world of work to elementary school children.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('378', '858425', 'COUN', '464', '2006', 'COUNSELING PROCEDURES', 'The study of theory, processes, and relationships involved in individual and family counseling approaches and practices. Designed as a prepracticum experience, the course includes laboratory demonstrations and practice to develop initial counseling skills.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('379', '39644', 'COUN', '480', '2006', 'EFFECTIVE PARENTING', 'Designed to help students and parents-to-be understand the principles and techniques of establishing effective parent-child relationships.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('380', '547033', 'CJUS', '101', '2006', 'INTRODUCTION TO CRIMINAL JUSTICE', 'An examination of the history, organization, and function of the various local and federal agencies that make up the criminal justice system. The survey is organized around the three major components of the criminal justice system: police, courts, and corrections.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('381', '954173', 'CJUS', '206', '2006', 'INDUSTRIAL AND BUSINESS SECURITY', 'An examination of the basic problems, procedures, and needs in the field of security work. A comparison of private agencies, hotels, retail and industrial enterprises which handle their own security with private and public security organizations. Emphasis is given to the role of private, industrial, and business security systems and their relationship to the criminal justice system.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('382', '221833', 'CJUS', '230', '2006', 'THE POLICE', 'An in-depth examination of the law enforcement sub-system of the criminal justice system. Includes historical precedents to American systems, the diversity of agencies and their roles, the internal components of agencies, and their interrelationships with other system components and other social and legal agents and agencies. Prerequisite: 101 or approval of the program director.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('383', '513716', 'CJUS', '250', '2006', 'COURTS AND CRIMINAL JUSTICE', 'Examination of the court component of the criminal justice system. Emphasis on structure, roles, and functions of courts and their relationship to other system components and social institutions. Prerequisite: 101 or approval of program director.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('384', '707267', 'CJUS', '270', '2006', 'CORRECTIONS', 'An in-depth examination of the Corrections component of the criminal justice system. Fundamentals of correctional practices and philosophy, historically and systemically studied, including law, sentencing, and appellate review as each relates to the correctional process. Survey of correctional components: community-based programs, institutions, administration, offender categories, classification, and treatment. Prerequisite: For Criminal Justice majors only--101 or approval of program director.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('385', '900121', 'CJUS', '293', '2006', 'CRIMINAL LAW', 'Examination of substantive criminal law with emphasis on history, theory, classification and elements of crimes, elements of proof, and other issues related to criminal law. Prerequisite: 101 or approval of program director.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('386', '165177', 'CJUS', '299', '2006', 'OVERVIEW OF THE CRIMINAL JUSTICE SYSTEM', 'A capstone course for the integration of knowledge concerning the criminal justice system and criminal law with some attention to current legal issues in law enforcement and corrections. Restricted to students receiving the Associate of Arts Degree in Criminal Justice. Registration is required during the semester in which the degree is to be received.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('387', '134264', 'CJUS', '303', '2006', 'COMMUNITY-BASED CORRECTIONS', 'An in-depth study of correctional programs, practices, and theory within the American community. Survey of community corrections in terms of historical contributions; legal, social, and ethical considerations; professionalism; roles of staff, administration, and community resources; and relationships among and within community systems. Prerequisite: 101, 270, or approval of program director.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('388', '758439', 'CJUS', '308', '2006', 'SUBSTANCE ABUSE', 'Analysis of the social, physical, and psychological impact of substance abuse. Examination of types, incidence, and forms of abuse and an analysis of social and legal reactions. Social trends and laws are examined to provide the student with foundations for a critical and analytical examination of substance abuse. Prerequisite: 101 or approval of program director.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('389', '596482', 'CJUS', '310', '2006', 'CORRECTIONAL INSTITUTIONS', 'Analysis of the structure and function of prison staffing, prison operations, the convict world, prison violence, and jails. Prerequisite: 101, 270, or approval of program director.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('390', '295237', 'CJUS', '330', '2006', 'CRIMINAL INVESTIGATION', 'An in-depth study of the principles, concepts, and theories applicable to the investigation procedures used by law enforcement agents and agencies. Analysis of case law affecting criminal investigations. The course is designed to familiarize the student with the mechanics of investigative evidence processing techniques. (Formerly 332 and 333.) Prerequisite: 101, 230, or approval of program director.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('391', '766539', 'CJUS', '419', '2006', 'INTERNSHIP IN CRIMINAL JUSTICE', 'One semester of supervised service in a criminal justice agency. Open only to Criminal Justice majors who have completed at least 60 hours of course work and have no prior experience. Students must make application for the Internship the semester prior to that in which it will be taken.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('392', '549324', 'CJUS', '400', '2006', 'CORRECTIONAL TREATMENT', 'An overview and an examination of the basics of classification and counseling approaches, emphasizing interpersonal skills development, data collection, interviewing, assessment techniques, and counseling. Prerequisite: 101, 270, and junior standing or approval of program director.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14095', '313306', 'CJUS', '405', '2006', 'Theories Of Crime And Delinquency', 'An analysis of the extent of crime and delinquency; impact of crime and delinquency on society; physical, social, and psychological causation of crime and delinquency; and current issues in criminology. (Same as SOCL 405.)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('394', '759215', 'CJUS', '409', '2006', 'CRIMINAL JUSTICE FIELD STUDY', 'A field study tour of specific criminal justice programs currently in operation. The student is allowed to relate theory to practice through short intensive observation of quality corrections and law enforcement programs. Prerequisite: Junior, or senior, or graduate status.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14101', '926101', 'CJUS', '415', '2006', 'Juvenile Delinquency And Justice', 'The nature, extent, and causes of juvenile delinquency; factors in its treatment and prevention, including community and agency programs; and analysis of the legal system as it applies to juveniles. (Same as SOCL 415).', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('396', '359561', 'CJUS', '416', '2006', 'CONTEMPORARY ISSUES IN CRIMINAL JUSTICE', 'Examination of the literature and analysis of current issues relevant to criminal justice. Prerequisite: Junior, Senior, or Graduate standing.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14186', '803789', 'CJUS', '418', '2006', 'Constitutional Law And Criminal Justice', 'Study of constitutional law and its applicability in the criminal justice system. Prerequisite: Government 201.', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('398', '728934', 'CJUS', '429', '2006', 'MANAGEMENT OF CRIMINAL JUSTICE AGENCIES', 'The application of supervisory and administrative principles to the management of correctional and police agencies in the United States. Students with prior credit for CJUS 413 and/or 414 are not eligible to receive credit in CJUS 429. Prerequisite: Junior or senior standing or permission of the department head.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('399', '998656', 'CURR', '304', '2006', 'MIDDLE AND SECONDARY METHODS II', 'Integration of advanced teaching strategies and assessment strategies in specific content areas. Must be admitted to Teacher Education.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('400', '787101', 'CURR', '306', '2006', 'MATERIALS AND METHODS IN FOREIGN LANGUAGE', 'Practices in teaching foreign language in high school. Must be admitted to Teacher Education.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('401', '134390', 'CURR', '307', '2006', 'MATERIALS AND METHODS IN ENGLISH', 'Methods of organizing and presenting English materials in high school. Must be admitted to Teacher Education.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('402', '8732', 'CURR', '308', '2006', 'MATERIALS AND METHODS IN MATHEMATICS', 'Techniques of organizing and teaching arithmetic, algebra and plane geometry in high school; solution of selected problems to illustrate fundamentals; interpretation and solution of reading problems. Must be admitted to Teacher Education.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('403', '251025', 'CURR', '309', '2006', 'MATERIALS AND METHODS IN SCIENCE', 'Methods of organizing and presenting materials in natural and physical sciences in high school. Must be admitted to Teacher Education.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('404', '147192', 'CURR', '311', '2006', 'MATERIALS AND METHODS IN SOCIAL STUDIES', 'Materials, techniques, and methods of teaching social studies in high school. Must be admitted to Teacher Education.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('405', '487261', 'CURR', '312', '2006', 'MATERIALS AND METHODS IN ART', 'Theory and application of classroom art. Must be admitted to Teacher Education.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('406', '884749', 'CURR', '313', '2006', 'MATERIALS AND METHODS IN SPEECH', 'Materials, techniques and methods of teaching speech. Must be admitted to Teacher Education.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('407', '122086', 'CURR', '386', '2006', 'PROFESSIONAL GROWTH AND DEVELOPMENT', 'Introduction to the process of documenting professional growth and development including preparation, selection, application, evaluation, and integration of portfolios. Must be admitted to Teacher Education. Take during professional semester 1. F, Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('408', '242678', 'CURR', '414', '2006', 'CURRICULUM DESIGN AND EVALUATION', 'Theory and practices of curriculum design and evaluation; social and psychological factors related to the adoption and acceptance of new programs in public schools.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('409', '743174', 'CURR', '429', '2006', 'DIAGNOSIS AND REMEDIATION OF READING IN SECONDARY', 'A study of diagnostic and remedial reading procedures as they apply to secondary schools. Prerequisite: Approval of instructor.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('410', '161729', 'CURR', '450', '2006', 'MULTICULTURAL EDUCATION', 'Introduction to subcultures including political, economic, and social backgrounds and the impact on educational needs and methodology. Must be admitted to Teacher Education. Take during professional semester 1.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('411', '789710', 'CURR', '470', '2006', 'DRUG ABUSE EDUCATION', 'Content and techniques for elementary and secondary school curriculum development and implementation in the area of drug abuse.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('412', '697348', 'CURR', '475', '2006', 'CRISIS PREVENTION IN THE SCHOOLS', 'Designed to provide students practical training for non-violent intervention in the school setting. It will focus on ways to defuse and handle with confidence children, adolescents and adults who are disruptive and violent, and how to cope with violent confrontations and students armed with weapons. Prerequisite: Junior standing or approval of instructor.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('413', '383562', 'CURR', '485', '2006', 'INTEGRATING TECHNOLOGY INTO THE K-12 CURRICULUM', 'Provides a survey of and practice with the applications of instructional technology to enhance learning. It provides a review of current and emerging technologies in instruction through an introduction to current research and variety of instructional software and hardware. Prerequisite: CURR 285 or permission of instructor. F, Su', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('414', '303426', 'DANC', '112', '2006', 'Tap Dance', 'The study of tap dance steps and techniques as related to theatre dance.', '1', '1', '2', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('415', '404616', 'DANC', '114', '2006', 'Jazz Dance I', 'The study of jazz techniques with emphasis on style and rhythmic design.', '1', '1', '2', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('416', '844040', 'DANC', '125', '2006', 'Modern Dance I', 'The study of modern dance techniques and principles of movement.', '1', '1', '2', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('417', '468603', 'DANC', '126', '2006', 'Ballet', 'Ballet technique including ballet terminology, technical principles, and theory.', '1', '1', '2', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('419', '42124', 'DANC', '129', '2006', 'Folk Dance', 'The instruction and practice of American and International folk dances.', '1', '1', '2', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('420', '65085', 'DANC', '209', '2006', 'Modern Dance II', 'Advanced technique, movement combinations, choreography, and theory of modern dance.', '1', '1', '2', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('421', '834115', 'DANC', '214', '2006', 'Jazz Dance II', 'Advanced technique, movement combinations, choreography, and theory of jazz dance.', '1', '1', '2', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('422', '808662', 'DANC', '405', '2006', 'ADMINISTRATION OF DANCE', 'The organization and administration of dance productions, dance festivals, and other dance performances.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('423', '614409', 'DHYG', '101', '2006', 'INTRODUCTION TO DENTAL HYGIENE', 'An introduction to Dental Hygiene including an overview of the normal oral cavity, common pathological conditions, preventive measures, dental hygiene education, and community dentistry. F,Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('424', '288685', 'DHYG', '318', '2006', 'ORAL HISTOLOGY AND EMBRYOLOGY', 'Microscopic studies with emphasis on development of the tissues and structures of the head, neck and oral cavity. Prerequisites: credit or registration in 319; Dental Hygiene 320, 321, 322, 323 or permission of department head.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('425', '567878', 'DHYG', '319', '2006', 'ORAL HISTOLOGY AND EMBRYOLOGY LAB', 'A laboratory course to accompany 318. Two hours laboratory. Prerequisite: credit or registration in 318 or permission of department head.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('426', '973461', 'DHYG', '320', '2006', 'CLINICAL DENTAL HYGIENE ORIENTATION', 'An orientation course designed to provide dental hygiene students with basic didactic background required for entry into the first clinical level of basic patient care. Prerequisite: registration in 321. F', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('427', '648245', 'DHYG', '321', '2006', 'CLINICAL DENTAL HYGIENE TECHNIQUE', 'An introduction to basic clinical techniques required for entry into the first clinical level of basic patient care. Designed to accompany 320. Prerequisite: registration in 320. F', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('428', '221103', 'DHYG', '322', '2006', 'DENTAL ANATOMY', 'The study of the anatomical, morphological and physiological characteristics of the human dentition. A laboratory component includes the identification and differentiation between various anatomical landmarks of the teeth. F', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('429', '702396', 'DHYG', '323', '2006', 'HEAD AND NECK ANATOMY', 'Designed to give the students a basic knowledge and understanding of head and neck anatomy. This includes terminology, osteology, and basic physiology, with special emphasis on the oral cavity. F', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('430', '646902', 'DHYG', '324', '2006', 'GENERAL AND ORAL PATHOLOGY', 'This course is designed to introduce the student to current concepts relative to the pathophysiologic mechanisms of human disease. The student is expected to acquire a fundamental knowledge of diagnostic principles regarding systemic disease in general and oral diseases in particular, as well as conceptual knowledge of treatment and prognostic principles. Prerequisites: 320, 321, 322, 323; registration in 325, 326. Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('431', '8204', 'DHYG', '325', '2006', 'CLINICAL DENTAL HYGIENE', 'A continuation of 321. Concentration on the assessment, treatment and evaluation of patients demonstrating dental health to minimal disease. Implementation of preventive procedures to maintain dental health. Prerequisites: 318-319, 320, 321, 322, 323; registration in 326. Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('432', '836786', 'DHYG', '326', '2006', 'CLINICAL DENTAL HYGIENE LECTURE', 'A didactic course to accompany 325 presenting information and techniques pertinent to the treatment of clinic patients. Concentration on dental health education, prevention of disease, evaluation and classification of oral conditions and introduction to dental emergencies. Prerequisite: registration in 325. Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('433', '655633', 'DHYG', '327', '2006', 'ROENTGENOLOGY AND ORAL RADIOLOGY', 'The study of the principles and theories of the x-ray machine. The technique of taking, processing and mounting dental x-ray film. Examination of the teeth and surrounding tissue through evaluation of x-ray and diagnostic models. Prerequisites: 320, 321, 322, 323; registration in 325, 326. Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('434', '259229', 'DHYG', '414', '2006', 'PERIODONTOLOGY', 'The study of normal and abnormal periodontium, the etiology of periodontal disease including systemic factors, immune response and pathogenesis of plaque related factors. Diagnosis, prognosis, and treatment planning are applied to the various classifications of periodontal diseases. Prerequisites: Completion of 300 level Dental Hygiene courses; registration in 416, 417. Su', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('435', '983978', 'DHYG', '415', '2006', 'DENTAL MATERIALS AND DENTAL ASSISTING', 'This course will introduce the student to materials used in dentistry and their application. Students will apply this knowledge in the laboratory where they will demonstrate specific laboratory and clinical procedures. Dental Assisting and laboratory functions will be demonstrated. Prerequisites: Completion of all 300 level Dental Hygiene courses; registration in 416, 417. Su', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('436', '142894', 'DHYG', '416', '2006', 'CLINICAL DENTAL HYGIENE', 'A continuation of 325. Application of didactic and clinical techniques to patient experiences involving healthy patients or those with minimal disease. Prerequisite: registration in 417. Su', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('437', '143978', 'DHYG', '417', '2006', 'CLINICAL DENTAL HYGIENE LECTURE', 'A lecture to accompany Dental Hygiene 416 and a continuation of Dental Hygiene 326. Emphasis will be placed on the evaluation and conditions, dental restorations as they relate to these conditions and occlusion. Prerequisite: registration in 416. Su', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('438', '106064', 'DHYG', '418', '2006', 'DENTAL HEALTH EDUCATION', 'Principles of dental health in individual and group situations. Understanding the use of teaching methods, materials, and visual aids for individuals of al ages. Prerequisites: 414, 415, 416, 417; registration in 421, 422.', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('439', '385571', 'DHYG', '421', '2006', 'CLINICAL DENTAL HYGIENE', 'A continuation of Dental Hygiene 416. Clinical experiences with a variety of patients including pediatric, geratric, and medically and developmentally compromised. Emphasis on the treatment of the patient with early periodontal disease. Prerequisites: 414, 415, 416, 417; registration in 422. F', '5', '5', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('440', '887152', 'DHYG', '422', '2006', 'CLINICAL DENTAL HYGIENE LECTURE', 'Course to accompany 421 presenting information and techniques pertinent to the assessment and treatment of a variety of dental hygiene patients. Emphasis on techniques and equipment used in the treatment of periodontal disease. Consideration of caries and periodontal tests and nutritional information in the treatment of clinic patients. Prerequisite: registration in 421. F', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('441', '267793', 'DHYG', '423', '2006', 'SEMINAR', 'Application of basic dental and dental hygiene sciences in developing patient treatment plans. Emphasis will be placed on the evaluation and synthesis of various treatment modalities for optimal patient care. Prerequisites: 414, 415, 416, 417; registration in 421, 422. Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('442', '175282', 'DHYG', '426', '2006', 'SURVEY OF DENTAL SPECIALTIES', 'Students learn the techniques used in the various specialties of dentistry such as periodontics, orthodontics, endodontics, oral surgery, prosthodontics, and hospital dentistry as they relate to the treatment of pediatric and geriatric patients. Prerequisites: 419, 421, 422, 431; registration in 428, 429, 433. Sp', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('443', '584500', 'DHYG', '427', '2006', 'ETHICS, JURISPRUDENCE AND PRACTICE MANAGEMENT', 'Laws and ethics relating to the dental hygienist. Dental office policies and procedures. Prerequisites: 419, 421, 422, 431, 433; registration in 428, 429. Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('444', '651354', 'DHYG', '428', '2006', 'CLINICAL DENTAL HYGIENE', 'A continuation of 422. Clinical experiences with a variety of patients including pediatric, geriatric, medically compromised and developmentally challenged. Emphasis on the moderate to advanced periodontal patient. Prerequisites: 419, 421, 422, 431, 433; registration in 429. Sp', '5', '5', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('445', '478708', 'DHYG', '429', '2006', 'CLINICAL DENTAL HYGIENE LECTURE', 'A didactic course to accompany 428. Continuation of the dental assessment and management of the medically compromised patient. Dental hygiene topics relevant to evidence-based practice of dental hygiene and professionalism issues will be discussed and debated. Prerequisite: registration in 428. Sp', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('446', '989115', 'DHYG', '431', '2006', 'ADVANCED PERIODONTOLOGY', 'A course emphasizing the role of the dental hygienist in total management of the periodontal patient, utilizing advanced concepts in treatment modalities and supportive maintenance care. Prerequisites: 414, 415, 416, 417; registration in 421, 422.', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('447', '935038', 'DHYG', '433', '2006', 'LOCAL ANESTHESIA & PAIN CONTROL', 'This course is designed to instruct students in the art and science of local anesthesia. The course encompasses pre-anesthetic evaluation of the patient, management of the medically compromised patient, physiology and pharmacology of local anesthetics and the management of medical emergencies. Prerequisites: 414, 415, 416, 417; registration in 421, 422.', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('448', '947310', 'ECON', '103', '2006', 'CONTEMPORARY ECONOMIC ISSUES', 'Introduction to the analysis of current national issues, including international business issues and business ethics, using the economic approach. Not open to students with prior credit in both Economics 201 and 202. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('449', '527995', 'ECON', '201', '2006', 'MACROECONOMIC PRINCIPLES', 'Focuses on the overall economic issues of unemployment, inflation, the level and distribution of national income, growth and international trade. Competing macroeconomic models are analyzed along with the tools of government fiscal and monetary policy. F, Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('450', '977161', 'ECON', '202', '2006', 'MICROECONOMIC PRINCIPLES', 'Focuses on such concepts as demand, supply, elasticity, production and cost. Analyzes market models of perfect competition, monopoly and imperfect competition. Includes discussion of international trade issues and government interventions. F, Sp, Su', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('451', '12396', 'ECON', '301', '2006', 'MONEY AND BANKING', 'The nature and function of money, credit and banking in the economic system. Modern monetary theory and problems; the course of financial development; international monetary relations; fiscal and monetary policies. Prerequisites: 201, 202. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('452', '362110', 'ECON', '312', '2006', 'INTERMEDIATE MACROECONOMIC ANALYSIS', 'Determinants of national income and employment. The role of consumption spending, business investment, government spending, and taxing and monetary policies in determining the level of economic activity. Prerequisites: 201, 202.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('453', '785824', 'ECON', '313', '2006', 'INTERNATIONAL TRADE', 'The theory of international trade from Mercantilism and the Classical world to modern Neo-classical theories; the impacts of trade policies; economic integration. Prerequisites: 201, 202.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('454', '626804', 'ECON', '321', '2006', 'MANAGERIAL ECONOMICS', 'Determination of optimal prices, output and input utilization. Estimation of demand and cost. Decision making in the presence and absence of market power. Prerequisites: 202, QMDS 210. F', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('455', '650794', 'ECON', '390', '2006', 'RESOURCE AND ENVIRONMENTAL ECONOMICS', 'Application of microeconomic principles to problems involving exhaustible resources, renewable resources, and environmental services. Pollution, resource depletion, and the role of government in the market are discussed. Effects of policy tools such as taxation, ceiling prices, and emission standards are analyzed. Prerequisite: 201, 202. (Formerly Resource Economics.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('456', '353702', 'EDFN', '201', '2006', 'INTRODUCTION', 'Orientation to teaching; opportunities; certification; legal aspects; organization, administration and financing of public education; history and philosophy of education; multicultural education. Twenty (20) hours of classroom observation will be completed during this course. F, Sp, S', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('457', '600266', 'EDFN', '401', '2006', 'ASSESSMENT', 'Principles of tests and measurements for elementary and secondary teachers; types of tests; essentials of test items; objective and standardized tests. Must be admitted to Teacher Education. F, Sp, S', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('458', '299040', 'EDFN', '436', '2006', 'EDUCATIONAL DATA PROCESSING', 'Principles of electronic data processing in education.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('459', '574805', 'EDFN', '439', '2006', 'FUNDAMENTALS OF STATISTICS IN EDUCATION', 'Tools necessary for competence in education statistics. Algebraic, geometric and trigonometric functions; principles of scaling and score transformation; principles of reliability and validity estimation.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('460', '302663', 'ELED', '340', '2006', 'BALANCED LITERACY: PRIMARY GRADES', 'Integration of reading, writing, spelling, listening in a balanced literacy program for students in primary grades. Must be admitted to Teacher Education. Take during professional semester 1. F, Sp', '6', '6', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('461', '945942', 'ELED', '341', '2006', 'BALANCED LITERACY: INTERMEDIATE GRADES', 'Integration of reading, writing, speaking, and listening strategies in teaching intermediate grade students how to enhance comprehension and responses to text and visual formats. Must be admitted to Teacher Education. Take during professional semester  2. F, Sp', '6', '6', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('462', '583009', 'ELED', '342', '2006', 'ELEMENTARY SOCIAL STUDIES METHODS', 'Integrative elementary social studies methods designed to promote organizational skills and content utilizing sound research, theory, and practice. Must be admitted to Teacher Education. Take during professional semester 3 F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('463', '139449', 'ELED', '419', '2006', 'MATHEMATICS/SCIENCE METHODOLOGY', 'The purpose of this course is to present techniques of organizing and teaching science and mathematics at the elementary school level. Must be admitted to Teacher Education. Includes supervised experiences in the school. (Formerly 415 and 416.)', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('464', '601575', 'ELED', '420', '2006', 'LANGUAGE ARTS/SOCIAL STUDIES METHODOLOGY', 'The major purpose of this course is to present techniques of organizing and teaching Language Arts and Social Studies at the elementary school level. Must be admitted to Teacher Education. Includes supervised experiences in the school. (Formerly 417 and 418).', '4', '4', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('465', '842237', 'ELED', '421', '2006', 'GENERIC METHODOLOGY', 'The major purpose of this course is to present those techniques and methods that are common to all elementary courses. Must be admitted to Teacher Education. Includes supervised experiences in the school.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('466', '123427', 'ELED', '441', '2006', 'SCIENCE METHODOLOGY', 'Techniques for organizing and teaching science at the lower and upper elementary school levels. Must be admitted to Teacher Education. Take during professional semester 3. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('467', '744468', 'ELED', '442', '2006', 'LOWER ELEMENTARY MATHEMATICS METHODOLOGY', 'Techniques of organizing and teaching mathematics at the lower elementary level. Must be admitted to Teacher Education. Take during professional semester 3. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('468', '986215', 'ELED', '443', '2006', 'UPPER ELEMENTARY MATHEMATICS METHODOLOGY', 'Techniques of organizing and teaching mathematics at the upper elementary level. Must be admitted to Teacher Education. Take during professional semester 3. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('469', '229490', 'ELED', '461', '2006', 'STUDENT TEACHING IN THE LOWER ELEMENTARY SCHOOL', 'Directed observation and participation in classroom activities in the lower grades in an approved elementary school. Must be admitted to Teacher Education. Take during professional semester 4. F, Sp', '5', '5', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('470', '130040', 'ELED', '462', '2006', 'STUDENT TEACHING FOR EARLY CHILDHOOD EDUCATION', 'Directed observation and participation in kindergarten and nursery school classroom activities. F, Sp', '6', '6', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('471', '873368', 'ELED', '463', '2006', 'STUDENT TEACHING IN THE UPPER ELEMENTARY SCHOOL', 'Directed observation and participation in classroom activities in the upper grades in an approved elementary school. Must be admitted to Teacher Education. Take during professional semester 4. F, Sp', '5', '5', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('472', '497283', 'ELED', '464', '2006', 'STUDENT TEACHING IN THE LOWER ELEMENTARY SCHOOL', 'Directed observation and participation in classroom activities in the lower grades in an approved elementary school. Must be admitted to Teacher Education. F, Sp', '9', '9', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('473', '305322', 'ELED', '451', '2006', 'EARLY CHILDHOOD EDUCATION', 'History, trends and principles of educational programs for children between the ages of three and six. Must be admitted to Teacher Education. F, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('474', '457868', 'ELED', '452', '2006', 'NURSERY SCHOOL AND KINDERGARTEN', 'Materials, methods, organization, and administration of nursery schools and kindergartens. Prerequisite: 451. Must be admitted to Teacher Education. Sp, Sum', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('475', '148638', 'ELED', '453', '2006', 'LANGUAGE EXPERIENCES FOR EARLY CHILDHOOD EDUCATION', 'Techniques and strategies for developing languages and teaching language skills to pre-school children. Prerequisite: Must be admitted to Teacher Education.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('476', '784030', 'ENGL', '090', '2006', 'GRAMMAR AND COMPOSITION', 'Intensive instruction in basic grammar and composition. Five semester hours will be added to the degree program of any student required to take this course.', '5', '5', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('477', '446984', 'ENGL', '102', '2006', 'COMPOSITION AND INTRODUCTION IN LITERATURE', 'Composition of literary essays, including documented papers; introduction to literature. Prerequisite: 101.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('478', '83676', 'ENGL', '103', '2006', 'PERSUASIVE WRITING', 'Composition of persuasive essays, including documented papers. Includes study of various forms of argument. Prerequisite: 101.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('479', '731340', 'ENGL', '110H', '2006', 'HONORS ENGLISH', 'A writing intensive course which introduces outstanding examples from world literature while developing the skills necessary to think and to write critically. Prerequisite: Admission to the Honors Program or permission of the Honors Council. Acceptance to the Honors Program and successful completion of English 110H substitute for English 102. Honors students are not required to take English 101. English 110H fulfills three hours of the total number of hours in English required in any curriculum.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('480', '974979', 'ENGL', '201', '2006', 'ENGLISH LITERATURE', 'From the beginning to Romanticism. Prerequisite: 102 or 103. Consult departmental course descriptions. Contents and texts vary.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('481', '60837', 'ENGL', '202', '2006', 'ENGLISH LITERATURE', 'From Romanticism to the present. Prerequisite: 102 or 103. Consult departmental course descriptions. Contents and texts vary.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('482', '743736', 'ENGL', '203', '2006', 'WORLD LITERATURE', 'Masterpieces from the Classical period to the Renaissance, in English translation. Prerequisite: 102 or 103. Consult departmental course descriptions. Contents and texts vary.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('483', '337089', 'ENGL', '204', '2006', 'WORLD LITERATURE', 'Masterpieces from the Renaissance to the present, in English translation. Prerequisite: 102 or 103. Consult departmental course descriptions. Contents and texts vary.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('484', '846660', 'ENGL', '205', '2006', 'AMERICAN LITERATURE', 'From the beginning to the Civil War. Prerequisite: 102 or 103. Consult departmental course descriptions. Contents and texts vary.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('485', '370541', 'ENGL', '206', '2006', 'AMERICAN LITERATURE', 'From the Civil War to the present. Prerequisite: 102 or 103. Consult departmental course descriptions. Contents and texts vary.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('486', '987883', 'ENGL', '209H', '2006', 'HONORS ENGLISH', 'A study of major works of British Literature, designed to strengthen the students’ critical abilities and writing skills. Prerequisite: Admission to the Undergraduate Honors Program or permission of the Honors Council.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('488', '970806', 'ENGL', '216', '2006', 'AFRICAN-AMERICAN LITERATURE', 'A thematic and cultural study of works of Afro-American writers.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('489', '286924', 'ENGL', '217', '2006', 'THE AFRICAN-AMERICAN EXPERIENCE IN LITERATURE', 'The black experience in America as portrayed by major black and white writers through their characters and thematic materials.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('490', '775167', 'ENGL', '219H', '2006', 'HONORS ENGLISH', 'A study of major works of American Literature designed to strengthen the students’ critical abilities and writing skills. Prerequisite: Admission to the Honors Program or permission of the Honors Council.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('491', '273469', 'ENGL', '220', '2006', 'TECHNICAL WRITING', 'Introduction to scientific and technical writing, including materials and forms (written, oral, and presentational) within respective colleges. A. Pharmacy and Health Sciences Technical Writing. B. Pure and Applied Technical Writing. C. Liberal Arts Technical Writing. D. General Technical Writing. Prerequisite: 102 or 103.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('492', '232867', 'ENGL', '225', '2006', 'VOCABULARY AND ETYMOLOGY', 'Study of general and technical vocabularies with emphasis on prefixes, roots, suffixes, and etymologies in the English language. Prerequisite: 102 or 103.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('493', '358176', 'ENGL', '250', '2006', 'SPECIAL TOPICS', 'Introduction to varying literary types and themes. Content varies. A. Women in Literature. B. Business in American Literature: Ethical and Social Inquiries. C. The Law and Literature. D. American Humor. E. Writings on Nature and Science. F. Horror I. Poets of Protest--1950-1969.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('494', '412918', 'ENGL', '300', '2006', 'DIRECTED STUDY--E-TV', 'Studies in language and literature. Content varies. A. The Story of English; B. Classical Drama; C. Shakespeare; D. Humanities Through the Arts; E. Voices and Visions. F. Literary Visions. No more than 12 semester hours may be applied toward degree requirements.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('495', '834441', 'ENGL', '303', '2006', 'LITERATURE AND FILM', 'The study of film adaptations with a focus on American literature and films as reflections of our national identity. Prerequisite: 102 or 103.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('497', '536344', 'ENGL', '309', '2006', 'LOUISIANA LIFE AND LITERATURE', 'Louisiana’s cultural heritage and folklore as preserved in her literature. Short stories, poetry, drama, and novels will be read including representative works by George Washington Cable, Kate Chopin, Ernest Gaines, Walker Percy, Robert Penn Warren, and Tennessee Williams. Prerequisite: 102.', '3', '3', '0', '0', '', '0');
