INSERT INTO `draft_courses` VALUES ('13942', '880760', 'ENGL', '393', '2006', 'Mythology Of The Greeks And Romans', 'Tales of the gods, heroes, and great families that have influenced literature, art, and music. (Same as Latin 303.)', '3', '3', '3', '0', '', '0');
