INSERT INTO `draft_courses` VALUES ('511', '939828', 'ENGL', '444', '2006', 'WRITING IN THE SCIENCES', 'Writing professional documents (abstracts, proposals, research) and developing professional presentations for the scientific community with emphasis on rhetorical strategies and the conventions of scientific discourse. Prerequisite: 220A, 220B, 220C, 220D, or consent of department head.', '3', '3', '0', '0', '', '0');
