INSERT INTO `draft_courses` VALUES ('540', '773593', 'ENGL', '490', '2006', 'Advanced Poetry Writing', 'The writing of poetry, with emphasis on voice, style, and vision. Prerequisite: ENGL 315 or approval of Department Head.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('541', '249111', 'ESLG', '071', '2006', 'BEGINNING READING AND WRITING', 'A beginning English reading and writing course for non-native speakers. Students will receive a grade of A, B, C, or NC.', '5', '5', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('542', '606216', 'ESLG', '072', '2006', 'BEGINNING STRUCTURE', 'A beginning English structure course for non-native speakers. Students will receive a grade of A, B, C, or NC.', '5', '5', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('543', '329861', 'ESLG', '073', '2006', 'PRONUNCIATION', 'The first of two English pronunciation courses for non-native speakers with emphasis on phonetics, rhythm, stress and intonation and the features of English phrasing. Students will receive a grade of A, B, C, or NC.', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('544', '522490', 'ESLG', '074', '2006', 'BEGINNING ORAL PROFICIENCY', 'A beginning English oral proficiency course for non-native speakers. Development of basic conversation skills utilizing the language laboratory, classroom and extended activities. Students will receive a grade of A, B, C, or NC.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('545', '883134', 'ESLG', '081', '2006', 'LOW INTERMEDIATE READING AND WRITING', 'The first of two intermediate English reading and writing courses for nonnative speakers. Prerequisite: A minimum of &quot;C&quot; in ESL-071 or placement based on English competency tests given by the Department of Foreign Languages. Students will receive a grade of A, B, C, or NC.', '5', '5', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('546', '732788', 'ESLG', '082', '2006', 'LOW INTERMEDIATE STRUCTURE', 'The first of two intermediate English structure courses for non-native speakers. Prerequisite: A minimum of &quot;C&quot; in ESL-072 or placement based on English competency tests given by the Department of Foreign Languages. Students will receive a grade of A, B, C, or NC.', '5', '5', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('547', '166411', 'ESLG', '083', '2006', 'PRONUNCIATION', 'The second of two English pronunciation courses for non-native speakers. A continuation of ESL-073. Focuses on increasing accuracy in pronunciation. Addresses individual needs. Prerequisite: A minimum of &quot;C&quot; in ESL-073 or placement based on English competency tests given by the Department of Foreign Languages. Students will receive a grade of A, B, C, or NC.', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('548', '147936', 'ESLG', '084', '2006', 'LOW INTERMEDIATE ORAL PROFICIENCY', 'The first of two intermediate oral proficiency courses for non-native speakers. Emphasis on fluency and communication skills in real life academic and non-academic contexts. Prerequisite: A minimum of &quot;C&quot; in ESL-074 or placement based on English competency tests given by the Department of Foreign Languages. Students will receive a grade of A, B, C, or NC.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('549', '336079', 'ESLG', '091', '2006', 'INTERMEDIATE READING AND WRITING', 'The second of two intermediate English reading and writing courses for nonnative speakers. Prerequisite: A minimum of &quot;C&quot; in ESL-081 or placement based on English competency tests given by the Department of Foreign Languages. Students will receive a grade of A, B, C, or NC.', '5', '5', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('550', '781137', 'ESLG', '093', '2006', 'AURAL COMPREHENSION', 'A listening comprehension course for non-native speakers. Listening and note taking skills within an academic context. Subject matter comprehension and paralinguistic cues in academic interaction. Prerequisite: A minimum of &quot;C&quot; in ESL-083 or placement based on English competency tests given by the Department of Foreign languages. Students will receive a grade of A, B, C, or NC.', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('551', '699661', 'ESLG', '094', '2006', 'INTERMEDIATE ORAL PROFICIENCY', 'The second of two intermediate oral proficiency courses for non-native speakers. Continued emphasis on refinement of conversation skills. Special emphasis on American idiom in academic and non-academic contexts. Prerequisite: A minimum of &quot;C&quot; in ESL-084 or placement based on English competency tests given by the Department of Foreign Languages. Students will receive a grade of A, B, C, or NC.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('552', '714536', 'ESLG', '103', '2006', 'ADVANCED STRUCTURE, GRAMMAR AND COMPOSITION', 'An advanced course in the study of the English language for non-native speakers emphasizing the acquisition of skills necessary in academic written English. Students will receive a grade of A, B, C, or NC. (An F will be given only for cheating or for excessive absences.) Undergraduate students who earn a &quot;C&quot; or better will receive 3 hours of credit for English 101.', '6', '6', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('553', '669333', 'ESLG', '104', '2006', 'ADVANCED READING AND VOCABULARY', 'An advanced course in the study of the English language for non-native speakers emphasizing the acquisition of vocabulary and reading comprehension skills appropriate to academic English. Students will receive a grade of A,B,C, or NC. (An F will be given only for cheating or for excessive absences.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('554', '484254', 'ESLG', '105', '2006', 'ADVANCED READING, WRITING, AND STRUCTURE', 'An advanced course in the study of the English language for nonnative speakers. This course deals with English structure, reading and writing. Successful completion of this course (&quot;C&quot; or better) satisfies the requirements of English 101. Prerequisite: A minimum of &quot;C&quot; in ESL-091 and 092 or placement based on English competency tests given by the Department of Foreign Languages.', '9', '9', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('555', '451170', 'ESLG', '106', '2006', 'ADVANCED ORAL LANGUAGE DEVELOPMENT', 'An advanced speaking course for non-native speakers. Focuses on academic and pre-professional speaking tasks. Prerequisite: A minimum of &quot;C&quot; in ESL-093 and ESL-094 or placement based on English competency tests given by the Department of Foreign Languages. Students will receive a grade of A, B, C, or NC. (An F will be given only for cheating or for excessive absence.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('556', '970446', 'ESLG', '107', '2006', 'THE AMERICAN EXPERIENCE', 'An advanced cultural orientation course for International Students with continued emphasis on understanding, speaking, reading, and writing at an advanced level. Prerequisite: A minimum of &quot;C&quot; in ESL-105 or placement based on competency tests given by the Department of Foreign Languages.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('557', '103288', 'ENTR', '250', '2006', 'ENTREPRENEURSHIP', 'This course will explore the unique aspects of entrepreneurship in modern society. Students will learn how to identify, assess, and develop business ideas and how to locate and evaluate business opportunities. Prerequisites: None. F, S', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('558', '388217', 'ENTR', '351', '2006', 'ENTREPRENEURIAL INFORMATION AND ANALYSIS', 'Integrated computer and accounting systems for new, small, and growing enterprises. The application of hardware and software and software technology to management, accounting, and finance in small businesses. Prerequisite: CINS 205.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14161', '469911', 'ENTR', '352', '2006', 'Entrepreneurial Finance', 'Financing of new and growing business enterprises; structuring, valuing, and pricing new venture deals, and sources and uses of funds; financial analysis and forecasting, capital budgeting, and working capital management in new, growing enterprises; later stage financing, going public. Prerequisite: Junior standing. (Same as Finance 330)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14155', '688211', 'ENTR', '353', '2006', 'Entrepreneurial Marketing', 'Marketing during the start up and growth stage of new ventures in a dynamic economy at the local, national, and international level. Emphasis on the application of marketing fundamentals in new growing ventures. Prerequisite: Junior standing. (Same as Marketing 330)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14146', '511893', 'ENTR', '432', '2006', 'New Venture Creation', 'Developing, planning, and running new enterprises. Analysis and pursuit of new business opportunities, and development of business plans. Prerequisites: 250 and senior standing; or consent of instructor. (Formerly MGMT 332.) F', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('13912', '248306', 'ENTR', '433', '2006', 'Enterprise Management', 'Students will study the application of management concepts to new and growing ventures. Analysis of organizational design, planning, implementation, and control of enterprises; social and ethical considerations in managing enterprises; and production, decision making, and behavioral factors in new and growing venture management. Prerequisite: Junior/senior standing or consent of instructor. F, Sp', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('563', '208125', 'EQSC', '330', '2006', 'INTRODUCTORY EQUINE SCIENCE', 'An introduction to the horse, including a study of the evolution and history, registries, evaluation and conformation, care and other basic concepts.', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('564', '695565', 'EQSC', '335', '2006', 'BASIC EQUITATION', 'An introduction to proper horsemanship. Four hours laboratory. A $25.00 lab fee is required.', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('565', '277604', 'EQSC', '336', '2006', 'EQUITATION', 'A study of proper horsemanship including gaits, handling, mounting and dismounting. Four hours laboratory. A $25.00 lab fee is required. Prerequisite: EQSC 335.', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('566', '129291', 'EQSC', '340', '2006', 'BROODMARE CARE', 'A study of the care of mares with foals at side, the weaning of foals, and care of weaned foals.', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('567', '397104', 'EQSC', '341', '2006', 'FOALING AND FOAL DEVELOPMENT', 'A study of the developing fetus and the birth of foals. Practical experience foaling-out mares, imprint training foals, and working with mares and foals.', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('568', '555767', 'EQSC', '342', '2006', 'SCHOOLING OF THE UNTRAINED HORSE', 'Practical experience training and breaking young horses or re-schooling older or problem horses. Six hours laboratory. Prerequisite: EQSC 335 or 336.', '2', '2', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('569', '848759', 'FCSC', '101', '2006', 'PROFESSIONAL ETIQUETTE', 'Understanding of basic social concepts in the home, society, business and the professional world.', '1', '1', '0', '0', '', '0');
