INSERT INTO `draft_courses` VALUES ('14128', '481715', 'MATH', '420', '2006', 'Introduction To Biostatistics I', 'An introduction to the discipline of statistics and its application to problems of biology, including human biology, medicine, and public health. Topics include Bayesian inferences, estimation techniques, hypothesis testing, goodness of fit, and analysis of variance. Prerequisite: MATH 114 or MATH 131 or permission of the department head and junior standing or above. (Same as Computer Science 420).', '3', '3', '3', '0', '', '0');
