INSERT INTO `draft_courses` VALUES ('1842', '729573', 'ANTS', '423', '2006', 'ARCHEOLOGICAL FIELD TECHNIQUES', 'A field course introducing the basic techniques of archeological mapping, excavation, cataloging, specimen preparation and preservation. A study of soils, physical stratigraphy, synecology, and techniques of artifact laboratory analysis. May be repeated once for a maximum of 12 hours credit. (Same as GEOG 423.)', '6', '6', '12', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14032', '813733', 'ANTS', '435', '2006', 'Anthropology Of Sex Roles', 'An analysis of different values that define appropriate masculine and feminine behavior (sex roles) in a variety of cultures. Explores the impact of social institutions on teaching and enforcing sex roles in traditional and modern societies. (Same as SOCL 435.)', '3', '3', '3', '0', '', '0');
