INSERT INTO `draft_courses` VALUES ('1866', '876408', 'ART', '443', '2006', 'SEMINAR IN ART HISTORY', 'Maximum, 12 hrs. The study of various eras and developments in world art. A. Expressionism. B. Baroque. C. Far Eastern. D. Theory. E. Art Since 1965. F. Women Artists. G. Surrealism in Art. H. History of Photography. Prerequisites: Art 201, 202, or approval of department head.', '3', '3', '12', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1867', '445623', 'ART', '451', '2006', 'Advanced Printmaking', 'Maximum, 12 hrs. Research problems in printmaking; choice of media. One hour lecture and five hours studio. Prerequisites: 101, 103; 257, 258, 259 or 260. Additional course fee $50.', '3', '3', '12', '0', '', '0');
