INSERT INTO `draft_courses` VALUES ('1869', '889639', 'ART', '471', '2006', 'ADVANCED PAINTING', 'Maximum, 12 hrs. Independent problems in choice of media; emphasis upon personal solutions. One hour lecture and five hours studio. Prerequisites: 271 or 272.', '3', '3', '12', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1870', '285153', 'ART', '481', '2006', 'Advanced Sculpture', 'Maximum, 12 cr. Advanced problems in sculptural processes and techniques. Emphasis upon individual creative expression. One hour lecture and five hours studio. Prerequisite: 381. Additional course fee $50.', '3', '3', '12', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1871', '441238', 'ART', '495', '2006', 'Research Problems', 'Maximum, 12 hrs. Individual and group study with approval of department head. Research, discussion and criticism. Additional course fee $50.', '3', '3', '12', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1872', '567092', 'ARTS', '110H', '2006', 'HONORS ARTS', 'Questions of aesthetics, issues of performance and/or exhibition, aspects of creativity in one or more of the arts.  Course content varies and may link two or more disciplines.  Prerequisite: Admission to the Honores Program or permission of the Honors Council.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1873', '14727', 'ASCI', '407', '2006', 'MEAT PROCESSING AND PRESERVATION', 'The factors involved in dressing percentage and output values of carcasses, cutting, curing, and handling of meat products. One hour lecture and four hours laboratory. Prerequisite: ASCI 101.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1874', '254971', 'ASCI', '430', '2006', 'ARTIFICIAL INSEMINATION & EMBRYO TRANSFER', 'Development and use of artificial insemination and embryo transfer techniques in modern livestock breeding programs. Two hours lecture and two hours laboratory. Prerequisites: ASCI 101, 341, CHEM 101 or 107, MATH 110 or 111.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1875', '89049', 'ATMS', '103', '2006', 'BASIC METEOROLOGY LABORATORY I', 'A laboratory course designed to expose students to basic uses of meteorological data from the surface, upper air, radars, and satellites. Topics include surface and upper-air map analysis, fronts and cyclones, and severe weather.', '1', '1', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1876', '209074', 'ATMS', '205', '2006', 'GENERAL METEOROLOGY', 'Structure of the earth’s atmosphere, distribution of temperature and precipitation, composition of the atmosphere, extratropical synoptic scale disturbances, introduction to thermodynamics, clouds and storms. Prerequisites: 102, 104, and credit or registration in MATH 113.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1877', '228672', 'ATMS', '305', '2006', 'DYNAMIC METEOROLOGY', 'A study of the dynamic principles governing the structure, movement, and development of weather systems.  Introduction to numerical weather prediction.  Prerequisite: 304.', '3', '3', '0', '0', '', '0');
