INSERT INTO `draft_courses` VALUES ('1881', '110527', 'ATMS', '310', '2006', 'FUNDAMENTALS OF CLIMATIC ANALYSIS', 'Fundamentals of the tabulation and analysis of climatic data, including computer data processing and statistical methods. Prerequisites: 102, and MATH 113.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1882', '424194', 'ATMS', '320', '2006', 'METEROLOGY SEMINAR', '1 credit, 4 credit maximum. Discussion and application of weather forecasting theories and techniques to current weather situations. Leading and participating in forecast map discussions. Forecast verification. Prerequisites: 205, MATH 131.', '1', '1', '4', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1883', '904310', 'ATMS', '401', '2006', 'SYNOPTIC METEOROLOGY', 'Analysis of synoptic weather charts and thermodynamic diagrams in the Weather Research Center.  Enphasis is on understanding the three-dimensional structure of the atmosphere, interpretation of surface, upper air, radar, meteorological satellite weather data, and National Weather Service products received in the Weather Research Center.  Prerequisites: 306, 308.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1884', '308942', 'ATMS', '402', '2006', 'SYNOPTIC METEOROLOGY', 'Analysis of synoptic weather charts and thermodynamic diagrams in the Weather Research Center.  Enphasis is on understanding the three-dimensional structure of the atmosphere, interpretation of surface, upper air, radar, meteorological satellite weather data, and National Weather Service products received in the Weather Research Center.  Prerequisites: 306, 308, 401.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1885', '435110', 'ATMS', '415', '2006', 'MICROMETEOROLOGY', 'Introduction to the physical processes in the atmospheric surface boundary layer. Influence of low level atmospheric turbulence on the diffusion of heat, water vapor (evaporation), and atmospheric pollutants. Prerequisites: 306 and PHYS 208.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1886', '645852', 'ATMS', '460', '2006', 'METEOROLOGY FOR TEACHERS', 'A non-mathematical introduction to meteorology designed for elementary and secondary school science teachers.  The courses are a blend of lecture, demonstrations, and hands-on work and are designed to be taken in sequence (460-461).  The courses cannot be used to satisfy degree requirements in Atmospheric Science.  ATMS 460 prerequisite for ATMS 461.', '3', '3', '0', '0', '', '0');
