INSERT INTO `draft_courses` VALUES ('1898', '644546', 'BIOL', '114', '2006', 'Fundamentals of Anatomy and Physiology I', 'Introduction to anatomy and physiology, including cells, tissues, organs, and the integumentary, skeletal, muscular, and nervous systems. Non-majors only. Cannot be taken for credit if credit has been awarded for 311 or 312. (Formerly Zoology 111.)', '3', '3', '3', '0', '', '0');
