INSERT INTO `draft_courses` VALUES ('1900', '951639', 'BIOL', '240', '2006', 'HUMAN ANATOMY', 'Gross structure and physiology of the body with emphasis on the skeletal, muscular, nervous, and circulatory systems. Prerequisites: 120 and 121, or 226; cumulative GPA of 2.5 or higher with a minimum of &quot;C&quot; in 120 or 226; credit or registration in 241. (Formerly Zoology 240.) F', '3', '3', '0', '0', '', '0');
