INSERT INTO `draft_courses` VALUES ('1982', '761229', 'CSCI', '411', '2006', 'OPERATING SYSTEMS', 'Theory and implementation of operating systems including process management, device management, memory management, and file system management. Security, networking, and distributed operating systems. Prerequisites: CSCI 253, 305, and 310.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1983', '195031', 'CSCI', '461', '2006', 'COOPERATIVE EDUCATION EXPERIENCE', 'Planned and supervised work experience in student’s major field. Open only to juniors and seniors in the Department of Computer Science who are approved for the Cooperative Education Program. Grades of CR (credit) or NC (no credit) will be awarded. May be repeated for a total of 3 credits. May not be applied to fulfill major requirements.', '1', '1', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1984', '656860', 'CSCI', '498', '2006', 'Research', '1-6 credits; maximum, 6 cr. Open to advanced undergraduates who are capable of developing a problem independently. Subject Areas: A. Applications; B. Software; C. Mathematics of Computation; D. Hardware. Prerequisites: Approval of department head.', '1', '6', '6', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1985', '81454', 'CSCI', '435', '2006', 'LINEAR PROGRAMMING', 'Development of the simplex algorithm, revised simplex, duality, and application. Prerequisite: MATH 202. 436. INTRODUCTION TO MATHEMATICAL CONCEPTS IN OPERATIONS RESEARCH. 3 cr. A mathematical approach to concepts in advanced linear and nonlinear programming, dynamic programming, game theories, queueing theory and other standard topics in operations research. Prerequisites: CSCI 435 and MATH 303. (Same as MATH 436.)', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14119', '286030', 'CSCI', '436', '2006', 'Intro Mathematical Concepts In Operations Research', 'A mathematical approach to concepts in advanced linear and nonlinear programming, dynamic programming, game theories, queueing theory and other standard topics in operations research. Prerequisites: CSCI 435 and MATH 303. (Same as MATH 436.)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1987', '911003', 'CSCI', '483', '2006', 'ARTIFICIAL INTELLIGENCE', 'Study of the computer in context with human thought processes. Heuristic programming; search strategies; knowledge representation; perception; learning; natural language understanding; user interfaces; intelligent systems. Prerequisites: 305 and 310.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('1988', '140087', 'CURR', '285', '2006', 'APPLICATION OF INSTRUCTIONAL MEDIA & TECHNOLOGY', 'This course is an introduction to the preparation, selection, application, evaluation, and integration of media and technology in instructional settings, including audiovisual and microcomputer applicatons.  Laboratory practice includes the production of projected, nonprojected, and computer-based instructional materials.', '3', '3', '0', '0', '', '0');
