INSERT INTO `draft_courses` VALUES ('2076', '831567', 'GEOG', '101', '2006', 'REGIONAL GEOGRAPHY', 'A regional approach to the study of human geography, with emphasis on spatial distributions, interactions, and processes on the earth&#39;s surface.  The geographical study of the major realms are divided as follows: GEOG 101 covers Europe (including Russia), North America, and North Africa/Middle East and GEOG 102 covers Latin America, Sub-Saharan Africa, and Asia.', '3', '3', '0', '0', '', '0');
