INSERT INTO `draft_courses` VALUES ('2223', '87341', 'SCIE', '102', '2006', 'INTEGRATED PHYSICAL SCIENCE II', 'Fundamental concepts of chemistry with an emphasis on the interdisciplinary nature of the concepts introduced.  Integrated lecture/lab format.  For Elementary Education Majors only. F, Sp', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('2224', '474370', 'SCIE', '104', '2006', 'INTEGRATED GEOSCIENCES', 'The goal of this course is to present an integrated approach to essential concepts in introductory geology (physical and historical geology), astronomy (from an earth science perspective), and weather and to emphasize the personal application of science, the process skills, problem-solving, and discovery/inquiry learning. Prerequisites: SCIE 101 and 102. For Elementary Education Majors only.', '3', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('2225', '489764', 'SOCL', '416', '2006', 'DIRECTED STUDY', 'Guided reading and/or research in a special area of sociology. Prerequisite: Twelve hours of sociology, a grade point of 3.00 in sociology courses and prior approval of supervising instructor.', '1', '3', '0', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('14035', '458685', 'SOCL', '435', '2006', 'Anthropology Of Sex Roles', 'An analysis of different values that define appropriate masculine and feminine behavior (sex roles) in a variety of cultures. Explores the impact of social institutions on teaching and enforcing sex roles in traditional and modern societies. (Same as ANTG 435)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('2227', '969947', 'SOCW', '318', '2006', 'COMMUNITY INTERVENTION', 'A study and evaluation of theories and strategies of community intervention; a problem- solving methodology to teach techniques of working with planning action groups around problem identification, and intervention activities; methods of community organization.', '3', '3', '0', '0', '', '0');
