INSERT INTO `draft_courses` VALUES ('2230', '15839', 'SOCW', '408', '2006', 'Child Abuse Recognition and Treatment', 'Defines the abused and neglected child; examines the dynamics associated with dysfunctional families and multi-level treatment approaches for the abused child and abusive parents. (Formerly 406 and 407; may receive credit for only one of the following: 406, 408, or PSYC 441.)', '3', '3', '3', '0', '', '0');
INSERT INTO `draft_courses` VALUES ('2231', '330631', 'SOCW', '417', '2006', 'SOCIAL WORK ADMINISTRATION', 'Study of organization and administrative practices utilized in public agencies for expediting services offered to the public. Emphasis on planning, programming, budgeting, public relations, interpretation of social policy, decision making, and legislation. Prerequisite: Approval of program director. (Formerly Organization and Administration in Social Welfare Agencies.)', '3', '3', '0', '0', '', '0');
