INSERT INTO `draft_courses` VALUES ('13283', '295461', 'SPCH', '310', '2006', 'Communication And Conflict', 'A study of the theory and practice of communication strategies and styles in conflict situations. The course will look at conflict in interpersonal and/or profession settings. Prerequisites: SPCH 101, 118, 201 or consent of the instructor.', '3', '3', '3', '0', '', '0');
